        for pattern in analysis_results['student_patterns'][:10]:
            logger.info(f"  ğŸ‘¤ {pattern}")

        # ê²°ê³¼ ì €ì¥
        with open("attok_auto_analysis.json", "w", encoding="utf-8") as f:
            json.dump(analysis_results, f, ensure_ascii=False, indent=2)
        logger.info("\nê²°ê³¼ ì €ì¥: attok_auto_analysis.json")

        # iframe ë‚´ë¶€ í™•ì¸
        iframes = driver.find_elements(By.TAG_NAME, "iframe")
        if iframes:
            logger.info(f"\n=== iframe ë¶„ì„ ({len(iframes)}ê°œ) ===")
            for i, iframe in enumerate(iframes):
                try:
                    driver.switch_to.frame(iframe)
                    iframe_url = driver.execute_script("return window.location.href")
                    logger.info(f"iframe {i}: {iframe_url}")

                    # iframe ë‚´ë¶€ì—ì„œ í•™ìƒ ìš”ì†Œ ì°¾ê¸°
                    iframe_students = driver.execute_script("""
                        var students = [];
                        document.querySelectorAll('td, div').forEach(function(elem) {
                            if (/[ê°€-í£]{2,4}/.test(elem.innerText)) {
                                students.push(elem.innerText.substring(0, 20));
                            }
                        });
                        return students;
                    """)

                    if iframe_students:
