"""
Attok ì›¹ì‚¬ì´íŠ¸ ëª¨ë‹ˆí„°ë§ ëª¨ë“ˆ v2
í•™ìƒ ì¹´ë“œ êµ¬ì¡°ë¥¼ ì •í™•íˆ ì¸ì‹í•˜ëŠ” ê°œì„ ëœ ë²„ì „
"""
import time
import logging
from typing import List, Callable, Optional
from selenium import webdriver
from selenium.webdriver.common.by import By
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
from selenium.webdriver.chrome.service import Service
from webdriver_manager.chrome import ChromeDriverManager
from models import Student, StudentStatus
import config
import re

logging.basicConfig(level=logging.INFO)
logger = logging.getLogger(__name__)

class AttokMonitorV2:
    """Attok ì›¹ì‚¬ì´íŠ¸ ëª¨ë‹ˆí„°ë§ í´ë˜ìŠ¤ - ê°œì„ ëœ ë²„ì „"""

    def __init__(self, headless: bool = True):
        self.driver: Optional[webdriver.Chrome] = None
        self.headless = headless
        self.is_logged_in = False
        self.students: dict[str, Student] = {}
        self.on_student_checkin: Optional[Callable] = None

    def setup_driver(self, headless: bool = None):
        """Chrome ë“œë¼ì´ë²„ ì„¤ì •"""
        if headless is None:
            headless = self.headless

        options = webdriver.ChromeOptions()
        chrome_options = config.HEADLESS_OPTIONS if headless else config.CHROME_OPTIONS

        for option in chrome_options:
            options.add_argument(option)

        # ìë™í™” ê°ì§€ ë°©ì§€
        options.add_experimental_option("excludeSwitches", ["enable-automation"])
        options.add_experimental_option('useAutomationExtension', False)

        try:
            service = Service(ChromeDriverManager().install())
            self.driver = webdriver.Chrome(service=service, options=options)
        except Exception as e:
            logger.error(f"Chrome ë“œë¼ì´ë²„ ì´ˆê¸°í™” ì‹¤íŒ¨: {e}")
            self.driver = webdriver.Chrome(options=options)
        logger.info(f"Chrome driver ì´ˆê¸°í™” ì™„ë£Œ (headless={headless})")

    def login(self) -> bool:
        """ë¡œê·¸ì¸ ì²˜ë¦¬"""
        try:
            self.setup_driver(headless=False)
            self.driver.get(config.ATTOK_URL)

            logger.info("ë¸Œë¼ìš°ì €ì—ì„œ ë¡œê·¸ì¸í•´ì£¼ì„¸ìš”...")

            wait = WebDriverWait(self.driver, 300)
            logged_in = wait.until(
                lambda driver: "attendance" in driver.current_url or 
                driver.find_elements(By.XPATH, "//a[contains(@href, 'logout')]")
            )

            if logged_in:
                self.is_logged_in = True
                logger.info("ë¡œê·¸ì¸ ì„±ê³µ!")
                return True

        except Exception as e:
            logger.error(f"ë¡œê·¸ì¸ ì‹¤íŒ¨: {e}")
            return False

    def check_students(self) -> List[Student]:
        """í•™ìƒ ì¶œê²° ìƒíƒœ í™•ì¸ - ê°œì„ ëœ ë²„ì „"""
        if not self.driver or not self.is_logged_in:
            return []

        try:
            checked_in_students = []

            # JavaScriptë¡œ í•™ìƒ ì¹´ë“œ ì°¾ê¸°
            student_cards_script = """
            var studentCards = [];

            // ëª¨ë“  ìš”ì†Œ ê²€ì‚¬
            document.querySelectorAll('*').forEach(function(elem) {
                var text = elem.innerText || '';

                // í•™ìƒ ì¹´ë“œ ì¡°ê±´:
                // 1. í•œê¸€ ì´ë¦„ (2-4ê¸€ì)
                // 2. "ë“±ì›" ë˜ëŠ” "í•˜ì›" í…ìŠ¤íŠ¸ í¬í•¨
                // 3. ì²´í¬ë°•ìŠ¤ê°€ ìˆì„ ê°€ëŠ¥ì„±

                var hasStudentName = /[ê°€-í£]{2,4}/.test(text);
                var hasAttendanceKeywords = text.includes('ë“±ì›') || text.includes('í•˜ì›') || text.includes('ì¶œê²°');

                if (hasStudentName && hasAttendanceKeywords) {
                    // ë°°ê²½ìƒ‰ í™•ì¸
                    var bgColor = window.getComputedStyle(elem).backgroundColor;
                    var parentBgColor = elem.parentElement ? 
                        window.getComputedStyle(elem.parentElement).backgroundColor : '';

                    // ìŠ¤ì¹´ì´ë¸”ë£¨ í™•ì¸ (RGB: 135, 206, 235 ë˜ëŠ” ë¹„ìŠ·í•œ ê°’)
                    var isSkyBlue = false;
                    var rgbMatch = bgColor.match(/rgb\\((\\d+),\\s*(\\d+),\\s*(\\d+)/);
                    if (rgbMatch) {
                        var r = parseInt(rgbMatch[1]);
                        var g = parseInt(rgbMatch[2]);
                        var b = parseInt(rgbMatch[3]);

                        // ìŠ¤ì¹´ì´ë¸”ë£¨ ë²”ìœ„ (ë” ë„“ê²Œ ì„¤ì •)
                        // R: 100-150, G: 180-220, B: 200-250
                        if (r >= 100 && r <= 150 && g >= 180 && g <= 220 && b >= 200 && b <= 250) {
                            isSkyBlue = true;
                        }
                    }

                    // ë¶€ëª¨ ìš”ì†Œë„ í™•ì¸
                    if (!isSkyBlue && parentBgColor) {
                        var parentRgbMatch = parentBgColor.match(/rgb\\((\\d+),\\s*(\\d+),\\s*(\\d+)/);
                        if (parentRgbMatch) {
                            var pr = parseInt(parentRgbMatch[1]);
                            var pg = parseInt(parentRgbMatch[2]);
                            var pb = parseInt(parentRgbMatch[3]);

                            if (pr >= 100 && pr <= 150 && pg >= 180 && pg <= 220 && pb >= 200 && pb <= 250) {
                                isSkyBlue = true;
                            }
                        }
                    }

                    // í•™ìƒ ì´ë¦„ ì¶”ì¶œ
                    var nameMatch = text.match(/[ê°€-í£]{2,4}/);
                    if (nameMatch) {
                        var studentName = nameMatch[0];

                        // ì‹œìŠ¤í…œ ë©”ë‰´ ì œì™¸
                        var excludeWords = ['ê²€ìƒ‰', 'ë“±ë¡', 'ì‹ ê·œ', 'ë¡œê·¸ì¸', 'ê³µì§€', 'ê·¸ë£¹', 
                                          'ë‚©ë¶€', 'ì¼ê´„', 'ì •ë³´', 'ìˆ˜ì •', 'ë¯¸ë‚©', 'ì²˜ë¦¬', 'ìš”ì²­'];
                        var isSystemMenu = false;
                        for (var word of excludeWords) {
                            if (studentName.includes(word)) {
                                isSystemMenu = true;
                                break;
                            }
                        }

                        if (!isSystemMenu) {
                            // ë“±ì› ì‹œê°„ ì¶”ì¶œ
                            var timeMatch = text.match(/(\\d{1,2}:\\d{2})/);
                            var checkinTime = timeMatch ? timeMatch[0] : null;

                            studentCards.push({
                                name: studentName,
                                isCheckedIn: isSkyBlue,
                                checkinTime: checkinTime,
                                bgColor: bgColor,
