      if (isDevelopmentMode()) {
        // ê°œë°œ ëª¨ë“œ: Mock ë°ì´í„° ì‚¬ìš©
        console.log('ê°œë°œ ëª¨ë“œ: Mock ë°ì´í„° ì‚¬ìš©')
        setProjects(mockProjects)
      } else {
        // í”„ë¡œë•ì…˜ ëª¨ë“œ: Electron API ì‚¬ìš©
        const result = await window.electronAPI.getProjects()
        if (result.success) {
          setProjects(result.data || [])
        } else {
          console.error('í”„ë¡œì íŠ¸ ë¡œë“œ ì‹¤íŒ¨:', result.error)
        }
      }
    } catch (error) {
      console.error('í”„ë¡œì íŠ¸ ë¡œë“œ ì˜¤ë¥˜:', error)
    } finally {
      setLoading(false)
    }
  }

  const handleProjectSelect = async (projectId: string) => {
    setLoading(true)
    try {
      if (isDevelopmentMode()) {
        // ê°œë°œ ëª¨ë“œ: Mock ë°ì´í„° ì‚¬ìš©
        const project = mockProjects.find(p => p.id === projectId) || mockProject
        setSelectedProject(project)
      } else {
        // í”„ë¡œë•ì…˜ ëª¨ë“œ: Electron API ì‚¬ìš©
        const result = await window.electronAPI.getProjectDetails(projectId)
        if (result.success) {
          setSelectedProject(result.data)
        } else {
          console.error('í”„ë¡œì íŠ¸ ìƒì„¸ ë¡œë“œ ì‹¤íŒ¨:', result.error)
        }
      }
      setCurrentView('details')
    } catch (error) {
      console.error('í”„ë¡œì íŠ¸ ì„ íƒ ì˜¤ë¥˜:', error)
    } finally {
      setLoading(false)
    }
  }

  const handleCreateProject = async (projectData: {
    name: string
    problem?: string
    description?: string
  }) => {
    setLoading(true)
    try {
      if (isDevelopmentMode()) {
        // ê°œë°œ ëª¨ë“œ: Mock ìƒì„±
        console.log('Mock í”„ë¡œì íŠ¸ ìƒì„±:', projectData)
        const newProject = {
          ...mockProject,
          id: `mock-${Date.now()}`,
          name: projectData.name,
          problem: projectData.problem,
          description: projectData.description,
          created_at: new Date().toISOString()
        }
        setProjects([newProject, ...projects])
      } else {
        // í”„ë¡œë•ì…˜ ëª¨ë“œ: Electron API ì‚¬ìš©
        const result = await window.electronAPI.createProject(projectData)
        if (result.success) {
          await loadProjects() // ëª©ë¡ ìƒˆë¡œê³ ì¹¨
        } else {
          console.error('í”„ë¡œì íŠ¸ ìƒì„± ì‹¤íŒ¨:', result.error)
        }
      }
    } catch (error) {
      console.error('í”„ë¡œì íŠ¸ ìƒì„± ì˜¤ë¥˜:', error)
    } finally {
      setLoading(false)
      setShowCreateModal(false)
    }
  }

  const handleBackToProjects = () => {
    setCurrentView('projects')
    setSelectedProject(null)
  }

  const handleOpenScriptPanel = () => {
    setCurrentView('script')
  }

  const handleBackFromScript = () => {
    setCurrentView('details')
  }

  const handleDeleteProject = async (projectId: string) => {
    setLoading(true)
    try {
      if (isDevelopmentMode()) {
        // ê°œë°œ ëª¨ë“œ: ë¡œì»¬ ìƒíƒœì—ì„œ ì‚­ì œ
        console.log('Mock í”„ë¡œì íŠ¸ ì‚­ì œ:', projectId)
        setProjects(prev => prev.filter(p => p.id !== projectId))

        // í˜„ì¬ ì„ íƒëœ í”„ë¡œì íŠ¸ê°€ ì‚­ì œë˜ë©´ í™ˆìœ¼ë¡œ
        if (selectedProject?.id === projectId) {
          setSelectedProject(null)
          setCurrentView('projects')
        }
      } else {
        // í”„ë¡œë•ì…˜ ëª¨ë“œ: Electron API ì‚¬ìš©
        const result = await window.electronAPI.deleteProject(projectId)
        if (result.success) {
          await loadProjects() // ëª©ë¡ ìƒˆë¡œê³ ì¹¨

          // í˜„ì¬ ì„ íƒëœ í”„ë¡œì íŠ¸ê°€ ì‚­ì œë˜ë©´ í™ˆìœ¼ë¡œ
          if (selectedProject?.id === projectId) {
            setSelectedProject(null)
            setCurrentView('projects')
          }
        } else {
          console.error('í”„ë¡œì íŠ¸ ì‚­ì œ ì‹¤íŒ¨:', result.error)
