        )

      case 'details':
        if (!selectedProject) {
          return <div>í”„ë¡œì íŠ¸ë¥¼ ì„ íƒí•´ì£¼ì„¸ìš”.</div>
        }
        return (
          <ProjectDetails
            project={selectedProject}
