const STORAGE_KEYS = {
  PROJECTS: 'volty-projects',
  SELECTED_PROJECT: 'volty-selected-project',
  USER_PREFERENCES: 'volty-user-preferences',
  AUTO_SAVE_INTERVAL: 'volty-auto-save-interval'
}

interface UserPreferences {
  autoSave: boolean
  autoSaveInterval: number // ì´ˆ ë‹¨ìœ„
  theme: 'dark' | 'light'
  lastOpenProject?: string
}

export class StorageManager {
  // í”„ë¡œì íŠ¸ ë°ì´í„° ì €ì¥
  static saveProjects(projects: any[]): void {
    try {
      localStorage.setItem(STORAGE_KEYS.PROJECTS, JSON.stringify(projects))
      console.log('í”„ë¡œì íŠ¸ ë°ì´í„° ì €ì¥ë¨:', projects.length, 'ê°œ í”„ë¡œì íŠ¸')
    } catch (error) {
      console.error('í”„ë¡œì íŠ¸ ì €ì¥ ì‹¤íŒ¨:', error)
    }
  }

  // í”„ë¡œì íŠ¸ ë°ì´í„° ë¡œë“œ
  static loadProjects(): any[] {
    try {
      const stored = localStorage.getItem(STORAGE_KEYS.PROJECTS)
      if (!stored) return []

      const projects = JSON.parse(stored)
      console.log('í”„ë¡œì íŠ¸ ë°ì´í„° ë¡œë“œë¨:', projects.length, 'ê°œ í”„ë¡œì íŠ¸')
      return projects
    } catch (error) {
      console.error('í”„ë¡œì íŠ¸ ë¡œë“œ ì‹¤íŒ¨:', error)
      return []
    }
  }

  // ì„ íƒëœ í”„ë¡œì íŠ¸ ì €ì¥
  static saveSelectedProject(project: any): void {
    try {
      localStorage.setItem(STORAGE_KEYS.SELECTED_PROJECT, JSON.stringify(project))
      this.updateUserPreference('lastOpenProject', project.id)
    } catch (error) {
      console.error('ì„ íƒ í”„ë¡œì íŠ¸ ì €ì¥ ì‹¤íŒ¨:', error)
    }
  }

  // ì„ íƒëœ í”„ë¡œì íŠ¸ ë¡œë“œ
  static loadSelectedProject(): any | null {
    try {
      const stored = localStorage.getItem(STORAGE_KEYS.SELECTED_PROJECT)
      return stored ? JSON.parse(stored) : null
    } catch (error) {
      console.error('ì„ íƒ í”„ë¡œì íŠ¸ ë¡œë“œ ì‹¤íŒ¨:', error)
      return null
    }
  }

  // ì‚¬ìš©ì ì„¤ì • ì €ì¥
  static saveUserPreferences(preferences: UserPreferences): void {
    try {
      localStorage.setItem(STORAGE_KEYS.USER_PREFERENCES, JSON.stringify(preferences))
    } catch (error) {
      console.error('ì‚¬ìš©ì ì„¤ì • ì €ì¥ ì‹¤íŒ¨:', error)
    }
  }

  // ì‚¬ìš©ì ì„¤ì • ë¡œë“œ
  static loadUserPreferences(): UserPreferences {
    try {
      const stored = localStorage.getItem(STORAGE_KEYS.USER_PREFERENCES)
      if (!stored) {
        // ê¸°ë³¸ ì„¤ì •
        return {
          autoSave: true,
          autoSaveInterval: 30, // 30ì´ˆ
          theme: 'dark'
        }
      }
      return JSON.parse(stored)
    } catch (error) {
      console.error('ì‚¬ìš©ì ì„¤ì • ë¡œë“œ ì‹¤íŒ¨:', error)
      return {
        autoSave: true,
        autoSaveInterval: 30,
        theme: 'dark'
      }
    }
  }

  // íŠ¹ì • ì„¤ì •ê°’ë§Œ ì—…ë°ì´íŠ¸
  static updateUserPreference(key: keyof UserPreferences, value: any): void {
    const preferences = this.loadUserPreferences()
    preferences[key] = value
