# PowerShell Profile Configuration
# Created by Claude Code Terminal Setup

# Set UTF-8 encoding
[Console]::OutputEncoding = [System.Text.Encoding]::UTF8
$PSDefaultParameterValues['*:Encoding'] = 'utf8'

# Aliases for common commands
Set-Alias ll Get-ChildItem
Set-Alias grep Select-String
Set-Alias touch New-Item
Set-Alias which Get-Command
Set-Alias np notepad
Set-Alias npp "C:\Program Files\Notepad++\notepad++.exe" -ErrorAction SilentlyContinue

# Git aliases
function gs { git status }
function ga { git add $args }
function gc { git commit -m $args }
function gp { git push }
function gpl { git pull }
function gb { git branch $args }
function gco { git checkout $args }
function glog { git log --oneline --graph --decorate --all }

# Navigation shortcuts
function .. { Set-Location .. }
function ... { Set-Location ..\.. }
function .... { Set-Location ..\..\.. }
function home { Set-Location ~ }
function desktop { Set-Location ~/Desktop }
function downloads { Set-Location ~/Downloads }
function docs { Set-Location ~/Documents }

# Utility functions
function mkcd {
    param($dir)
    mkdir $dir -Force
    Set-Location $dir
}

function la { Get-ChildItem -Force }
function lsa { Get-ChildItem -Force -Attributes Hidden,System,Normal }

function path {
    $env:Path -split ';' | Sort-Object
}

function reload-profile {
    . $PROFILE
    Write-Host "Profile reloaded!" -ForegroundColor Green
}

function edit-profile {
    code $PROFILE
}

function find-file {
    param($pattern)
    Get-ChildItem -Recurse -Filter $pattern 2>$null
}

function find-text {
    param($pattern, $path = ".")
    Get-ChildItem -Path $path -Recurse -File | Select-String -Pattern $pattern
}

function extract {
    param($file)
    if (Test-Path $file) {
        switch -Regex ($file) {
            '\.zip$' { Expand-Archive $file -DestinationPath . }
            '\.tar$' { tar -xf $file }
            '\.tar\.gz$|\.tgz$' { tar -xzf $file }
            '\.7z$' { 7z x $file }
            default { Write-Host "Unknown archive format" -ForegroundColor Red }
        }
    }
}

function size {
    param($path = ".")
    Get-ChildItem -Path $path -Recurse | Measure-Object -Property Length -Sum | 
    ForEach-Object { "{0:N2} MB" -f ($_.Sum / 1MB) }
}

function ports {
    netstat -an | Select-String "LISTENING"
}

function ip {
    (Get-NetIPAddress | Where-Object {$_.AddressFamily -eq 'IPv4' -and $_.InterfaceAlias -notlike '*Loopback*'}).IPAddress
}

function weather {
    param($city = "Seoul")
    (Invoke-WebRequest "http://wttr.in/$city" -UserAgent "curl").Content
}

# Docker shortcuts (if Docker is installed)
if (Get-Command docker -ErrorAction SilentlyContinue) {
    function dps { docker ps }
    function dpsa { docker ps -a }
    function di { docker images }
    function dex { 
        param($container, $command = "bash")
        docker exec -it $container $command
    }
    function dlog {
        param($container)
        docker logs -f $container
    }
    function dstop { docker stop $(docker ps -q) }
    function dclean { 
        docker system prune -a -f
        docker volume prune -f
    }
}

# Python virtual environment shortcuts
function venv {
    param($name = "venv")
    python -m venv $name
    & "./$name/Scripts/Activate.ps1"
}

function activate {
    param($name = "venv")
    & "./$name/Scripts/Activate.ps1"
}

# Node.js shortcuts
if (Get-Command node -ErrorAction SilentlyContinue) {
    function nrd { npm run dev }
    function nrs { npm run start }
    function nrb { npm run build }
    function nrt { npm run test }
    function ni { npm install }
    function nis { npm install --save $args }
    function nisd { npm install --save-dev $args }
}

# Better history search
Set-PSReadLineOption -HistorySearchCursorMovesToEnd
Set-PSReadLineKeyHandler -Key UpArrow -Function HistorySearchBackward
Set-PSReadLineKeyHandler -Key DownArrow -Function HistorySearchForward
Set-PSReadLineKeyHandler -Key Tab -Function MenuComplete

# Show git branch in prompt (simple version)
function prompt {
    $branch = ""
    if (Test-Path .git) {
        $branch = git branch --show-current 2>$null
        if ($branch) {
            $branch = " [$branch]"
        }
    }

    $path = (Get-Location).Path.Replace($HOME, "~")
    Write-Host "$path" -NoNewline -ForegroundColor Cyan
    Write-Host "$branch" -NoNewline -ForegroundColor Yellow
    return "> "
}

# Welcome message
Write-Host "PowerShell $($PSVersionTable.PSVersion)" -ForegroundColor Green
Write-Host "Profile loaded from: $PROFILE" -ForegroundColor DarkGray
Write-Host "Type 'Get-Command -Module PSReadLine' for readline commands" -ForegroundColor DarkGray
Write-Host ""

