    margin-bottom: 10px;
    border-radius: 5px;
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.notification-item.warning {
    border-left-color: #ff9800;
}

.notification-content {
    flex: 1;
}

.notification-time {
    color: #999;
    font-size: 12px;
}

