// Socket.IO 연결
const socket = io();
let currentStudentId = null;

// 페이지 로드 시 초기화
document.addEventListener('DOMContentLoaded', function() {
    updateClock();
    loadDashboardData();

    // 1초마다 시계 업데이트
    setInterval(updateClock, 1000);

    // 30초마다 데이터 새로고침
    setInterval(loadDashboardData, 30000);

    // 모달 닫기 버튼
    document.querySelector('.close').onclick = function() {
        document.getElementById('extend-modal').style.display = 'none';
    }

    // 모달 외부 클릭 시 닫기
    window.onclick = function(event) {
        const modal = document.getElementById('extend-modal');
        if (event.target == modal) {
            modal.style.display = 'none';
        }
    }
});

// Socket.IO 이벤트 리스너
socket.on('connect', function() {
    console.log('서버에 연결되었습니다');
});

socket.on('refresh_data', function() {
    loadDashboardData();
});

socket.on('student_ended', function(data) {
    loadDashboardData();
    showNotification(`${data.student_id} 학생의 수업이 종료되었습니다`);
});

socket.on('student_extended', function(data) {
    loadDashboardData();
    showNotification(`${data.student_id} 학생의 수업이 ${data.minutes}분 연장되었습니다`);
});

// 현재 시간 표시
function updateClock() {
    const now = new Date();
    const timeString = now.toLocaleString('ko-KR', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
        hour12: false
    });
    document.getElementById('current-time').textContent = timeString;
}

// 대시보드 데이터 로드
async function loadDashboardData() {
    try {
        // 통계 데이터 로드
        const statsResponse = await fetch('/api/stats/today');
        const stats = await statsResponse.json();
        updateStats(stats);

        // 현재 출석 데이터 로드
        const attendanceResponse = await fetch('/api/attendance/current');
        const attendance = await attendanceResponse.json();
        updateAttendanceTable(attendance);

        // 알림 데이터 로드
        const notificationsResponse = await fetch('/api/notifications');
        const notifications = await notificationsResponse.json();
        updateNotifications(notifications);

        // 마지막 업데이트 시간 표시
        const now = new Date();
        document.getElementById('last-update').textContent = 
            `마지막 업데이트: ${now.toLocaleTimeString('ko-KR')}`;

    } catch (error) {
        console.error('데이터 로드 실패:', error);
    }
}

// 통계 업데이트
function updateStats(stats) {
    document.getElementById('total-students').textContent = stats.total_students || 0;
    document.getElementById('current-students').textContent = stats.current_students || 0;
    document.getElementById('ending-soon').textContent = stats.ending_soon || 0;
    document.getElementById('completed-students').textContent = stats.completed_students || 0;
}

// 출석 테이블 업데이트
function updateAttendanceTable(students) {
    const tbody = document.getElementById('attendance-tbody');
    const noDataDiv = document.getElementById('no-students');

    if (students.length === 0) {
        tbody.innerHTML = '';
        noDataDiv.style.display = 'block';
        return;
    }

    noDataDiv.style.display = 'none';
    tbody.innerHTML = '';

    students.forEach(student => {
        const row = document.createElement('tr');

        // 남은 시간에 따른 클래스 설정
        let timeClass = '';
        let badgeClass = 'normal';

        if (student.remaining_minutes <= 0) {
            timeClass = 'danger';
            badgeClass = 'overtime';
        } else if (student.remaining_minutes <= 10) {
            timeClass = 'warning';
            badgeClass = 'ending-soon';
        }

        // 남은 시간 표시
        let remainingText = '';
        if (student.remaining_minutes > 0) {
            const hours = Math.floor(student.remaining_minutes / 60);
            const minutes = student.remaining_minutes % 60;
            if (hours > 0) {
                remainingText = `${hours}시간 ${minutes}분`;
            } else {
                remainingText = `${minutes}분`;
            }
        } else {
            remainingText = '초과';
        }

        row.innerHTML = `
            <td>${student.student_id}</td>
            <td><strong>${student.student_name}</strong></td>
            <td>${student.check_in_time}</td>
            <td>${student.expected_end_time}</td>
            <td class="time-remaining ${timeClass}">${remainingText}</td>
            <td><span class="badge ${badgeClass}">${getStatusText(badgeClass)}</span></td>
            <td>
                <button class="btn btn-primary" onclick="endSession('${student.student_id}')">종료</button>
                <button class="btn btn-warning" onclick="showExtendModal('${student.student_id}', '${student.student_name}')">연장</button>
            </td>
        `;

        tbody.appendChild(row);
    });
}

// 상태 텍스트 변환
function getStatusText(status) {
    const statusMap = {
        'normal': '수업중',
        'ending-soon': '종료 임박',
        'overtime': '시간 초과'
    };
    return statusMap[status] || '수업중';
}

// 알림 업데이트
function updateNotifications(notifications) {
    const container = document.getElementById('notifications-container');

    if (notifications.length === 0) {
        container.innerHTML = '<div class="no-data">최근 알림이 없습니다</div>';
        return;
    }

    container.innerHTML = '';

    notifications.forEach(notif => {
        const item = document.createElement('div');
        item.className = `notification-item ${notif.type === 'ending_soon' ? 'warning' : ''}`;

        const sentAt = new Date(notif.sent_at);
        const timeAgo = getTimeAgo(sentAt);

        item.innerHTML = `
            <div class="notification-content">
                <div>${notif.message}</div>
                <div class="notification-time">${timeAgo}</div>
            </div>
        `;

        container.appendChild(item);
    });
}

// 시간 경과 텍스트
function getTimeAgo(date) {
    const now = new Date();
    const diff = Math.floor((now - date) / 1000); // 초 단위

    if (diff < 60) return '방금 전';
    if (diff < 3600) return `${Math.floor(diff / 60)}분 전`;
    if (diff < 86400) return `${Math.floor(diff / 3600)}시간 전`;
    return date.toLocaleString('ko-KR');
}

// 수업 종료
async function endSession(studentId) {
    if (!confirm('정말로 이 학생의 수업을 종료하시겠습니까?')) {
        return;
    }

    try {
        const response = await fetch(`/api/student/${studentId}/end`, {
            method: 'POST'
        });

        if (response.ok) {
            loadDashboardData();
            showNotification('수업이 종료되었습니다');
        }
    } catch (error) {
        console.error('수업 종료 실패:', error);
        alert('수업 종료 중 오류가 발생했습니다');
    }
}

// 연장 모달 표시
function showExtendModal(studentId, studentName) {
    currentStudentId = studentId;
    document.getElementById('extend-student-name').textContent = 
        `${studentName} 학생의 수업 시간을 연장하시겠습니까?`;
    document.getElementById('extend-modal').style.display = 'block';
}

// 시간 연장
async function extendTime(minutes) {
    if (!currentStudentId) return;

    try {
        const response = await fetch(`/api/student/${currentStudentId}/extend`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ minutes: minutes })
        });

        if (response.ok) {
            document.getElementById('extend-modal').style.display = 'none';
            loadDashboardData();
            showNotification(`수업이 ${minutes}분 연장되었습니다`);
        }
    } catch (error) {
