// src/services/claudeService.ts
class ClaudeService {
  private apiKey = import.meta.env.VITE_CLAUDE_API_KEY;

  async generateScript(emotionDump: string): Promise<string> {
    const prompt = `
    YouTube ì±„ë„ Voltyì˜ ìŠ¤íƒ€ì¼ë¡œ 15ë¶„ ëŒ€ë³¸ì„ ì‘ì„±í•´ì£¼ì„¸ìš”.

