import sqlite3 from 'sqlite3'
import { app } from 'electron'
import { join } from 'path'
import { readFileSync } from 'fs'

export interface Project {
  id: string
  name: string
  problem?: string
  description?: string
  status: string
  created_at: string
  updated_at: string
}

export interface Folder {
  id: number
  project_id: string
  name: string
  path: string
  order_index: number
  completed: boolean
  video_count: number
  created_at: string
}

export interface Script {
  id: number
  project_id: string
  emotion_dump?: string
  generated_script?: string
  duration_minutes: number
  created_at: string
}

export class DatabaseManager {
  private db: sqlite3.Database | null = null
  private dbPath: string

  constructor() {
    const userDataPath = app.getPath('userData')
    this.dbPath = join(userDataPath, 'volty-studio.db')
  }

  async initialize(): Promise<void> {
    return new Promise((resolve, reject) => {
      this.db = new sqlite3.Database(this.dbPath, (err) => {
        if (err) {
          console.error('ë°ì´í„°ë² ì´ìŠ¤ ì—°ê²° ì‹¤íŒ¨:', err)
          reject(err)
          return
        }

        console.log('ë°ì´í„°ë² ì´ìŠ¤ ì—°ê²° ì„±ê³µ:', this.dbPath)
        this.setupSchema().then(resolve).catch(reject)
      })
    })
  }

  private async setupSchema(): Promise<void> {
    if (!this.db) throw new Error('ë°ì´í„°ë² ì´ìŠ¤ê°€ ì´ˆê¸°í™”ë˜ì§€ ì•ŠìŒ')

    return new Promise((resolve, reject) => {
      // ìŠ¤í‚¤ë§ˆ íŒŒì¼ ì½ê¸°
      const schemaPath = join(__dirname, '../../database/schema.sql')
      const schema = readFileSync(schemaPath, 'utf8')

      // ê° ì¿¼ë¦¬ ì‹¤í–‰
      const queries = schema.split(';').filter(query => query.trim())

      let completed = 0
      queries.forEach((query) => {
        if (!query.trim()) {
          completed++
          if (completed === queries.length) resolve()
          return
        }

        this.db!.run(query, (err) => {
          if (err) {
            console.error('ìŠ¤í‚¤ë§ˆ ì‹¤í–‰ ì˜¤ë¥˜:', err)
            reject(err)
            return
          }

          completed++
          if (completed === queries.length) {
            console.log('ë°ì´í„°ë² ì´ìŠ¤ ìŠ¤í‚¤ë§ˆ ì„¤ì • ì™„ë£Œ')
            resolve()
          }
        })
      })
    })
  }

  async createProject(data: {
    name: string
    problem?: string
    description?: string
  }): Promise<Project> {
    if (!this.db) throw new Error('ë°ì´í„°ë² ì´ìŠ¤ê°€ ì´ˆê¸°í™”ë˜ì§€ ì•ŠìŒ')

    const projectId = `volty_${Date.now()}`

    return new Promise((resolve, reject) => {
      const query = `
        INSERT INTO projects (id, name, problem, description, status)
        VALUES (?, ?, ?, ?, 'filming')
      `

      this.db!.run(query, [projectId, data.name, data.problem || null, data.description || null], function(err) {
        if (err) {
          reject(err)
          return
        }

        // ê¸°ë³¸ í´ë” êµ¬ì¡° ìƒì„±
        const folderQuery = `
          INSERT INTO folders (project_id, name, path, order_index, completed)
          SELECT ?, name, '', order_index, 0
          FROM default_folders
        `

        this.db!.run(folderQuery, [projectId], (folderErr) => {
          if (folderErr) {
            reject(folderErr)
            return
          }

          // ìƒì„±ëœ í”„ë¡œì íŠ¸ ì¡°íšŒ
          const selectQuery = 'SELECT * FROM projects WHERE id = ?'
          this.db!.get(selectQuery, [projectId], (selectErr, row) => {
            if (selectErr) {
              reject(selectErr)
              return
            }
            resolve(row as Project)
          })
        })
      })
    })
  }

  async getProjects(): Promise<Project[]> {
    if (!this.db) throw new Error('ë°ì´í„°ë² ì´ìŠ¤ê°€ ì´ˆê¸°í™”ë˜ì§€ ì•ŠìŒ')

    return new Promise((resolve, reject) => {
      const query = 'SELECT * FROM projects ORDER BY created_at DESC'

      this.db!.all(query, (err, rows) => {
        if (err) {
          reject(err)
          return
        }
        resolve(rows as Project[])
      })
    })
  }

  async getProjectWithFolders(projectId: string): Promise<Project & { folders: Folder[] }> {
    if (!this.db) throw new Error('ë°ì´í„°ë² ì´ìŠ¤ê°€ ì´ˆê¸°í™”ë˜ì§€ ì•ŠìŒ')

    return new Promise((resolve, reject) => {
      // í”„ë¡œì íŠ¸ ì¡°íšŒ
      const projectQuery = 'SELECT * FROM projects WHERE id = ?'

      this.db!.get(projectQuery, [projectId], (err, projectRow) => {
        if (err) {
          reject(err)
          return
        }

        if (!projectRow) {
          reject(new Error('í”„ë¡œì íŠ¸ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŒ'))
          return
        }

        // í´ë” ì¡°íšŒ
        const foldersQuery = 'SELECT * FROM folders WHERE project_id = ? ORDER BY order_index'

        this.db!.all(foldersQuery, [projectId], (folderErr, folderRows) => {
          if (folderErr) {
            reject(folderErr)
            return
          }

          resolve({
            ...(projectRow as Project),
            folders: folderRows as Folder[]
          })
        })
      })
    })
  }

  async updateFolderStatus(folderId: number, completed: boolean): Promise<void> {
    if (!this.db) throw new Error('ë°ì´í„°ë² ì´ìŠ¤ê°€ ì´ˆê¸°í™”ë˜ì§€ ì•ŠìŒ')

    return new Promise((resolve, reject) => {
      const query = 'UPDATE folders SET completed = ? WHERE id = ?'

      this.db!.run(query, [completed ? 1 : 0, folderId], (err) => {
        if (err) {
          reject(err)
          return
        }
        resolve()
      })
    })
  }

  async saveScript(data: {
    projectId: string
    emotionDump: string
    generatedScript: string
    duration?: number
  }): Promise<Script> {
    if (!this.db) throw new Error('ë°ì´í„°ë² ì´ìŠ¤ê°€ ì´ˆê¸°í™”ë˜ì§€ ì•ŠìŒ')

    return new Promise((resolve, reject) => {
      const query = `
        INSERT INTO scripts (project_id, emotion_dump, generated_script, duration_minutes)
        VALUES (?, ?, ?, ?)
      `

      this.db!.run(query, [
        data.projectId,
        data.emotionDump,
        data.generatedScript,
        data.duration || 15
      ], function(err) {
        if (err) {
          reject(err)
          return
        }

        // ìƒì„±ëœ ìŠ¤í¬ë¦½íŠ¸ ì¡°íšŒ
        const selectQuery = 'SELECT * FROM scripts WHERE id = ?'
        this.db!.get(selectQuery, [this.lastID], (selectErr, row) => {
          if (selectErr) {
            reject(selectErr)
            return
          }
          resolve(row as Script)
        })
      })
    })
  }

  async getLatestScript(projectId: string): Promise<Script | null> {
    if (!this.db) throw new Error('ë°ì´í„°ë² ì´ìŠ¤ê°€ ì´ˆê¸°í™”ë˜ì§€ ì•ŠìŒ')

    return new Promise((resolve, reject) => {
      const query = `
        SELECT * FROM scripts 
        WHERE project_id = ? 
        ORDER BY created_at DESC 
        LIMIT 1
      `

      this.db!.get(query, [projectId], (err, row) => {
        if (err) {
          reject(err)
          return
        }
        resolve(row as Script || null)
      })
    })
  }
}

