import { contextBridge, ipcRenderer } from 'electron'

// API íƒ€ì… ì •ì˜
export interface ElectronAPI {
  // í”„ë¡œì íŠ¸ ê´€ë¦¬
  createProject: (data: {
    name: string
    problem?: string
    description?: string
