@tailwind base;
@tailwind components;
@tailwind utilities;

/* ì „ì—­ ìŠ¤íƒ€ì¼ */
@layer base {
  * {
    box-sizing: border-box;
  }

  body {
    margin: 0;
    padding: 0;
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen',
      'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue',
      sans-serif;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    background-color: #0f0f0f;
    color: #ffffff;
    overflow-x: hidden;
  }

  #root {
    width: 100%;
    min-height: 100vh;
  }
}

/* ì»¤ìŠ¤í…€ ìŠ¤í¬ë¡¤ë°” */
@layer utilities {
  .scrollbar-thin {
    scrollbar-width: thin;
    scrollbar-color: #374151 #1f1f1f;
  }

  .scrollbar-thin::-webkit-scrollbar {
    width: 6px;
    height: 6px;
  }

  .scrollbar-thin::-webkit-scrollbar-track {
    background: #1f1f1f;
    border-radius: 3px;
  }

  .scrollbar-thin::-webkit-scrollbar-thumb {
    background: #374151;
    border-radius: 3px;
  }

  .scrollbar-thin::-webkit-scrollbar-thumb:hover {
    background: #4b5563;
  }
}

/* ì• ë‹ˆë©”ì´ì…˜ */
@layer utilities {
  .gradient-text {
    @apply bg-gradient-to-r from-volty-pink to-volty-purple bg-clip-text text-transparent;
  }

  .glass-effect {
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    background-color: rgba(15, 15, 15, 0.8);
    border: 1px solid rgba(255, 255, 255, 0.1);
  }

  .hover-glow {
    transition: all 0.3s ease;
  }

  .hover-glow:hover {
    box-shadow: 0 0 20px rgba(236, 72, 153, 0.3);
  }
}

/* í´ë” ê·¸ë¦¬ë“œ ì• ë‹ˆë©”ì´ì…˜ */
@keyframes folder-appear {
  from {
    opacity: 0;
    transform: scale(0.8) translateY(20px);
  }
  to {
    opacity: 1;
    transform: scale(1) translateY(0);
  }
}

.folder-item {
  animation: folder-appear 0.5s ease-out;
}

.folder-item:nth-child(n) {
  animation-delay: calc(var(--item-index) * 50ms);
}

/* ë¡œë”© ìŠ¤í”¼ë„ˆ */
@keyframes spin {
  to {
    transform: rotate(360deg);
  }
}

.animate-spin {
  animation: spin 1s linear infinite;
}

