import { useState, useEffect } from 'react'
import { ProjectGrid } from './components/ProjectGrid'
import { ProjectDetails } from './components/ProjectDetails'
import { AIScriptPanel } from './components/AIScriptPanel'
import { CreateProjectModal } from './components/CreateProjectModal'
import { mockProjects, mockProject, isDevelopmentMode } from './mocks/mockData'

function App() {
  const [currentView, setCurrentView] = useState<'projects' | 'details' | 'script'>('projects')
  const [selectedProject, setSelectedProject] = useState<any>(null)
  const [projects, setProjects] = useState<any[]>([])
  const [showCreateModal, setShowCreateModal] = useState(false)
  const [loading, setLoading] = useState(false)

  // í”„ë¡œì íŠ¸ ëª©ë¡ ë¡œë“œ
  useEffect(() => {
    loadProjects()
  }, [])

  const loadProjects = async () => {
    setLoading(true)
    try {
      if (isDevelopmentMode()) {
        // ê°œë°œ ëª¨ë“œ: Mock ë°ì´í„° ì‚¬ìš©
        console.log('ê°œë°œ ëª¨ë“œ: Mock ë°ì´í„° ì‚¬ìš©')
        setProjects(mockProjects)
      } else {
        // í”„ë¡œë•ì…˜ ëª¨ë“œ: Electron API ì‚¬ìš©
        const result = await window.electronAPI.getProjects()
        if (result.success) {
          setProjects(result.data || [])
        } else {
          console.error('í”„ë¡œì íŠ¸ ë¡œë“œ ì‹¤íŒ¨:', result.error)
        }
      }
    } catch (error) {
      console.error('í”„ë¡œì íŠ¸ ë¡œë“œ ì˜¤ë¥˜:', error)
    } finally {
      setLoading(false)
    }
  }

  const handleProjectSelect = async (projectId: string) => {
    setLoading(true)
    try {
      if (isDevelopmentMode()) {
        // ê°œë°œ ëª¨ë“œ: Mock ë°ì´í„° ì‚¬ìš©
        const project = mockProjects.find(p => p.id === projectId) || mockProject
        setSelectedProject(project)
      } else {
        // í”„ë¡œë•ì…˜ ëª¨ë“œ: Electron API ì‚¬ìš©
        const result = await window.electronAPI.getProjectDetails(projectId)
        if (result.success) {
          setSelectedProject(result.data)
        } else {
          console.error('í”„ë¡œì íŠ¸ ìƒì„¸ ë¡œë“œ ì‹¤íŒ¨:', result.error)
        }
      }
      setCurrentView('details')
    } catch (error) {
      console.error('í”„ë¡œì íŠ¸ ì„ íƒ ì˜¤ë¥˜:', error)
    } finally {
      setLoading(false)
    }
  }

  const handleCreateProject = async (projectData: {
    name: string
    problem?: string
    description?: string
  }) => {
    setLoading(true)
    try {
      if (isDevelopmentMode()) {
        // ê°œë°œ ëª¨ë“œ: Mock ìƒì„±
        console.log('Mock í”„ë¡œì íŠ¸ ìƒì„±:', projectData)
        const newProject = {
          ...mockProject,
          id: `mock-${Date.now()}`,
          name: projectData.name,
          problem: projectData.problem,
          description: projectData.description,
          created_at: new Date().toISOString()
        }
        setProjects([newProject, ...projects])
      } else {
        // í”„ë¡œë•ì…˜ ëª¨ë“œ: Electron API ì‚¬ìš©
        const result = await window.electronAPI.createProject(projectData)
        if (result.success) {
          await loadProjects() // ëª©ë¡ ìƒˆë¡œê³ ì¹¨
        } else {
          console.error('í”„ë¡œì íŠ¸ ìƒì„± ì‹¤íŒ¨:', result.error)
        }
      }
    } catch (error) {
      console.error('í”„ë¡œì íŠ¸ ìƒì„± ì˜¤ë¥˜:', error)
    } finally {
      setLoading(false)
      setShowCreateModal(false)
    }
  }

  const handleBackToProjects = () => {
    setCurrentView('projects')
    setSelectedProject(null)
  }

  const handleOpenScriptPanel = () => {
    setCurrentView('script')
  }

  const handleBackFromScript = () => {
    setCurrentView('details')
  }

  // ë©”ì¸ ë Œë”ë§
  const renderCurrentView = () => {
    if (loading) {
      return (
        <div className="flex items-center justify-center min-h-screen">
          <div className="text-center">
            <div className="animate-spin w-8 h-8 border-2 border-volty-pink border-t-transparent rounded-full mx-auto mb-4"></div>
            <p className="text-gray-400">ë¡œë”© ì¤‘...</p>
          </div>
        </div>
      )
    }

    switch (currentView) {
      case 'projects':
        return (
          <ProjectGrid
            projects={projects}
            onProjectSelect={handleProjectSelect}
            onCreateProject={() => setShowCreateModal(true)}
          />
        )

      case 'details':
        if (!selectedProject) {
          return <div>í”„ë¡œì íŠ¸ë¥¼ ì„ íƒí•´ì£¼ì„¸ìš”.</div>
        }
        return (
          <ProjectDetails
            project={selectedProject}
            onBack={handleBackToProjects}
            onOpenScript={handleOpenScriptPanel}
          />
        )

      case 'script':
        if (!selectedProject) {
          return <div>í”„ë¡œì íŠ¸ë¥¼ ì„ íƒí•´ì£¼ì„¸ìš”.</div>
        }
        return (
          <AIScriptPanel
            project={selectedProject}
            onBack={handleBackFromScript}
          />
        )

      default:
        return <div>ì•Œ ìˆ˜ ì—†ëŠ” ë·°ì…ë‹ˆë‹¤.</div>
    }
  }

  return (
    <div className="min-h-screen bg-volty-dark text-white">
      {/* ì•± í—¤ë” */}
      <header className="border-b border-gray-800 p-4 glass-effect">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <div className="w-8 h-8 bg-gradient-to-r from-volty-pink to-volty-purple rounded-lg"></div>
            <h1 className="text-2xl font-bold gradient-text">
              Volty Creator Studio
            </h1>
          </div>

          {/* í˜„ì¬ ë·° í‘œì‹œ */}
          <div className="flex items-center space-x-2 text-sm text-gray-400">
            {currentView === 'projects' && 'ğŸ“ í”„ë¡œì íŠ¸'}
            {currentView === 'details' && selectedProject && (
              <span>ğŸ¬ {selectedProject.name}</span>
            )}
            {currentView === 'script' && selectedProject && (
              <span>âœï¸ {selectedProject.name} - AI ëŒ€ë³¸</span>
            )}

            {isDevelopmentMode() && (
              <span className="px-2 py-1 bg-yellow-600 text-yellow-100 text-xs rounded">
                ê°œë°œëª¨ë“œ
              </span>
            )}
          </div>
        </div>
      </header>

      {/* ë©”ì¸ ì½˜í…ì¸  */}
      <main className="p-6">
        {renderCurrentView()}
      </main>

      {/* í”„ë¡œì íŠ¸ ìƒì„± ëª¨ë‹¬ */}
      {showCreateModal && (
        <CreateProjectModal
          onCreate={handleCreateProject}
          onClose={() => setShowCreateModal(false)}
          loading={loading}
        />
      )}
    </div>
  )
}

export default App

