              icon={<SchoolIcon />}
              color={theme.palette.primary.main}
              trend={userStats.growthRate.toFixed(1)}
            />
          </Grid>
          <Grid item xs={12} md={3}>
            <MetricCard
              title="Learning Streak"
              value={`${userStats.currentStreak} days`}
              subtitle={`Best: ${userStats.longestStreak} days`}
              icon={<FireIcon />}
              color={theme.palette.error.main}
            />
          </Grid>
          <Grid item xs={12} md={3}>
            <MetricCard
              title="Total Sessions"
              value={userStats.totalSessions}
              subtitle={`${userStats.completedMinutes} minutes`}
              icon={<ScheduleIcon />}
              color={theme.palette.success.main}
            />
          </Grid>
          <Grid item xs={12} md={3}>
            <MetricCard
              title="Average Score"
              value={`${userStats.averageScore}%`}
              subtitle="Last 7 days"
              icon={<TrophyIcon />}
              color={theme.palette.warning.main}
              trend={5.2}
            />
          </Grid>
        </Grid>

        {/* Charts Section */}
        <Grid container spacing={3} mb={4}>
          {/* Weekly Progress Chart */}
          <Grid item xs={12} lg={8}>
            <motion.div variants={itemVariants}>
              <Card>
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    Weekly Progress
                  </Typography>
                  <ResponsiveContainer width="100%" height={300}>
                    <AreaChart data={userStats.weeklyProgress}>
                      <defs>
                        <linearGradient id="colorScore" x1="0" y1="0" x2="0" y2="1">
                          <stop offset="5%" stopColor={theme.palette.primary.main} stopOpacity={0.8}/>
                          <stop offset="95%" stopColor={theme.palette.primary.main} stopOpacity={0.1}/>
                        </linearGradient>
                      </defs>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="day" />
                      <YAxis />
                      <RechartsTooltip />
                      <Area 
                        type="monotone" 
                        dataKey="score" 
                        stroke={theme.palette.primary.main} 
                        fillOpacity={1} 
                        fill="url(#colorScore)" 
                      />
                      <Line 
                        type="monotone" 
                        dataKey="minutes" 
                        stroke={theme.palette.secondary.main} 
                        strokeWidth={2}
                      />
                    </AreaChart>
                  </ResponsiveContainer>
                </CardContent>
              </Card>
            </motion.div>
          </Grid>

          {/* Skill Radar Chart */}
          <Grid item xs={12} lg={4}>
            <motion.div variants={itemVariants}>
              <Card>
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    Skill Analysis
                  </Typography>
                  <ResponsiveContainer width="100%" height={300}>
                    <RadarChart data={userStats.skillRadar}>
                      <PolarGrid />
                      <PolarAngleAxis dataKey="skill" />
                      <PolarRadiusAxis angle={90} domain={[0, 100]} />
                      <Radar 
                        name="Skills" 
                        dataKey="value" 
                        stroke={theme.palette.primary.main} 
                        fill={theme.palette.primary.main} 
                        fillOpacity={0.6} 
                      />
                    </RadarChart>
                  </ResponsiveContainer>
                </CardContent>
              </Card>
            </motion.div>
          </Grid>
        </Grid>

        {/* Recent Activities & Achievements */}
        <Grid container spacing={3}>
          {/* Recent Activities */}
          <Grid item xs={12} md={6}>
            <motion.div variants={itemVariants}>
              <Card>
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    Recent Activities
                  </Typography>
                  <Stack spacing={2}>
                    {userStats.recentActivities.map((activity, index) => (
                      <motion.div
                        key={index}
                        initial={{ x: -20, opacity: 0 }}
                        animate={{ x: 0, opacity: 1 }}
                        transition={{ delay: index * 0.1 }}
                      >
                        <Paper
                          sx={{
                            p: 2,
                            display: 'flex',
                            justifyContent: 'space-between',
                            alignItems: 'center',
                            '&:hover': {
                              bgcolor: alpha(theme.palette.primary.main, 0.05)
                            }
                          }}
                        >
                          <Box display="flex" alignItems="center" gap={2}>
                            <Avatar sx={{ bgcolor: theme.palette.primary.light }}>
                              {activity.type === 'lesson' && <SchoolIcon />}
                              {activity.type === 'chat' && <ChatIcon />}
                              {activity.type === 'quiz' && <AssessmentIcon />}
                            </Avatar>
                            <Box>
                              <Typography variant="body1">
                                {activity.title}
                              </Typography>
                              <Typography variant="caption" color="text.secondary">
                                {activity.time}
                              </Typography>
                            </Box>
                          </Box>
                          <Chip 
                            label={`${activity.score}%`} 
                            color={activity.score >= 80 ? 'success' : 'warning'}
                            size="small"
                          />
                        </Paper>
                      </motion.div>
                    ))}
                  </Stack>
                </CardContent>
              </Card>
            </motion.div>
          </Grid>

          {/* Achievements */}
          <Grid item xs={12} md={6}>
            <motion.div variants={itemVariants}>
              <Card>
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    Achievements
                  </Typography>
                  <Grid container spacing={2}>
                    {userStats.achievements.map((achievement, index) => (
                      <Grid item xs={6} key={achievement.id}>
                        <motion.div
                          whileHover={{ scale: achievement.unlocked ? 1.05 : 1 }}
                          initial={{ rotate: 0 }}
                          animate={{ rotate: achievement.unlocked ? 0 : 0 }}
                        >
                          <Paper
                            sx={{
                              p: 2,
                              textAlign: 'center',
                              opacity: achievement.unlocked ? 1 : 0.3,
                              filter: achievement.unlocked ? 'none' : 'grayscale(100%)',
                              cursor: 'pointer'
                            }}
                          >
                            <Typography variant="h2">
                              {achievement.icon}
                            </Typography>
                            <Typography variant="body2">
                              {achievement.name}
                            </Typography>
                            {achievement.unlocked && (
                              <Chip 
                                label="Unlocked" 
                                size="small" 
                                color="success" 
                                sx={{ mt: 1 }}
                              />
                            )}
                          </Paper>
                        </motion.div>
                      </Grid>
                    ))}
                  </Grid>
                </CardContent>
              </Card>
            </motion.div>
          </Grid>
        </Grid>

        {/* Action Buttons */}
        <motion.div variants={itemVariants}>
          <Box mt={4} display="flex" gap={2} flexWrap="wrap">
            <Button
              variant="contained"
              size="large"
              startIcon={<ChatIcon />}
              onClick={() => navigate('/chat')}
              sx={{ 
                background: `linear-gradient(45deg, ${theme.palette.primary.main} 30%, ${theme.palette.primary.light} 90%)`,
                boxShadow: 3
              }}
            >
              Start AI Chat
            </Button>
            <Button
              variant="outlined"
              size="large"
              startIcon={<SchoolIcon />}
              onClick={() => navigate('/tutors')}
            >
              Find a Tutor
            </Button>
            <Button
              variant="outlined"
              size="large"
              startIcon={<AssessmentIcon />}
              onClick={() => navigate('/learning-records')}
            >
              View Progress
            </Button>
          </Box>
        </motion.div>
      </motion.div>
    </Box>
  )
}

