        // ë‹¤ìŒ ëŒ€í™” ë¡œë“œ
      }, 3000)
    }
  }

  // í‚¤ë³´ë“œ ë‹¨ì¶•í‚¤
  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      if (e.key === 'Enter' && !e.shiftKey) {
        e.preventDefault()
        if (inputText.trim()) {
          sendMessage()
        }
      }
      if (e.key === ' ' && dialogue.length > 0) {
        e.preventDefault()
        nextDialogue()
      }
    }

    window.addEventListener('keydown', handleKeyPress)
    return () => window.removeEventListener('keydown', handleKeyPress)
  }, [inputText, dialogue])

  return (
    <Box
      sx={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
