import React, { useState, useEffect, useRef } from 'react'
import {
  Box,
  Container,
  Typography,
  TextField,
  Button,
  IconButton,
  Paper,
  Fade,
  Grow,
  Slide,
  Avatar,
  LinearProgress,
  Chip,
  Menu,
  MenuItem,
  useTheme,
  alpha
} from '@mui/material'
import {
  Send as SendIcon,
  Settings as SettingsIcon,
  VolumeUp as VolumeIcon,
  Save as SaveIcon,
  Replay as ReplayIcon,
  ChevronRight as NextIcon,
  FavoriteBorder,
  Favorite
} from '@mui/icons-material'
import { motion, AnimatePresence } from 'framer-motion'

interface Character {
  id: string
  name: string
  imageUrl: string
  currentEmotion: 'normal' | 'happy' | 'sad' | 'surprised' | 'angry' | 'thinking'
}

interface Scenario {
  id: string
  name: string
  backgroundUrl: string
  ambientSound?: string
}

interface DialogueMessage {
  id: string
  character: string
  text: string
  emotion: string
  isUser?: boolean
}

const VisualNovelStoryMode: React.FC = () => {
  const theme = useTheme()
  const [currentCharacter, setCurrentCharacter] = useState<string>('jennifer')
  const [currentScenario, setCurrentScenario] = useState<string>('cafe')
  const [dialogue, setDialogue] = useState<DialogueMessage[]>([])
  const [inputText, setInputText] = useState('')
  const [isTyping, setIsTyping] = useState(false)
  const [characterEmotion, setCharacterEmotion] = useState<string>('normal')
  const [showSettings, setShowSettings] = useState(false)
  const [settingsAnchor, setSettingsAnchor] = useState<null | HTMLElement>(null)
  const [relationshipLevel, setRelationshipLevel] = useState(3)
  const [autoMode, setAutoMode] = useState(false)
  const textFieldRef = useRef<HTMLInputElement>(null)

  // ìºë¦­í„° ë°ì´í„° (ì‹¤ì œë¡œëŠ” CDNì´ë‚˜ public í´ë”ì—ì„œ ê°€ì ¸ì˜´)
  const characters: Record<string, Character> = {
    jennifer: {
      id: 'jennifer',
      name: 'Jennifer',
      imageUrl: 'https://cdn.jsdelivr.net/gh/Tarikul-Islam-Anik/Animated-Fluent-Emojis/Emojis/People/Woman.png',
      currentEmotion: 'normal'
    },
    alex: {
      id: 'alex',
      name: 'Alex',
      imageUrl: 'https://cdn.jsdelivr.net/gh/Tarikul-Islam-Anik/Animated-Fluent-Emojis/Emojis/People/Man.png',
      currentEmotion: 'normal'
    },
    sophia: {
      id: 'sophia',
      name: 'Sophia',
      imageUrl: 'https://cdn.jsdelivr.net/gh/Tarikul-Islam-Anik/Animated-Fluent-Emojis/Emojis/People/Woman%20Office%20Worker.png',
      currentEmotion: 'normal'
    }
  }

  // ì‹œë‚˜ë¦¬ì˜¤ ë°°ê²½ ì´ë¯¸ì§€
  const scenarios: Record<string, Scenario> = {
    cafe: {
      id: 'cafe',
      name: 'Cozy Cafe',
      backgroundUrl: 'https://images.unsplash.com/photo-1501339847302-ac426a4a7cbb?w=1920&h=1080&fit=crop',
      ambientSound: 'cafe-ambient.mp3'
    },
    park: {
      id: 'park',
      name: 'Cherry Blossom Park',
      backgroundUrl: 'https://images.unsplash.com/photo-1476362174823-3a23f4aa6d76?w=1920&h=1080&fit=crop',
      ambientSound: 'nature-ambient.mp3'
    },
    classroom: {
      id: 'classroom',
      name: 'Study Room',
      backgroundUrl: 'https://images.unsplash.com/photo-1580582932707-520aed937b7b?w=1920&h=1080&fit=crop',
      ambientSound: 'quiet-room.mp3'
    },
    restaurant: {
      id: 'restaurant',
      name: 'Korean Restaurant',
      backgroundUrl: 'https://images.unsplash.com/photo-1552566626-52f8b828add9?w=1920&h=1080&fit=crop',
      ambientSound: 'restaurant-ambient.mp3'
    }
  }

  // ìºë¦­í„° ê°ì •ë³„ ì´ë¯¸ì§€ ë³€í™” (ì‹¤ì œë¡œëŠ” ë‹¤ë¥¸ ì´ë¯¸ì§€ URL ì‚¬ìš©)
  const getCharacterImage = (characterId: string, emotion: string) => {
    const emotionMap: Record<string, string> = {
      happy: 'ğŸ˜Š',
      sad: 'ğŸ˜¢',
      surprised: 'ğŸ˜®',
      angry: 'ğŸ˜ ',
      thinking: 'ğŸ¤”',
      normal: 'ğŸ˜'
    }

    // ì‹¤ì œë¡œëŠ” emotionë³„ ë‹¤ë¥¸ ì¼ëŸ¬ìŠ¤íŠ¸ URL ë°˜í™˜
    return characters[characterId]?.imageUrl || ''
  }

  // ëŒ€í™” ì „ì†¡
  const sendMessage = async () => {
    if (!inputText.trim()) return

    // ì‚¬ìš©ì ë©”ì‹œì§€ëŠ” ëŒ€í™” ê¸°ë¡ì— ì¶”ê°€í•˜ì§€ë§Œ í™”ë©´ì—ëŠ” í‘œì‹œí•˜ì§€ ì•ŠìŒ
    const userMessage: DialogueMessage = {
      id: Date.now().toString(),
      character: 'You',
      text: inputText,
      emotion: 'normal',
      isUser: true
    }

    setInputText('')
    setIsTyping(true)

    try {
      const response = await fetch('http://localhost:3001/api/story/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          character: currentCharacter,
          scenario: currentScenario,
          message: inputText,
          sessionId: 'visual-novel-session-1'
        })
      })

      if (response.ok) {
        const data = await response.json()

        setTimeout(() => {
          const aiMessage: DialogueMessage = {
            id: (Date.now() + 1).toString(),
            character: characters[currentCharacter]?.name || 'AI',
            text: data.message,
            emotion: data.emotion || 'normal',
            isUser: false
          }

          setDialogue([aiMessage]) // í•œ ë²ˆì— í•˜ë‚˜ì˜ ëŒ€í™”ë§Œ í‘œì‹œ
          setCharacterEmotion(data.emotion || 'normal')
          setIsTyping(false)

          if (data.relationshipLevel) {
            setRelationshipLevel(data.relationshipLevel)
          }
        }, 1500)
      }
    } catch (error) {
      console.error('Story chat error:', error)
      setIsTyping(false)

      // ë°ëª¨ ì‘ë‹µ
      const demoResponse: DialogueMessage = {
        id: (Date.now() + 1).toString(),
        character: characters[currentCharacter]?.name || 'AI',
        text: "Oh, that sounds wonderful! I'd love to hear more about your experiences. This place has such a nice atmosphere, doesn't it?",
        emotion: 'happy',
        isUser: false
      }

      setTimeout(() => {
        setDialogue([demoResponse])
        setCharacterEmotion('happy')
        setIsTyping(false)
      }, 1500)
    }
  }

  // ë‹¤ìŒ ëŒ€í™”ë¡œ ì§„í–‰
  const nextDialogue = () => {
    // ìë™ ëª¨ë“œì—ì„œ ë‹¤ìŒ ëŒ€í™” ì§„í–‰ ë¡œì§
    if (autoMode && dialogue.length > 0) {
      setTimeout(() => {
        // ë‹¤ìŒ ëŒ€í™” ë¡œë“œ
      }, 3000)
    }
  }

  // í‚¤ë³´ë“œ ë‹¨ì¶•í‚¤
  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      if (e.key === 'Enter' && !e.shiftKey) {
        e.preventDefault()
        if (inputText.trim()) {
          sendMessage()
        }
      }
      if (e.key === ' ' && dialogue.length > 0) {
        e.preventDefault()
        nextDialogue()
      }
    }

    window.addEventListener('keydown', handleKeyPress)
    return () => window.removeEventListener('keydown', handleKeyPress)
  }, [inputText, dialogue])

  return (
    <Box
      sx={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        overflow: 'hidden',
        backgroundColor: '#000'
      }}
    >
      {/* ë°°ê²½ ì´ë¯¸ì§€ */}
      <Box
        sx={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundImage: `url(${scenarios[currentScenario]?.backgroundUrl})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          filter: 'brightness(0.7)',
          zIndex: 1
        }}
      />

      {/* ê·¸ë¼ë°ì´ì…˜ ì˜¤ë²„ë ˆì´ */}
      <Box
        sx={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: `linear-gradient(to bottom, 
            ${alpha(theme.palette.background.default, 0)} 0%, 
            ${alpha(theme.palette.background.default, 0.3)} 60%, 
            ${alpha(theme.palette.background.default, 0.7)} 100%)`,
          zIndex: 2
        }}
      />

      {/* ìƒë‹¨ UI (ì„¤ì •, ì €ì¥ ë“±) */}
      <Box
        sx={{
          position: 'absolute',
          top: 20,
          right: 20,
          display: 'flex',
          gap: 2,
          zIndex: 10
        }}
      >
        <IconButton
          onClick={(e) => {
            setSettingsAnchor(e.currentTarget)
            setShowSettings(true)
          }}
          sx={{
            backgroundColor: alpha(theme.palette.background.paper, 0.8),
            '&:hover': {
              backgroundColor: alpha(theme.palette.background.paper, 0.9)
            }
          }}
        >
          <SettingsIcon />
        </IconButton>

        <IconButton
          sx={{
            backgroundColor: alpha(theme.palette.background.paper, 0.8),
            '&:hover': {
              backgroundColor: alpha(theme.palette.background.paper, 0.9)
            }
          }}
        >
          <SaveIcon />
        </IconButton>
      </Box>

      {/* ê´€ê³„ë„ ë ˆë²¨ í‘œì‹œ */}
      <Box
        sx={{
          position: 'absolute',
          top: 20,
          left: 20,
          zIndex: 10
        }}
      >
        <Paper
          sx={{
            px: 2,
            py: 1,
            backgroundColor: alpha(theme.palette.background.paper, 0.8),
            display: 'flex',
            alignItems: 'center',
            gap: 1
          }}
        >
          <Typography variant="caption" sx={{ mr: 1 }}>
            {characters[currentCharacter]?.name}
          </Typography>
          {[...Array(5)].map((_, i) => (
            <IconButton key={i} size="small" disabled>
              {i < relationshipLevel ? 
                <Favorite sx={{ fontSize: 16, color: 'error.main' }} /> : 
                <FavoriteBorder sx={{ fontSize: 16 }} />
              }
            </IconButton>
          ))}
        </Paper>
      </Box>

      {/* ë©”ì¸ ìºë¦­í„° ì´ë¯¸ì§€ */}
      <AnimatePresence mode="wait">
        <motion.div
          key={`${currentCharacter}-${characterEmotion}`}
          initial={{ opacity: 0, scale: 0.9, y: 50 }}
          animate={{ opacity: 1, scale: 1, y: 0 }}
          exit={{ opacity: 0, scale: 0.9, y: 50 }}
          transition={{ duration: 0.5 }}
          style={{
            position: 'absolute',
            bottom: '35%',
            left: '50%',
            transform: 'translateX(-50%)',
            zIndex: 3
          }}
        >
          <Box
            component="img"
            src={getCharacterImage(currentCharacter, characterEmotion)}
            alt={characters[currentCharacter]?.name}
            sx={{
              height: { xs: '300px', sm: '400px', md: '500px' },
              width: 'auto',
              filter: 'drop-shadow(0 10px 30px rgba(0,0,0,0.5))',
              animation: 'float 3s ease-in-out infinite'
            }}
          />
        </motion.div>
      </AnimatePresence>

      {/* ëŒ€í™”ì°½ (í•˜ë‹¨) */}
      <Box
        sx={{
          position: 'absolute',
          bottom: 0,
          left: 0,
          right: 0,
          height: '35%',
          zIndex: 5
        }}
      >
        <Paper
          sx={{
            height: '100%',
            backgroundColor: alpha(theme.palette.background.paper, 0.95),
            borderTop: `2px solid ${theme.palette.primary.main}`,
            borderRadius: '20px 20px 0 0',
            p: 3,
            display: 'flex',
            flexDirection: 'column'
          }}
        >
          {/* ìºë¦­í„° ì´ë¦„ */}
          {dialogue.length > 0 && !dialogue[0].isUser && (
            <Typography
              variant="h6"
              sx={{
                color: theme.palette.primary.main,
                mb: 1,
                fontWeight: 'bold'
              }}
            >
              {dialogue[0].character}
            </Typography>
          )}

          {/* ëŒ€í™” í…ìŠ¤íŠ¸ */}
          <Box sx={{ flex: 1, overflowY: 'auto' }}>
            <AnimatePresence mode="wait">
              {dialogue.map((msg, index) => (
                !msg.isUser && (
                  <motion.div
                    key={msg.id}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -20 }}
                    transition={{ duration: 0.3 }}
                  >
                    <Typography
                      variant="body1"
                      sx={{
                        fontSize: '1.1rem',
                        lineHeight: 1.8,
                        color: theme.palette.text.primary
                      }}
                    >
                      {isTyping && index === dialogue.length - 1 ? (
                        <TypewriterText text={msg.text} />
                      ) : (
                        msg.text
                      )}
                    </Typography>
                  </motion.div>
                )
              ))}
            </AnimatePresence>

            {isTyping && dialogue.length === 0 && (
              <Box display="flex" gap={1}>
                <motion.div
                  animate={{ opacity: [0.5, 1, 0.5] }}
                  transition={{ duration: 1.5, repeat: Infinity }}
                >
                  <Typography variant="h4">...</Typography>
                </motion.div>
              </Box>
            )}
          </Box>

          {/* ì…ë ¥ ì˜ì—­ */}
          <Box
            sx={{
              display: 'flex',
              gap: 2,
              mt: 2,
              pt: 2,
              borderTop: `1px solid ${alpha(theme.palette.divider, 0.3)}`
            }}
          >
            <TextField
              ref={textFieldRef}
              fullWidth
              placeholder="Type your response... (Press Enter to send)"
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
              onKeyPress={(e) => {
                if (e.key === 'Enter' && !e.shiftKey) {
                  e.preventDefault()
                  sendMessage()
                }
              }}
              disabled={isTyping}
              variant="outlined"
              sx={{
                '& .MuiOutlinedInput-root': {
                  backgroundColor: alpha(theme.palette.background.default, 0.5)
                }
              }}
            />
            <Button
              variant="contained"
              onClick={sendMessage}
              disabled={!inputText.trim() || isTyping}
              endIcon={<SendIcon />}
              sx={{ minWidth: 120 }}
            >
              Send
            </Button>

            {/* ìë™ ì§„í–‰ ë²„íŠ¼ */}
            <Button
              variant={autoMode ? "contained" : "outlined"}
              onClick={() => setAutoMode(!autoMode)}
              sx={{ minWidth: 100 }}
            >
              {autoMode ? 'Auto ON' : 'Auto OFF'}
            </Button>
          </Box>

          {/* ì»¨íŠ¸ë¡¤ íŒíŠ¸ */}
          <Typography
            variant="caption"
            sx={{
              mt: 1,
              color: theme.palette.text.secondary,
              textAlign: 'center'
            }}
          >
            Press SPACE to continue â€¢ Press ENTER to send message
          </Typography>
        </Paper>
      </Box>

      {/* ì‹œë‚˜ë¦¬ì˜¤ ì„ íƒ ë©”ë‰´ */}
      <Menu
        anchorEl={settingsAnchor}
        open={showSettings}
        onClose={() => {
          setShowSettings(false)
          setSettingsAnchor(null)
        }}
      >
        <MenuItem disabled>
          <Typography variant="subtitle2">Change Scene</Typography>
        </MenuItem>
        {Object.entries(scenarios).map(([id, scenario]) => (
          <MenuItem
            key={id}
            onClick={() => {
              setCurrentScenario(id)
              setShowSettings(false)
              setSettingsAnchor(null)
            }}
            selected={currentScenario === id}
          >
            {scenario.name}
          </MenuItem>
        ))}
        <MenuItem disabled>
          <Typography variant="subtitle2">Change Character</Typography>
        </MenuItem>
        {Object.entries(characters).map(([id, character]) => (
          <MenuItem
            key={id}
            onClick={() => {
              setCurrentCharacter(id)
              setShowSettings(false)
              setSettingsAnchor(null)
              setDialogue([])
            }}
            selected={currentCharacter === id}
          >
            {character.name}
          </MenuItem>
        ))}
      </Menu>

      {/* í”Œë¡œíŒ… ì• ë‹ˆë©”ì´ì…˜ CSS */}
      <style>
        {`
          @keyframes float {
            0% { transform: translateX(-50%) translateY(0px); }
            50% { transform: translateX(-50%) translateY(-10px); }
            100% { transform: translateX(-50%) translateY(0px); }
          }
        `}
      </style>
    </Box>
  )
}

// íƒ€ì´í•‘ ì• ë‹ˆë©”ì´ì…˜ ì»´í¬ë„ŒíŠ¸
const TypewriterText: React.FC<{ text: string }> = ({ text }) => {
  const [displayText, setDisplayText] = useState('')
  const [currentIndex, setCurrentIndex] = useState(0)

  useEffect(() => {
    if (currentIndex < text.length) {
      const timer = setTimeout(() => {
        setDisplayText(prev => prev + text[currentIndex])
        setCurrentIndex(prev => prev + 1)
      }, 30)
      return () => clearTimeout(timer)
    }
  }, [currentIndex, text])

  return <>{displayText}</>
}

export default VisualNovelStoryMode

