import React, { useEffect, useState } from 'react'
import { Box } from '@mui/material'
import { motion, AnimatePresence } from 'framer-motion'

interface CharacterIllustrationProps {
  characterId: string
  emotion: string
  isTyping?: boolean
}

const CharacterIllustration: React.FC<CharacterIllustrationProps> = ({
  characterId,
  emotion,
  isTyping = false
}) => {
  const [gesture, setGesture] = useState('idle')

  // ìºë¦­í„°ë³„ SVG ì¼ëŸ¬ìŠ¤íŠ¸ë ˆì´ì…˜ ìƒì„±
  const getCharacterSVG = () => {
    // ì‹¤ì œë¡œëŠ” ê° ìºë¦­í„°/ê°ì •ë³„ SVG íŒŒì¼ì„ ì‚¬ìš©í•˜ê±°ë‚˜
    // React ì»´í¬ë„ŒíŠ¸ë¡œ ì§ì ‘ ê·¸ë ¤ì•¼ í•©ë‹ˆë‹¤
    return (
      <svg
        width="400"
        height="600"
        viewBox="0 0 400 600"
        xmlns="http://www.w3.org/2000/svg"
      >
        {/* ëª¸í†µ */}
        <motion.ellipse
          cx="200"
          cy="400"
          rx="80"
          ry="150"
          fill="#4A90E2"
          initial={{ scaleY: 1 }}
          animate={{ 
            scaleY: isTyping ? [1, 1.02, 1] : 1,
          }}
          transition={{ 
            duration: 2,
            repeat: isTyping ? Infinity : 0
          }}
        />

        {/* íŒ” - ì™¼ìª½ */}
        <motion.path
          d="M 120 350 Q 80 380 70 450"
          stroke="#4A90E2"
          strokeWidth="30"
          fill="none"
          strokeLinecap="round"
          initial={{ rotate: 0 }}
          animate={{ 
            rotate: emotion === 'happy' ? [-5, 5, -5] : 
                   emotion === 'thinking' ? [0, -10, 0] : 0,
            originX: '120px',
            originY: '350px'
          }}
          transition={{ 
            duration: 3,
            repeat: Infinity,
            ease: "easeInOut"
          }}
        />

        {/* íŒ” - ì˜¤ë¥¸ìª½ */}
        <motion.path
          d="M 280 350 Q 320 380 330 450"
          stroke="#4A90E2"
          strokeWidth="30"
          fill="none"
          strokeLinecap="round"
          initial={{ rotate: 0 }}
          animate={{ 
            rotate: emotion === 'happy' ? [5, -5, 5] : 
                   emotion === 'surprised' ? [0, 20, 0] : 
                   isTyping ? [0, -15, 0, -15, 0] : 0,
            originX: '280px',
            originY: '350px'
          }}
          transition={{ 
            duration: isTyping ? 1 : 3,
            repeat: Infinity,
