          <Box sx={{ flex: 1, overflowY: 'auto' }}>
            <AnimatePresence mode="wait">
              {dialogue.map((msg, index) => (
                !msg.isUser && (
                  <motion.div
                    key={msg.id}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -20 }}
                    transition={{ duration: 0.3 }}
                  >
                    <Typography
                      variant="body1"
                      sx={{
                        fontSize: '1.1rem',
                        lineHeight: 1.8,
