          message: inputText,
          sessionId: 'visual-novel-session-1'
        })
      })

      if (response.ok) {
        const data = await response.json()

        setTimeout(() => {
          const aiMessage: DialogueMessage = {
            id: (Date.now() + 1).toString(),
            character: characters[currentCharacter]?.name || 'AI',
            text: data.message,
