      }

      setTimeout(() => {
        setDialogue([demoResponse])
        setCharacterEmotion('happy')
        setIsTyping(false)
      }, 1500)
    }
  }

  // ë‹¤ìŒ ëŒ€í™”ë¡œ ì§„í–‰
  const nextDialogue = () => {
    // ìë™ ëª¨ë“œì—ì„œ ë‹¤ìŒ ëŒ€í™” ì§„í–‰ ë¡œì§
    if (autoMode && dialogue.length > 0) {
      setTimeout(() => {
