          <SaveIcon />
        </IconButton>
      </Box>

      {/* ê´€ê³„ë„ ë ˆë²¨ í‘œì‹œ */}
      <Box
        sx={{
          position: 'absolute',
          top: 20,
          left: 20,
          zIndex: 10
        }}
      >
        <Paper
          sx={{
            px: 2,
            py: 1,
            backgroundColor: alpha(theme.palette.background.paper, 0.8),
            display: 'flex',
