    const theme = themes[characterId as keyof typeof themes] || themes.jennifer

    return `https://via.placeholder.com/${sizes.width}x${sizes.height}/${theme.bg}/${theme.text}?text=${characterId.toUpperCase()}`
  }

  return (
    <AnimatePresence mode="wait">
      <motion.div
