              // ì´ë¯¸ì§€ ë¡œë“œ ì‹¤íŒ¨ì‹œ í´ë°±
              e.target.src = getPlaceholderCharacter()
            }}
          />
        </Box>

        {/* ê°ì • í‘œí˜„ ì´í™íŠ¸ */}
        {emotion !== 'normal' && (
          <Box
