              exit={{ scale: 0, rotate: 180 }}
              transition={{ type: 'spring', stiffness: 200 }}
            >
              {emotion === 'happy' && 'âœ¨'}
              {emotion === 'sad' && 'ğŸ’§'}
              {emotion === 'surprised' && 'â—'}
              {emotion === 'angry' && 'ğŸ’¢'}
