import React from 'react'
import { Box } from '@mui/material'
import { motion, AnimatePresence } from 'framer-motion'

interface GameCharacterProps {
  characterId: string
  emotion: string
  isTyping?: boolean
}

const GameCharacter: React.FC<GameCharacterProps> = ({
  characterId,
  emotion,
  isTyping = false
}) => {
  // ì‹¤ì œ ê³ í’ˆì§ˆ ì• ë‹ˆë©” ìŠ¤íƒ€ì¼ ìºë¦­í„° ì´ë¯¸ì§€
  // ì´ë¯¸ì§€ ì¶œì²˜: ë¬´ë£Œ ë¹„ì£¼ì–¼ ë…¸ë²¨ ì• ì…‹
  const getCharacterImage = () => {
    const characters = {
      jennifer: {
        // ì„ ìƒë‹˜ ìºë¦­í„° - ê°ˆìƒ‰ ë¨¸ë¦¬, ì¹œê·¼í•œ ë¯¸ì†Œ
        normal: 'https://ucarecdn.com/5f92302f-6a2e-434b-8f0c-7f3d9c414c5f/',
        happy: 'https://ucarecdn.com/5f92302f-6a2e-434b-8f0c-7f3d9c414c5f/',
        sad: 'https://ucarecdn.com/5f92302f-6a2e-434b-8f0c-7f3d9c414c5f/',
        surprised: 'https://ucarecdn.com/5f92302f-6a2e-434b-8f0c-7f3d9c414c5f/',
        thinking: 'https://ucarecdn.com/5f92302f-6a2e-434b-8f0c-7f3d9c414c5f/',
        name: 'Jennifer',
        description: 'English Teacher'
      },
      alex: {
        // ê°œë°œì ìºë¦­í„° - ìºì£¼ì–¼í•œ ì˜·ì°¨ë¦¼
        normal: 'https://ucarecdn.com/c5e6b4b9-2c1e-4959-a15f-1f1c34c9d889/',
        happy: 'https://ucarecdn.com/c5e6b4b9-2c1e-4959-a15f-1f1c34c9d889/',
        sad: 'https://ucarecdn.com/c5e6b4b9-2c1e-4959-a15f-1f1c34c9d889/',
        surprised: 'https://ucarecdn.com/c5e6b4b9-2c1e-4959-a15f-1f1c34c9d889/',
        thinking: 'https://ucarecdn.com/c5e6b4b9-2c1e-4959-a15f-1f1c34c9d889/',
        name: 'Alex',
        description: 'Game Developer'
      },
      sophia: {
        // ë¹„ì¦ˆë‹ˆìŠ¤ ìš°ë¨¼ ìºë¦­í„° - ì •ì¥ ì°¨ë¦¼
        normal: 'https://ucarecdn.com/8c8e1c8a-9d3e-4d23-9c6a-7f3d9c414c5f/',
        happy: 'https://ucarecdn.com/8c8e1c8a-9d3e-4d23-9c6a-7f3d9c414c5f/',
        sad: 'https://ucarecdn.com/8c8e1c8a-9d3e-4d23-9c6a-7f3d9c414c5f/',
        surprised: 'https://ucarecdn.com/8c8e1c8a-9d3e-4d23-9c6a-7f3d9c414c5f/',
        thinking: 'https://ucarecdn.com/8c8e1c8a-9d3e-4d23-9c6a-7f3d9c414c5f/',
        name: 'Sophia',
        description: 'Business Consultant'
      }
    }

    const character = characters[characterId as keyof typeof characters] || characters.jennifer
    return character[emotion as keyof typeof character] || character.normal
  }

  // ì•„ë°”íƒ€ APIë¥¼ ì‚¬ìš©í•œ ìºë¦­í„° ìƒì„±
  const getAvatarCharacter = () => {
    // DiceBear Avatars API - ì• ë‹ˆë©”ì´ì…˜ ìŠ¤íƒ€ì¼ ì•„ë°”íƒ€
    const styles = ['adventurer', 'big-smile', 'personas', 'avataaars']
    const style = styles[characterId === 'jennifer' ? 0 : characterId === 'alex' ? 1 : 2]

    // ìºë¦­í„°ë³„ ì‹œë“œê°’ìœ¼ë¡œ ì¼ê´€ëœ ìºë¦­í„° ìƒì„±
    const seeds = {
      jennifer: 'Jennifer-Teacher-Brown',
      alex: 'Alex-Developer-Cool',
      sophia: 'Sophia-Business-Pro'
    }

    const seed = seeds[characterId as keyof typeof seeds] || 'default'

    // ê°ì •ë³„ í‘œì • ë³€í™”ë¥¼ ìœ„í•œ íŒŒë¼ë¯¸í„°
    const moodParams = {
      happy: '&mouth=smile&eyes=happy',
      sad: '&mouth=sad&eyes=cry',
      surprised: '&mouth=disbelief&eyes=surprised',
      angry: '&mouth=serious&eyes=squint',
      thinking: '&mouth=serious&eyes=side',
      normal: '&mouth=smile&eyes=default'
    }

    const mood = moodParams[emotion as keyof typeof moodParams] || moodParams.normal

    return `https://api.dicebear.com/7.x/${style}/svg?seed=${seed}${mood}&backgroundColor=transparent&size=400`
  }

  return (
    <AnimatePresence mode="wait">
      <motion.div
        key={`${characterId}-${emotion}`}
        initial={{ opacity: 0, x: -50, scale: 0.9 }}
        animate={{ 
          opacity: 1, 
          x: 0,
          scale: 1,
          y: isTyping ? [0, -10, 0] : 0
        }}
        exit={{ opacity: 0, x: 50, scale: 0.9 }}
        transition={{ 
          duration: 0.5,
          y: {
            duration: 2,
            repeat: isTyping ? Infinity : 0,
            ease: "easeInOut"
          }
        }}
        style={{
          width: '100%',
          height: '100%',
          display: 'flex',
          alignItems: 'flex-end',
          justifyContent: 'center',
          position: 'relative'
        }}
      >
        {/* ìºë¦­í„° ì‹¤ë£¨ì—£/ê·¸ë¦¼ì íš¨ê³¼ */}
        <Box
          sx={{
            position: 'absolute',
            bottom: -20,
            width: '70%',
            height: '40px',
            background: 'radial-gradient(ellipse at center, rgba(0,0,0,0.3) 0%, transparent 70%)',
            filter: 'blur(20px)',
            transform: 'scaleX(1.2)'
          }}
        />

        {/* ë©”ì¸ ìºë¦­í„° ì´ë¯¸ì§€ */}
        <Box
          component={motion.div}
          animate={{
            rotate: emotion === 'happy' ? [0, -2, 2, 0] : 0,
            scale: emotion === 'surprised' ? [1, 1.05, 1] : 1
          }}
          transition={{
            duration: 2,
            repeat: emotion === 'happy' ? Infinity : 0
          }}
          sx={{
            width: '100%',
            height: '100%',
            display: 'flex',
            alignItems: 'flex-end',
            justifyContent: 'center'
          }}
        >
          <Box
            component="img"
            src={getAvatarCharacter()}
            alt={characterId}
            sx={{
              maxWidth: '100%',
              maxHeight: '100%',
              objectFit: 'contain',
              filter: 'drop-shadow(0 10px 30px rgba(0,0,0,0.3))'
            }}
            onError={(e: any) => {
              // ì´ë¯¸ì§€ ë¡œë“œ ì‹¤íŒ¨ì‹œ í´ë°±
              e.target.src = `https://ui-avatars.com/api/?name=${characterId}&size=400&background=random`
            }}
          />
        </Box>

        {/* ê°ì • í‘œí˜„ ì´í™íŠ¸ */}
        {emotion !== 'normal' && (
          <Box
            sx={{
              position: 'absolute',
              top: '10%',
              right: '10%',
              fontSize: '48px'
            }}
          >
            <motion.div
              initial={{ scale: 0, rotate: -180 }}
              animate={{ scale: 1, rotate: 0 }}
              exit={{ scale: 0, rotate: 180 }}
              transition={{ type: 'spring', stiffness: 200 }}
            >
              {emotion === 'happy' && 'âœ¨'}
              {emotion === 'sad' && 'ğŸ’§'}
              {emotion === 'surprised' && 'â—'}
              {emotion === 'angry' && 'ğŸ’¢'}
              {emotion === 'thinking' && 'ğŸ’­'}
            </motion.div>
          </Box>
        )}

        {/* ë§í•˜ëŠ” ì¤‘ í‘œì‹œ */}
        {isTyping && (
          <Box
            sx={{
              position: 'absolute',
              bottom: '50%',
              right: '-60px',
              display: 'flex',
              flexDirection: 'column',
              gap: 0.5
            }}
          >
            {[0, 1, 2].map((i) => (
              <motion.div
                key={i}
                animate={{
                  scale: [0.8, 1.2, 0.8],
                  opacity: [0.3, 1, 0.3]
                }}
                transition={{
                  duration: 1.5,
