        e.preventDefault()
        if (inputText.trim()) {
          sendMessage()
        }
      }
      if (e.key === ' ' && dialogue.length > 0) {
        e.preventDefault()
        nextDialogue()
      }
    }

    window.addEventListener('keydown', handleKeyPress)
    return () => window.removeEventListener('keydown', handleKeyPress)
  }, [inputText, dialogue])

