            p: 3,
            display: 'flex',
            flexDirection: 'column'
          }}
        >
          {/* ìºë¦­í„° ì´ë¦„ */}
          {dialogue.length > 0 && !dialogue[0].isUser && (
            <Typography
              variant="h6"
              sx={{
                color: theme.palette.primary.main,
                mb: 1,
                fontWeight: 'bold'
              }}
            >
              {dialogue[0].character}
            </Typography>
          )}

          {/* ëŒ€í™” í…ìŠ¤íŠ¸ */}
          <Box sx={{ flex: 1, overflowY: 'auto' }}>
            <AnimatePresence mode="wait">
              {dialogue.map((msg, index) => (
                !msg.isUser && (
                  <motion.div
                    key={msg.id}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -20 }}
                    transition={{ duration: 0.3 }}
                  >
                    <Typography
                      variant="body1"
                      sx={{
                        fontSize: '1.1rem',
                        lineHeight: 1.8,
                        color: theme.palette.text.primary
                      }}
                    >
                      {isTyping && index === dialogue.length - 1 ? (
                        <TypewriterText text={msg.text} />
                      ) : (
                        msg.text
                      )}
                    </Typography>
                  </motion.div>
                )
              ))}
            </AnimatePresence>

            {isTyping && dialogue.length === 0 && (
              <Box display="flex" gap={1}>
                <motion.div
                  animate={{ opacity: [0.5, 1, 0.5] }}
                  transition={{ duration: 1.5, repeat: Infinity }}
                >
                  <Typography variant="h4">...</Typography>
                </motion.div>
              </Box>
            )}
          </Box>

          {/* ì…ë ¥ ì˜ì—­ */}
          <Box
            sx={{
              display: 'flex',
              gap: 2,
              mt: 2,
              pt: 2,
              borderTop: `1px solid ${alpha(theme.palette.divider, 0.3)}`
            }}
          >
            <TextField
              ref={textFieldRef}
              fullWidth
              placeholder="Type your response... (Press Enter to send)"
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
              onKeyPress={(e) => {
                if (e.key === 'Enter' && !e.shiftKey) {
                  e.preventDefault()
                  sendMessage()
                }
              }}
              disabled={isTyping}
              variant="outlined"
              sx={{
                '& .MuiOutlinedInput-root': {
                  backgroundColor: alpha(theme.palette.background.default, 0.5)
                }
              }}
            />
            <Button
              variant="contained"
              onClick={sendMessage}
              disabled={!inputText.trim() || isTyping}
              endIcon={<SendIcon />}
              sx={{ minWidth: 120 }}
            >
              Send
            </Button>

            {/* ìë™ ì§„í–‰ ë²„íŠ¼ */}
            <Button
              variant={autoMode ? "contained" : "outlined"}
              onClick={() => setAutoMode(!autoMode)}
              sx={{ minWidth: 100 }}
            >
              {autoMode ? 'Auto ON' : 'Auto OFF'}
            </Button>
          </Box>

          {/* ì»¨íŠ¸ë¡¤ íŒíŠ¸ */}
          <Typography
            variant="caption"
            sx={{
              mt: 1,
              color: theme.palette.text.secondary,
              textAlign: 'center'
            }}
          >
            Press SPACE to continue â€¢ Press ENTER to send message
          </Typography>
        </Paper>
      </Box>

      {/* ì‹œë‚˜ë¦¬ì˜¤ ì„ íƒ ë©”ë‰´ */}
      <Menu
        anchorEl={settingsAnchor}
        open={showSettings}
        onClose={() => {
          setShowSettings(false)
          setSettingsAnchor(null)
        }}
      >
        <MenuItem disabled>
          <Typography variant="subtitle2">Change Scene</Typography>
        </MenuItem>
        {Object.entries(scenarios).map(([id, scenario]) => (
          <MenuItem
            key={id}
            onClick={() => {
              setCurrentScenario(id)
              setShowSettings(false)
              setSettingsAnchor(null)
            }}
            selected={currentScenario === id}
          >
            {scenario.name}
          </MenuItem>
        ))}
        <MenuItem disabled>
          <Typography variant="subtitle2">Change Character</Typography>
        </MenuItem>
        {Object.entries(characters).map(([id, character]) => (
          <MenuItem
            key={id}
            onClick={() => {
              setCurrentCharacter(id)
              setShowSettings(false)
              setSettingsAnchor(null)
              setDialogue([])
            }}
            selected={currentCharacter === id}
          >
            {character.name}
          </MenuItem>
        ))}
      </Menu>

      {/* í”Œë¡œíŒ… ì• ë‹ˆë©”ì´ì…˜ CSS */}
      <style>
        {`
          @keyframes float {
            0% { transform: translateX(-50%) translateY(0px); }
            50% { transform: translateX(-50%) translateY(-10px); }
            100% { transform: translateX(-50%) translateY(0px); }
          }
        `}
      </style>
    </Box>
  )
}

// íƒ€ì´í•‘ ì• ë‹ˆë©”ì´ì…˜ ì»´í¬ë„ŒíŠ¸
const TypewriterText: React.FC<{ text: string }> = ({ text }) => {
  const [displayText, setDisplayText] = useState('')
  const [currentIndex, setCurrentIndex] = useState(0)

  useEffect(() => {
    if (currentIndex < text.length) {
      const timer = setTimeout(() => {
        setDisplayText(prev => prev + text[currentIndex])
        setCurrentIndex(prev => prev + 1)
      }, 30)
      return () => clearTimeout(timer)
    }
  }, [currentIndex, text])

  return <>{displayText}</>
}

export default VisualNovelStoryMode

