// AI 자격증 학습 플랫폼 메인 애플리케이션

class StudyPlatform {
    constructor() {
        this.currentUser = null;
        this.currentSubject = null;
        this.questions = [];
        this.currentQuiz = null;
        this.statistics = {};
        this.settings = {
            questionCount: 20,
            timeLimit: 30,
            theme: 'light'
        };

        this.init();
    }

    async init() {
        console.log('AI 학습 플랫폼 초기화 중...');

        // 로컬 스토리지에서 데이터 로드
        this.loadSettings();
        this.loadStatistics();

        // 이벤트 리스너 설정
        this.setupEventListeners();

        // 테마 적용
        this.applyTheme();

        // 로딩 화면 제거
        setTimeout(() => {
            document.getElementById('loading').classList.add('hidden');
        }, 1500);

        console.log('초기화 완료!');
    }

    setupEventListeners() {
        // 네비게이션 버튼들
        document.querySelectorAll('.nav-btn').forEach(btn => {
            btn.addEventListener('click', (e) => {
                const screen = e.currentTarget.dataset.screen;
                this.showScreen(screen);
            });
        });

        // 테마 토글
        document.getElementById('themeToggle').addEventListener('click', () => {
            this.toggleTheme();
        });

        // 설정 버튼
        document.getElementById('settingsBtn').addEventListener('click', () => {
            this.showModal('settingsModal');
        });

        // 과목 선택
        document.getElementById('subjectSelect').addEventListener('change', (e) => {
            this.currentSubject = e.target.value;
            this.updateDashboard();
        });

        // 파일 업로드
        this.setupFileUpload();

        // 빠른 시작 버튼들
        this.setupQuickActions();

        // 퀴즈 관련
        this.setupQuizControls();

        // 모달 닫기
        document.querySelectorAll('.modal-close').forEach(btn => {
            btn.addEventListener('click', (e) => {
                const modal = e.target.closest('.modal');
                this.closeModal(modal.id);
            });
        });

        // 모달 배경 클릭으로 닫기
        document.querySelectorAll('.modal').forEach(modal => {
            modal.addEventListener('click', (e) => {
                if (e.target === modal) {
                    this.closeModal(modal.id);
                }
            });
        });
    }

    setupFileUpload() {
        const uploadZone = document.getElementById('uploadZone');
        const fileInput = document.getElementById('fileInput');
        const uploadBtn = document.getElementById('uploadBtn');

        // 드래그 앤 드롭
        uploadZone.addEventListener('dragover', (e) => {
            e.preventDefault();
            uploadZone.classList.add('dragover');
        });

        uploadZone.addEventListener('dragleave', () => {
            uploadZone.classList.remove('dragover');
        });

        uploadZone.addEventListener('drop', (e) => {
            e.preventDefault();
            uploadZone.classList.remove('dragover');
            const files = Array.from(e.dataTransfer.files);
            this.handleFiles(files);
        });

        // 클릭 업로드
        uploadBtn.addEventListener('click', () => {
            fileInput.click();
        });

        fileInput.addEventListener('change', (e) => {
            const files = Array.from(e.target.files);
            console.log('파일 선택됨:', files);
            this.handleFiles(files);
        });
    }

    setupQuickActions() {
        document.getElementById('randomQuizBtn').addEventListener('click', () => {
            this.startQuiz('random');
        });

        document.getElementById('weakQuizBtn').addEventListener('click', () => {
            this.startQuiz('weak');
        });

        document.getElementById('timeAttackBtn').addEventListener('click', () => {
            this.startQuiz('timed');
        });

        document.getElementById('reviewBtn').addEventListener('click', () => {
            this.showScreen('wrong-note');
        });
    }

    setupQuizControls() {
        document.getElementById('exitQuizBtn').addEventListener('click', () => {
            if (confirm('퀴즈를 종료하시겠습니까?')) {
                this.exitQuiz();
            }
        });

        document.getElementById('nextQuestionBtn').addEventListener('click', () => {
            this.nextQuestion();
        });

        document.getElementById('prevQuestionBtn').addEventListener('click', () => {
            this.prevQuestion();
        });

        document.getElementById('submitQuizBtn').addEventListener('click', () => {
            this.submitQuiz();
        });
    }

    showScreen(screenName) {
        // 모든 화면 숨기기
        document.querySelectorAll('.screen').forEach(screen => {
            screen.classList.remove('active');
        });

        // 선택된 화면 보이기
        document.getElementById(screenName).classList.add('active');

        // 네비게이션 버튼 상태 업데이트
        document.querySelectorAll('.nav-btn').forEach(btn => {
            btn.classList.remove('active');
        });

        document.querySelector(`[data-screen="${screenName}"]`).classList.add('active');

        // 화면별 초기화
        switch(screenName) {
            case 'dashboard':
                this.updateDashboard();
                break;
            case 'analytics':
                this.updateAnalytics();
                break;
            case 'wrong-note':
                this.updateWrongNote();
                break;
        }
    }

    updateDashboard() {
        const stats = this.getStatistics();

        document.getElementById('totalQuestions').textContent = stats.totalQuestions;
        document.getElementById('correctRate').textContent = stats.correctRate + '%';
        document.getElementById('weakProblems').textContent = stats.weakProblems;
        document.getElementById('studyTime').textContent = stats.studyTime + '분';
    }

    async handleFiles(files) {
        console.log('handleFiles 호출됨:', files.length, '개 파일');
        const fileList = document.getElementById('fileList');

        if (files.length === 0) {
            console.log('선택된 파일이 없습니다.');
            return;
        }

        for (const file of files) {
            console.log('파일 처리 중:', file.name, file.type, file.size);

            if (this.isValidFile(file)) {
                try {
                    console.log('유효한 파일입니다. 처리 시작...');

                    const fileItem = this.createFileItem(file);
                    fileList.appendChild(fileItem);

                    // 파일 내용 파싱
                    console.log('파일 파싱 시작...');
                    const content = await this.parseFile(file);
                    console.log('파싱 완료, 내용 길이:', content.length);

                    const questions = await this.generateQuestions(content);
                    console.log('문제 생성 완료:', questions.length, '개');

                    this.questions.push(...questions);
                    this.saveQuestions();
                    this.updateDashboard();

                    this.showNotification(`파일이 성공적으로 업로드되었습니다! (${questions.length}개 문제 생성)`, 'success');
                } catch (error) {
                    console.error('파일 처리 오류:', error);
                    this.showNotification('파일 처리 중 오류가 발생했습니다: ' + error.message, 'error');
                }
            } else {
                console.log('지원하지 않는 파일:', file.name, file.type);
                this.showNotification(`지원하지 않는 파일 형식입니다: ${file.name}`, 'warning');
            }
        }
    }

    isValidFile(file) {
        const validTypes = [
            'application/pdf',
            'text/plain',
            'application/vnd.openxmlformats-officedocument.wordprocessingml.document'
        ];

        // 파일 확장자도 체크
        const validExtensions = ['.pdf', '.txt', '.docx'];
        const fileExtension = file.name.toLowerCase().substring(file.name.lastIndexOf('.'));

        console.log('파일 검증:', {
            name: file.name,
            type: file.type,
            extension: fileExtension,
            size: file.size
        });

        const isValidType = validTypes.includes(file.type) || validExtensions.includes(fileExtension);
        const isValidSize = file.size <= 10 * 1024 * 1024; // 10MB 제한

        console.log('검증 결과:', { isValidType, isValidSize });

        return isValidType && isValidSize;
    }

    createFileItem(file) {
        const div = document.createElement('div');
        div.className = 'file-item';

        div.innerHTML = `
            <div class="file-info">
                <div class="file-icon">
                    <i class="fas fa-file-${this.getFileIconType(file.type)}"></i>
                </div>
                <div>
                    <div class="file-name">${file.name}</div>
                    <div class="file-size">${this.formatFileSize(file.size)}</div>
                </div>
            </div>
            <div class="file-actions">
                <button class="btn-secondary btn-small" onclick="app.removeFile(this)">
                    <i class="fas fa-trash"></i>
                </button>
            </div>
        `;

        return div;
    }

    getFileIconType(mimeType) {
        if (mimeType.includes('pdf')) return 'pdf';
        if (mimeType.includes('text')) return 'text';
        if (mimeType.includes('word')) return 'word';
        return 'alt';
    }

    formatFileSize(bytes) {
        if (bytes === 0) return '0 Bytes';
        const k = 1024;
        const sizes = ['Bytes', 'KB', 'MB', 'GB'];
        const i = Math.floor(Math.log(bytes) / Math.log(k));
        return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
    }

    async parseFile(file) {
        console.log('파일 파싱 시작:', file.name, file.type);

        return new Promise((resolve, reject) => {
            const reader = new FileReader();

            reader.onload = async (e) => {
                try {
                    const content = e.target.result;
                    console.log('파일 읽기 완료, 크기:', content.length || content.byteLength);

                    if (file.type === 'application/pdf' || file.name.toLowerCase().endsWith('.pdf')) {
                        console.log('PDF 파일 파싱 시작');
                        const text = await this.parsePDF(content);
                        resolve(text);
                    } else {
                        // 텍스트 파일
                        console.log('텍스트 파일 파싱 완료');
                        resolve(content);
                    }
                } catch (error) {
                    console.error('파일 파싱 오류:', error);
                    reject(error);
                }
            };

            reader.onerror = (error) => {
                console.error('파일 읽기 오류:', error);
                reject(error);
            };

            // 파일 타입에 따라 읽기 방식 결정
            if (file.type === 'application/pdf' || file.name.toLowerCase().endsWith('.pdf')) {
                reader.readAsArrayBuffer(file);
            } else {
                reader.readAsText(file, 'UTF-8');
            }
        });
    }

    async parsePDF(arrayBuffer) {
        // PDF.js를 사용한 PDF 파싱
        try {
            const pdf = await pdfjsLib.getDocument(arrayBuffer).promise;
            let text = '';

            for (let i = 1; i <= pdf.numPages; i++) {
                const page = await pdf.getPage(i);
                const textContent = await page.getTextContent();
                const pageText = textContent.items.map(item => item.str).join(' ');
                text += pageText + '\n';
            }

            return text;
        } catch (error) {
            throw new Error('PDF 파싱 실패: ' + error.message);
        }
    }

    async generateQuestions(content) {
        // 간단한 문제 생성 로직 (실제로는 더 복잡한 AI 모델 필요)
        const sentences = content.split(/[.!?]+/).filter(s => s.trim().length > 20);
        const questions = [];

        // 샘플 문제들 생성
        for (let i = 0; i < Math.min(sentences.length, 10); i++) {
            const sentence = sentences[i].trim();
            if (sentence.length < 50) continue;

            const question = {
                id: Date.now() + i,
                text: `다음 문장에서 올바른 설명은 무엇입니까?\n\n"${sentence}"`,
                options: [
                    '위 내용이 정확하다',
                    '위 내용에 오류가 있다',
                    '위 내용은 불완전하다',
                    '위 내용을 판단할 수 없다'
                ],
                correctAnswer: 0,
                subject: this.currentSubject || '기타',
                difficulty: 'medium',
                category: '이론',
                createdAt: new Date().toISOString()
            };

            questions.push(question);
        }

        return questions;
    }

    startQuiz(type = 'random') {
        if (this.questions.length === 0) {
            this.showNotification('먼저 학습 자료를 업로드해주세요.', 'warning');
            return;
        }

        let quizQuestions = [];
        const questionCount = this.settings.questionCount;

        switch(type) {
            case 'random':
                quizQuestions = this.getRandomQuestions(questionCount);
                break;
            case 'weak':
                quizQuestions = this.getWeakQuestions(questionCount);
                break;
            case 'timed':
                quizQuestions = this.getRandomQuestions(questionCount);
                break;
        }

        this.currentQuiz = {
            questions: quizQuestions,
            currentIndex: 0,
            answers: new Array(quizQuestions.length).fill(null),
            startTime: Date.now(),
            type: type,
            timeLimit: type === 'timed' ? this.settings.timeLimit * 60 : null
        };

        this.showScreen('quiz');
        this.renderQuestion();
        this.startTimer();
    }

    getRandomQuestions(count) {
        const shuffled = [...this.questions].sort(() => 0.5 - Math.random());
        return shuffled.slice(0, Math.min(count, shuffled.length));
    }

    getWeakQuestions(count) {
        // 틀린 문제들을 우선적으로 선택
        const stats = this.getQuestionStatistics();
        const weakQuestions = this.questions
            .filter(q => stats[q.id] && stats[q.id].correctRate < 50)
            .sort((a, b) => (stats[a.id]?.correctRate || 0) - (stats[b.id]?.correctRate || 0));

        if (weakQuestions.length >= count) {
            return weakQuestions.slice(0, count);
        } else {
            const additional = this.getRandomQuestions(count - weakQuestions.length);
            return [...weakQuestions, ...additional];
        }
    }

    renderQuestion() {
        if (!this.currentQuiz) return;

        const quiz = this.currentQuiz;
        const question = quiz.questions[quiz.currentIndex];

        document.getElementById('currentQuestion').textContent = quiz.currentIndex + 1;
        document.getElementById('totalQuizQuestions').textContent = quiz.questions.length;
        document.getElementById('questionText').textContent = question.text;

        // 진행률 업데이트
        const progress = ((quiz.currentIndex + 1) / quiz.questions.length) * 100;
        document.getElementById('quizProgress').style.width = progress + '%';

        // 선택지 렌더링
        const optionsList = document.getElementById('optionsList');
        optionsList.innerHTML = '';

        question.options.forEach((option, index) => {
            const div = document.createElement('div');
            div.className = 'option-item';
            div.textContent = `${index + 1}. ${option}`;
            div.addEventListener('click', () => this.selectOption(index));

            if (quiz.answers[quiz.currentIndex] === index) {
                div.classList.add('selected');
            }

            optionsList.appendChild(div);
        });

        // 버튼 상태 업데이트
        document.getElementById('prevQuestionBtn').disabled = quiz.currentIndex === 0;

        const isLastQuestion = quiz.currentIndex === quiz.questions.length - 1;
        document.getElementById('nextQuestionBtn').style.display = isLastQuestion ? 'none' : 'inline-flex';
        document.getElementById('submitQuizBtn').style.display = isLastQuestion ? 'inline-flex' : 'none';
    }

    selectOption(index) {
        if (!this.currentQuiz) return;

        this.currentQuiz.answers[this.currentQuiz.currentIndex] = index;

        // 선택지 스타일 업데이트
        document.querySelectorAll('.option-item').forEach((item, i) => {
            item.classList.toggle('selected', i === index);
        });
    }

    nextQuestion() {
        if (!this.currentQuiz) return;

        if (this.currentQuiz.currentIndex < this.currentQuiz.questions.length - 1) {
            this.currentQuiz.currentIndex++;
            this.renderQuestion();
        }
    }

    prevQuestion() {
        if (!this.currentQuiz) return;

        if (this.currentQuiz.currentIndex > 0) {
            this.currentQuiz.currentIndex--;
            this.renderQuestion();
        }
    }

    startTimer() {
        if (!this.currentQuiz || !this.currentQuiz.timeLimit) return;

        const timer = document.getElementById('quizTimer');
        let remainingTime = this.currentQuiz.timeLimit;

        this.timerInterval = setInterval(() => {
            const minutes = Math.floor(remainingTime / 60);
            const seconds = remainingTime % 60;
            timer.textContent = `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;

            if (remainingTime <= 0) {
                this.submitQuiz();
                return;
            }

            remainingTime--;
        }, 1000);
    }

    submitQuiz() {
        if (!this.currentQuiz) return;

        clearInterval(this.timerInterval);

        const quiz = this.currentQuiz;
        const results = this.calculateResults(quiz);

        // 통계 업데이트
        this.updateStatistics(results);

        // 결과 표시
        this.showQuizResults(results);

        // 퀴즈 정리
        this.currentQuiz = null;
        this.showScreen('dashboard');
    }

    calculateResults(quiz) {
        let correctCount = 0;
        const detailedResults = [];

        quiz.questions.forEach((question, index) => {
            const userAnswer = quiz.answers[index];
            const isCorrect = userAnswer === question.correctAnswer;

            if (isCorrect) correctCount++;

            detailedResults.push({
                question: question,
                userAnswer: userAnswer,
                correctAnswer: question.correctAnswer,
                isCorrect: isCorrect
            });
        });

        return {
            totalQuestions: quiz.questions.length,
            correctCount: correctCount,
            incorrectCount: quiz.questions.length - correctCount,
            score: Math.round((correctCount / quiz.questions.length) * 100),
            timeSpent: Math.round((Date.now() - quiz.startTime) / 1000),
            detailedResults: detailedResults
        };
    }

    showQuizResults(results) {
        alert(`퀴즈 완료!\n\n정답: ${results.correctCount}/${results.totalQuestions}\n점수: ${results.score}점\n소요 시간: ${Math.floor(results.timeSpent / 60)}분 ${results.timeSpent % 60}초`);
    }

    exitQuiz() {
        clearInterval(this.timerInterval);
        this.currentQuiz = null;
        this.showScreen('dashboard');
    }

    updateAnalytics() {
        // 차트 업데이트 로직
        console.log('분석 화면 업데이트');
    }

    updateWrongNote() {
        const wrongNoteContent = document.getElementById('wrongNoteContent');
        const stats = this.getQuestionStatistics();

        const wrongQuestions = this.questions.filter(q => 
            stats[q.id] && stats[q.id].correctRate < 100
        );

        if (wrongQuestions.length === 0) {
            wrongNoteContent.innerHTML = '<p>틀린 문제가 없습니다. 훌륭해요! 🎉</p>';
            return;
        }

        wrongNoteContent.innerHTML = wrongQuestions.map(q => `
            <div class="wrong-question-item">
                <h4>${q.text}</h4>
                <p>정답률: ${stats[q.id]?.correctRate || 0}%</p>
            </div>
        `).join('');
    }

    toggleTheme() {
        const currentTheme = document.documentElement.getAttribute('data-theme');
        const newTheme = currentTheme === 'dark' ? 'light' : 'dark';

        document.documentElement.setAttribute('data-theme', newTheme);
        this.settings.theme = newTheme;
        this.saveSettings();

        // 아이콘 변경
        const icon = document.querySelector('#themeToggle i');
        icon.className = newTheme === 'dark' ? 'fas fa-sun' : 'fas fa-moon';
    }

    applyTheme() {
        document.documentElement.setAttribute('data-theme', this.settings.theme);
        const icon = document.querySelector('#themeToggle i');
        icon.className = this.settings.theme === 'dark' ? 'fas fa-sun' : 'fas fa-moon';
    }

    showModal(modalId) {
        document.getElementById(modalId).classList.add('active');
    }

    closeModal(modalId) {
        document.getElementById(modalId).classList.remove('active');
    }

    showNotification(message, type = 'info') {
        // 간단한 알림 (나중에 더 예쁜 토스트로 변경 가능)
        const className = type === 'error' ? 'error' : type === 'warning' ? 'warning' : 'info';
        console.log(`[${type.toUpperCase()}] ${message}`);

        // 임시로 alert 사용 (나중에 커스텀 알림으로 교체)
        if (type === 'error') {
            alert('오류: ' + message);
        }
    }

    removeFile(button) {
        const fileItem = button.closest('.file-item');
        if (fileItem) {
            fileItem.remove();
        }
    }

    // 데이터 관리
    loadSettings() {
        const saved = localStorage.getItem('studyPlatform_settings');
        if (saved) {
            this.settings = { ...this.settings, ...JSON.parse(saved) };
        }
    }

    saveSettings() {
        localStorage.setItem('studyPlatform_settings', JSON.stringify(this.settings));
    }

    loadStatistics() {
        const saved = localStorage.getItem('studyPlatform_statistics');
        if (saved) {
            this.statistics = JSON.parse(saved);
        }
    }

    saveStatistics() {
        localStorage.setItem('studyPlatform_statistics', JSON.stringify(this.statistics));
    }

    saveQuestions() {
        localStorage.setItem('studyPlatform_questions', JSON.stringify(this.questions));
    }

    loadQuestions() {
        const saved = localStorage.getItem('studyPlatform_questions');
        if (saved) {
            this.questions = JSON.parse(saved);
        }
    }

    getStatistics() {
        return {
            totalQuestions: this.questions.length,
            correctRate: this.calculateOverallCorrectRate(),
            weakProblems: this.getWeakProblemsCount(),
            studyTime: this.getTotalStudyTime()
        };
    }

    calculateOverallCorrectRate() {
        const stats = this.getQuestionStatistics();
        const rates = Object.values(stats).map(s => s.correctRate);
        return rates.length > 0 ? Math.round(rates.reduce((a, b) => a + b, 0) / rates.length) : 0;
    }

    getWeakProblemsCount() {
        const stats = this.getQuestionStatistics();
        return Object.values(stats).filter(s => s.correctRate < 70).length;
    }

    getTotalStudyTime() {
        return this.statistics.totalStudyTime || 0;
    }

    getQuestionStatistics() {
        return this.statistics.questionStats || {};
    }

    updateStatistics(results) {
        if (!this.statistics.questionStats) {
            this.statistics.questionStats = {};
        }

        results.detailedResults.forEach(result => {
            const qId = result.question.id;
            if (!this.statistics.questionStats[qId]) {
                this.statistics.questionStats[qId] = {
                    attempts: 0,
                    correct: 0,
                    correctRate: 0
                };
            }

            const stat = this.statistics.questionStats[qId];
            stat.attempts++;
            if (result.isCorrect) stat.correct++;
            stat.correctRate = Math.round((stat.correct / stat.attempts) * 100);
        });

        this.statistics.totalStudyTime = (this.statistics.totalStudyTime || 0) + Math.round(results.timeSpent / 60);
        this.saveStatistics();
        this.updateDashboard();
    }

    exportData() {
        const data = {
            questions: this.questions,
            statistics: this.statistics,
            settings: this.settings,
            exportDate: new Date().toISOString()
        };

        const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `study_data_${new Date().toISOString().split('T')[0]}.json`;
        a.click();
        URL.revokeObjectURL(url);
    }

    resetData() {
        if (confirm('모든 학습 데이터를 삭제하시겠습니까? 이 작업은 되돌릴 수 없습니다.')) {
            localStorage.removeItem('studyPlatform_questions');
            localStorage.removeItem('studyPlatform_statistics');
            localStorage.removeItem('studyPlatform_settings');

            this.questions = [];
            this.statistics = {};
            this.settings = { questionCount: 20, timeLimit: 30, theme: 'light' };

            this.updateDashboard();
            document.getElementById('fileList').innerHTML = '';

            this.showNotification('모든 데이터가 삭제되었습니다.', 'info');
        }
    }
}

// 전역 변수로 앱 인스턴스 생성
