getSimpleDescription(keyword) {
    const descriptions = {
        'TCP': '신뢰성 있는 연결지향 프로토콜',
        'UDP': '빠른 비연결지향 프로토콜',
        '스택': 'LIFO 방식의 자료구조',
        '큐': 'FIFO 방식의 자료구조',
        '배열': '연속된 메모리의 자료구조',
        '데이터베이스': '데이터를 체계적으로 관리하는 시스템',
