    const lines = content.split('\n').filter(line => line.trim().length > 10);
    const concepts = this.extractConcepts(content);

    console.log('추출된 개념들:', concepts);

    // 다양한 유형의 문제 생성
    questions.push(...this.generateDefinitionQuestions(concepts));
    questions.push(...this.generateComparisonQuestions(concepts));
    questions.push(...this.generateApplicationQuestions(concepts));
    questions.push(...this.generateKeywordQuestions(content));

    // 문제 ID 설정
    questions.forEach((q, index) => {
        q.id = Date.now() + index;
        q.subject = this.currentSubject || '기타';
        q.createdAt = new Date().toISOString();
    });

    console.log('생성된 문제 수:', questions.length);
    return questions.slice(0, 15); // 최대 15문제
}

extractConcepts(content) {
    // 주요 개념과 키워드 추출
    const concepts = [];

    // IT 관련 키워드들
    const itKeywords = [
        '데이터베이스', '프로토콜', '알고리즘', '자료구조', '운영체제',
        'TCP', 'UDP', 'HTTP', 'SQL', '배열', '스택', '큐',
        '정렬', '탐색', '암호화', '해시', '컴파일러', '인터프리터',
        '프로세스', '메모리', '네트워크', '보안', '프로그래밍'
    ];

    // 전기 관련 키워드들
    const electricKeywords = [
        '전압', '전류', '저항', '전력', '회로', '트랜지스터', '다이오드',
        '커패시터', '인덕터', '옴의법칙', 'AC', 'DC', '주파수', '임피던스'
    ];

    // 소방 관련 키워드들
    const fireKeywords = [
        '소화기', '스프링클러', '화재', '연기', '소화', '방화', '피난',
        '소방설비', '화재경보', '방재', '안전관리', '소화설비'
    ];

    const allKeywords = [...itKeywords, ...electricKeywords, ...fireKeywords];

    for (const keyword of allKeywords) {
        if (content.includes(keyword)) {
            const sentences = content.split(/[.!?]+/)
                .filter(s => s.includes(keyword) && s.trim().length > 20);

            if (sentences.length > 0) {
                concepts.push({
                    keyword: keyword,
                    context: sentences[0].trim(),
                    category: this.getKeywordCategory(keyword)
                });
            }
        }
    }

    return concepts;
}

getKeywordCategory(keyword) {
    const itKeywords = ['데이터베이스', '프로토콜', '알고리즘', 'TCP', 'UDP'];
    const electricKeywords = ['전압', '전류', '저항', '전력', '회로'];
    const fireKeywords = ['소화기', '화재', '소방설비'];

    if (itKeywords.some(k => keyword.includes(k))) return 'IT';
    if (electricKeywords.some(k => keyword.includes(k))) return '전기';
    if (fireKeywords.some(k => keyword.includes(k))) return '소방';
    return '기타';
}

generateDefinitionQuestions(concepts) {
    const questions = [];

    for (let i = 0; i < Math.min(concepts.length, 5); i++) {
        const concept = concepts[i];

        const question = {
            text: `${concept.keyword}에 대한 설명으로 가장 적절한 것은?`,
            options: this.generateDefinitionOptions(concept),
            correctAnswer: 0,
            difficulty: 'medium',
            category: '정의',
            type: 'definition'
        };

        questions.push(question);
    }

    return questions;
}

generateDefinitionOptions(concept) {
    const correctOption = concept.context;

    // 카테고리별 오답 생성
    const wrongOptions = this.generateWrongDefinitions(concept.category, concept.keyword);

    const options = [correctOption, ...wrongOptions].slice(0, 4);

    // 배열 섞기
    for (let i = options.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [options[i], options[j]] = [options[j], options[i]];
    }

    return options;
}

generateWrongDefinitions(category, keyword) {
    const wrongDefinitions = {
        'IT': [
            '하드웨어 구성 요소 중 하나로 물리적 연결을 담당한다',
            '사용자 인터페이스를 설계하는 방법론이다',
            '네트워크 속도를 측정하는 단위이다'
        ],
        '전기': [
            '전기 에너지를 저장하는 장치이다',
            '전기 신호를 증폭하는 회로이다',
            '전기 저항을 줄이는 방법이다'
        ],
        '소방': [
            '화재 예방을 위한 건축 구조이다',
            '연기 배출 시설의 일종이다',
            '방화벽 설치 기준이다'
        ]
    };

    return wrongDefinitions[category] || [
        '관련 기술의 한 분야이다',
        '표준화된 규격 중 하나이다',
        '특별한 용도로 사용되는 도구이다'
    ];
}

generateComparisonQuestions(concepts) {
    const questions = [];

    if (concepts.length >= 2) {
        for (let i = 0; i < Math.min(2, concepts.length - 1); i++) {
            const concept1 = concepts[i];
            const concept2 = concepts[i + 1];

            if (concept1.category === concept2.category) {
                const question = {
                    text: `${concept1.keyword}와 ${concept2.keyword}의 차이점으로 옳은 것은?`,
                    options: [
                        `${concept1.keyword}는 ${this.getSimpleDescription(concept1.keyword)}이고, ${concept2.keyword}는 ${this.getSimpleDescription(concept2.keyword)}이다`,
                        `${concept1.keyword}와 ${concept2.keyword}는 같은 기능을 한다`,
                        `${concept1.keyword}는 ${concept2.keyword}의 상위 개념이다`,
                        `${concept1.keyword}와 ${concept2.keyword}는 서로 반대 개념이다`
                    ],
                    correctAnswer: 0,
                    difficulty: 'hard',
                    category: '비교',
                    type: 'comparison'
                };

                questions.push(question);
            }
        }
    }

    return questions;
}

getSimpleDescription(keyword) {
    const descriptions = {
        'TCP': '신뢰성 있는 연결지향 프로토콜',
        'UDP': '빠른 비연결지향 프로토콜',
        '스택': 'LIFO 방식의 자료구조',
        '큐': 'FIFO 방식의 자료구조',
        '배열': '연속된 메모리의 자료구조',
        '데이터베이스': '데이터를 체계적으로 관리하는 시스템',
        '알고리즘': '문제 해결을 위한 절차',
        '운영체제': '하드웨어와 소프트웨어 간 인터페이스'
    };

    return descriptions[keyword] || '특별한 기능을 하는 개념';
}

generateApplicationQuestions(concepts) {
    const questions = [];

    for (let i = 0; i < Math.min(concepts.length, 3); i++) {
        const concept = concepts[i];

        const question = {
            text: `${concept.keyword}를 실제로 활용하는 경우는?`,
            options: this.generateApplicationOptions(concept.keyword),
            correctAnswer: 0,
            difficulty: 'medium',
            category: '응용',
            type: 'application'
        };

        questions.push(question);
    }

    return questions;
}

generateApplicationOptions(keyword) {
    const applications = {
        'TCP': [
            '웹 브라우저가 웹 서버와 통신할 때',
            '프린터로 문서를 출력할 때',
            '키보드로 문자를 입력할 때',
            '화면에 이미지를 표시할 때'
        ],
        'UDP': [
            '실시간 스트리밍 서비스에서',
            '파일을 다운로드할 때',
            '이메일을 전송할 때',
            '데이터베이스에 접속할 때'
        ],
        '스택': [
            '함수 호출 관리에서',
            '파일 저장 시스템에서',
            '사용자 인터페이스에서',
            '네트워크 연결에서'
        ],
        '데이터베이스': [
            '고객 정보를 체계적으로 관리할 때',
            '컴퓨터 부팅 과정에서',
            '그래픽을 화면에 출력할 때',
            '키보드 입력을 처리할 때'
        ]
    };

    return applications[keyword] || [
        '해당 기술이 필요한 상황에서',
        '일반적인 컴퓨터 사용 시',
        '특별한 경우에만',
        '전혀 사용하지 않는다'
    ];
}

generateKeywordQuestions(content) {
    const questions = [];
    const lines = content.split('\n').filter(line => line.includes('는') || line.includes('이다'));

    for (let i = 0; i < Math.min(lines.length, 3); i++) {
        const line = lines[i].trim();
        if (line.length < 30) continue;

        // 빈칸 문제 생성
        const words = line.split(' ');
        if (words.length > 5) {
            const keywordIndex = Math.floor(words.length / 2);
            const keyword = words[keywordIndex];
            const questionText = line.replace(keyword, '( ? )');

            const question = {
                text: `다음 빈칸에 들어갈 가장 적절한 단어는?\n\n"${questionText}"`,
                options: [
                    keyword,
                    this.generateSimilarWord(keyword),
                    this.generateSimilarWord(keyword),
                    this.generateSimilarWord(keyword)
                ],
                correctAnswer: 0,
                difficulty: 'easy',
                category: '어휘',
                type: 'keyword'
            };

            questions.push(question);
        }
    }

    return questions;
}

generateSimilarWord(word) {
    const alternatives = [
        '시스템', '방법', '기술', '도구', '과정', '절차', '방식', '구조',
        '개념', '원리', '이론', '모델', '패턴', '규칙', '표준', '규격'
    ];

    return alternatives[Math.floor(Math.random() * alternatives.length)];
}

