// attok.co.kr 출결 모니터링 스크립트 Step 2
// 브라우저 콘솔에 복사-붙여넣기하여 실행
// 추가 기능: 출석시간 저장, 경과시간 계산, localStorage 데이터 유지

(function() {
    console.log('=== 출결 모니터링 스크립트 Step 2 시작 ===');

    let monitoringInterval;
    const LESSON_DURATION = 2 * 60 * 60 * 1000; // 2시간 (밀리초)
    const STORAGE_KEY = 'attendanceData';

    // localStorage에서 출석 데이터 불러오기
    function loadAttendanceData() {
        try {
            const data = localStorage.getItem(STORAGE_KEY);
            return data ? JSON.parse(data) : {};
        } catch (e) {
            console.error('출석 데이터 로드 실패:', e);
            return {};
        }
    }

    // localStorage에 출석 데이터 저장
    function saveAttendanceData(data) {
        try {
            localStorage.setItem(STORAGE_KEY, JSON.stringify(data));
        } catch (e) {
            console.error('출석 데이터 저장 실패:', e);
        }
    }

    // 출석 데이터 초기화
    let attendanceData = loadAttendanceData();

    // 시간 포맷팅 함수 (HH:MM:SS)
    function formatTime(date) {
        return date.toLocaleTimeString('ko-KR', {
            hour: '2-digit',
            minute: '2-digit',
            second: '2-digit'
        });
    }

    // 경과 시간 계산 (밀리초를 시:분:초로 변환)
    function calculateElapsedTime(startTime) {
        const now = new Date();
        const elapsed = now.getTime() - startTime;

        const hours = Math.floor(elapsed / (1000 * 60 * 60));
        const minutes = Math.floor((elapsed % (1000 * 60 * 60)) / (1000 * 60));
        const seconds = Math.floor((elapsed % (1000 * 60)) / 1000);

        return {
            hours,
            minutes,
            seconds,
            total: elapsed,
            formatted: `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`
        };
    }

    // 남은 시간 계산
    function calculateRemainingTime(startTime) {
        const elapsed = new Date().getTime() - startTime;
        const remaining = LESSON_DURATION - elapsed;

        if (remaining <= 0) {
            return {
                hours: 0,
                minutes: 0,
                seconds: 0,
                total: 0,
                formatted: '수업완료',
                isCompleted: true
            };
        }

        const hours = Math.floor(remaining / (1000 * 60 * 60));
        const minutes = Math.floor((remaining % (1000 * 60 * 60)) / (1000 * 60));
        const seconds = Math.floor((remaining % (1000 * 60)) / 1000);

        return {
            hours,
            minutes,
            seconds,
            total: remaining,
            formatted: `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`,
            isCompleted: false
        };
    }

    // 테이블 형태로 콘솔 출력
    function printAttendanceTable(students) {
        console.log('\n=== 출석 현황 테이블 ===');
        console.log('┌─────────────────┬─────────────┬─────────────┬─────────────┐');
        console.log('│      학생명     │  출석시간   │  경과시간   │  남은시간   │');
        console.log('├─────────────────┼─────────────┼─────────────┼─────────────┤');

        students.forEach(student => {
            const nameCell = student.name.padEnd(15, ' ');
            const timeCell = student.attendanceTime ? student.attendanceTime.padEnd(11, ' ') : '미출석      ';
            const elapsedCell = student.elapsedTime ? student.elapsedTime.padEnd(11, ' ') : '-          ';
            const remainingCell = student.remainingTime ? student.remainingTime.padEnd(11, ' ') : '-          ';

            console.log(`│ ${nameCell} │ ${timeCell} │ ${elapsedCell} │ ${remainingCell} │`);
        });

        console.log('└─────────────────┴─────────────┴─────────────┴─────────────┘');
    }

    // 하늘색 배경을 가진 학생 찾기 및 시간 계산
    function findAttendedStudentsWithTime() {
        console.log('--- 출석 학생 체크 및 시간 계산 시작 ---');

        const allRows = document.querySelectorAll('tr, .student-row');
        let currentAttended = [];
        let totalChecked = 0;
        const now = new Date();

        allRows.forEach((row, index) => {
            const computedStyle = window.getComputedStyle(row);
            const bgColor = computedStyle.backgroundColor;

            // skyblue 색상 확인
            const isSkyBlue = 
                bgColor.includes('135, 206, 235') ||
                bgColor.includes('skyblue') ||
                bgColor.toLowerCase().includes('skyblue') ||
                row.classList.contains('attended');

            if (isSkyBlue) {
                // 학생 이름 추출
                let studentName = '';

                if (row.dataset && row.dataset.name) {
                    studentName = row.dataset.name;
                } else if (row.querySelector('.student-name')) {
                    studentName = row.querySelector('.student-name').textContent.trim();
                } else if (row.querySelector('td:nth-child(2)')) {
                    studentName = row.querySelector('td:nth-child(2)').textContent.trim();
                } else if (row.textContent.trim()) {
                    const text = row.textContent.trim();
                    const nameMatch = text.match(/[가-힣]{2,4}/);
                    studentName = nameMatch ? nameMatch[0] : text.split(' ')[0];
                }

                if (studentName && studentName !== '학생명') {
                    // 기존 출석 데이터가 없으면 새로 생성
                    if (!attendanceData[studentName]) {
                        attendanceData[studentName] = {
                            attendanceTime: now.getTime(),
                            attendanceTimeFormatted: formatTime(now)
                        };
                        saveAttendanceData(attendanceData);
                        console.log(`새로운 출석 등록: ${studentName} (${formatTime(now)})`);
                    }

                    const studentData = attendanceData[studentName];
                    const elapsed = calculateElapsedTime(studentData.attendanceTime);
                    const remaining = calculateRemainingTime(studentData.attendanceTime);

                    currentAttended.push({
                        name: studentName,
                        attendanceTime: studentData.attendanceTimeFormatted,
                        elapsedTime: elapsed.formatted,
                        remainingTime: remaining.formatted,
                        isCompleted: remaining.isCompleted
                    });
                }
            }

            totalChecked++;
        });

        // 현재 출석하지 않은 학생들의 데이터는 유지하되 테이블에서는 제외
        // (페이지를 벗어났다가 다시 돌아온 경우를 대비)

        console.log(`총 ${totalChecked}개 요소 중 ${currentAttended.length}명의 출석 학생 발견`);

        if (currentAttended.length > 0) {
            printAttendanceTable(currentAttended);

            // 요약 정보
            const completedCount = currentAttended.filter(s => s.isCompleted).length;
            const inProgressCount = currentAttended.length - completedCount;

            console.log(`\n출석 요약:`);
            console.log(`- 현재 출석: ${currentAttended.length}명`);
            console.log(`- 수업 진행중: ${inProgressCount}명`);
            console.log(`- 수업 완료: ${completedCount}명`);
        } else {
            console.log('현재 출석한 학생이 없습니다.');
        }

        console.log('--- 출석 학생 체크 완료 ---');
        return currentAttended;
    }

    // 자동 모니터링 시작 함수
    function startMonitoring() {
        console.log('5초마다 자동 출석 체크를 시작합니다...');

        // 즉시 한 번 실행
        findAttendedStudentsWithTime();

        // 5초마다 반복 실행
        monitoringInterval = setInterval(() => {
            const currentTime = new Date().toLocaleTimeString();
            console.log(`\n[${currentTime}] 자동 체크 실행:`);
            findAttendedStudentsWithTime();
        }, 5000);

        console.log('자동 모니터링이 시작되었습니다. 중지하려면 stopMonitoring()을 실행하세요.');
    }

    // 자동 모니터링 중지 함수
    function stopMonitoring() {
        if (monitoringInterval) {
            clearInterval(monitoringInterval);
            monitoringInterval = null;
            console.log('자동 모니터링이 중지되었습니다.');
        } else {
            console.log('실행 중인 모니터링이 없습니다.');
        }
    }

    // 출석 데이터 초기화 함수
    function resetAttendanceData() {
        attendanceData = {};
        saveAttendanceData(attendanceData);
        console.log('출석 데이터가 초기화되었습니다.');
    }

    // 전체 출석 기록 보기
    function showAllAttendanceData() {
        console.log('\n=== 전체 출석 데이터 ===');
        if (Object.keys(attendanceData).length === 0) {
            console.log('저장된 출석 데이터가 없습니다.');
            return;
        }

        const allStudents = Object.keys(attendanceData).map(name => {
            const data = attendanceData[name];
            const elapsed = calculateElapsedTime(data.attendanceTime);
            const remaining = calculateRemainingTime(data.attendanceTime);

            return {
                name: name,
                attendanceTime: data.attendanceTimeFormatted,
                elapsedTime: elapsed.formatted,
                remainingTime: remaining.formatted,
                isCompleted: remaining.isCompleted
            };
        });

        printAttendanceTable(allStudents);
    }

    // 특정 학생 출석 데이터 삭제
    function removeStudentData(studentName) {
        if (attendanceData[studentName]) {
            delete attendanceData[studentName];
            saveAttendanceData(attendanceData);
            console.log(`${studentName}의 출석 데이터가 삭제되었습니다.`);
        } else {
            console.log(`${studentName}의 출석 데이터를 찾을 수 없습니다.`);
        }
    }

    // 전역 함수로 등록 (콘솔에서 직접 호출 가능)
    window.findAttendedStudentsWithTime = findAttendedStudentsWithTime;
    window.startMonitoring = startMonitoring;
    window.stopMonitoring = stopMonitoring;
    window.resetAttendanceData = resetAttendanceData;
    window.showAllAttendanceData = showAllAttendanceData;
    window.removeStudentData = removeStudentData;

    // 스크립트 로드 완료 메시지
    console.log('스크립트 Step 2 로드 완료!');
    console.log('사용 가능한 함수:');
    console.log('  - findAttendedStudentsWithTime(): 현재 출석 학생 찾기 + 시간 계산');
    console.log('  - startMonitoring(): 5초마다 자동 체크 시작');
    console.log('  - stopMonitoring(): 자동 체크 중지');
    console.log('  - showAllAttendanceData(): 전체 출석 기록 보기');
    console.log('  - resetAttendanceData(): 출석 데이터 초기화');
    console.log('  - removeStudentData("학생명"): 특정 학생 데이터 삭제');
    console.log('');
    console.log('출석 시간은 localStorage에 저장되어 페이지 새로고침 후에도 유지됩니다.');
    console.log('2시간 수업 기준으로 남은 시간이 계산됩니다.');
    console.log('');
    console.log('시작하려면 findAttendedStudentsWithTime() 또는 startMonitoring()을 실행하세요.');

    // 기존 데이터가 있으면 알림
    if (Object.keys(attendanceData).length > 0) {
        console.log(`\n기존 출석 데이터 ${Object.keys(attendanceData).length}건이 로드되었습니다.`);
        console.log('전체 기록을 보려면 showAllAttendanceData()를 실행하세요.');
    }

})();

