                font-size: 13px;
                cursor: move;
                user-select: none;
                display: flex;
                justify-content: space-between;
                align-items: center;
            }

            #monitorCloseBtn {
                background: rgba(255, 255, 255, 0.2);
                border: none;
                color: white;
                width: 20px;
                height: 20px;
                border-radius: 50%;
                cursor: pointer;
                font-size: 14px;
                display: flex;
                align-items: center;
                justify-content: center;
                transition: background 0.2s;
            }

            #monitorCloseBtn:hover {
                background: rgba(255, 255, 255, 0.3);
            }

            #monitorContent {
                padding: 12px;
                height: calc(100% - 40px);
                overflow-y: auto;
            }

            .monitor-section {
                margin-bottom: 15px;
            }

            .monitor-section h4 {
                margin: 0 0 8px 0;
                font-size: 13px;
                color: #333;
                border-bottom: 1px solid #eee;
                padding-bottom: 4px;
                display: flex;
                justify-content: space-between;
                align-items: center;
            }

            .section-icon {
                font-size: 11px;
                color: #666;
            }

            .student-item {
                display: flex;
                justify-content: space-between;
                align-items: center;
                padding: 8px;
                margin: 4px 0;
                background: #f8f9fa;
