            }

            .student-time {
                text-align: right;
                min-width: 70px;
            }

            .remaining-time {
                font-weight: bold;
                padding: 3px 6px;
                border-radius: 4px;
                background: #e8f5e8;
                color: #155724;
                font-size: 11px;
                display: block;
                text-align: center;
            }

            .remaining-time.warning {
                background: #fff3cd;
                color: #856404;
            }

            .remaining-time.danger {
                background: #f8d7da;
                color: #721c24;
                animation: blink 1s infinite;
            }

            .remaining-time.completed {
                background: #d4edda;
                color: #155724;
            }

            @keyframes blink {
                0%, 50% { opacity: 1; }
                51%, 100% { opacity: 0.6; }
            }

            .lesson-duration {
                font-size: 9px;
                color: #999;
                margin-top: 2px;
            }

            .status-summary {
                display: grid;
                grid-template-columns: repeat(3, 1fr);
                gap: 8px;
                margin-bottom: 15px;
            }

            .status-item {
                text-align: center;
                padding: 8px;
                background: #f1f3f4;
                border-radius: 6px;
                transition: background 0.2s;
            }

            .status-item:hover {
                background: #e8eaed;
            }

            .status-number {
                display: block;
                font-size: 18px;
