                border: 2px dashed #dee2e6;
            }

            .monitor-controls {
                display: grid;
                grid-template-columns: 1fr 1fr;
                gap: 6px;
                margin-top: 15px;
            }

            .monitor-btn {
                padding: 8px 12px;
                border: none;
                border-radius: 4px;
                font-size: 11px;
                cursor: pointer;
                transition: all 0.2s;
                font-weight: 500;
            }

            .monitor-btn.primary {
                background: #007bff;
                color: white;
            }

            .monitor-btn.primary:hover {
                background: #0056b3;
                transform: translateY(-1px);
            }

            .monitor-btn.secondary {
                background: #6c757d;
                color: white;
            }

            .monitor-btn.secondary:hover {
                background: #545b62;
                transform: translateY(-1px);
            }

            .monitor-btn.success {
                background: #28a745;
                color: white;
            }

            .monitor-btn.success:hover {
                background: #1e7e34;
                transform: translateY(-1px);
            }

            .monitor-btn.danger {
                background: #dc3545;
                color: white;
            }

            .monitor-btn.danger:hover {
                background: #c82333;
                transform: translateY(-1px);
            }

            .settings-panel {
                background: #f8f9fa;
                border-radius: 6px;
                padding: 10px;
                margin-top: 10px;
                border: 1px solid #e9ecef;
            }

            .settings-row {
                display: flex;
                justify-content: space-between;
                align-items: center;
                margin: 6px 0;
            }

            .settings-label {
                font-size: 11px;
                color: #333;
                font-weight: 500;
            }

            .settings-input {
                width: 60px;
                padding: 2px 4px;
                border: 1px solid #ddd;
                border-radius: 3px;
                font-size: 10px;
            }

            .settings-toggle {
                width: 30px;
                height: 16px;
                background: #ccc;
                border-radius: 8px;
                position: relative;
                cursor: pointer;
                transition: background 0.2s;
            }

            .settings-toggle.active {
                background: #007bff;
            }

            .settings-toggle::after {
                content: '';
                position: absolute;
                width: 12px;
                height: 12px;
                border-radius: 50%;
                background: white;
                top: 2px;
                left: 2px;
                transition: left 0.2s;
            }

            .settings-toggle.active::after {
                left: 16px;
            }

            .last-update {
                text-align: center;
                font-size: 10px;
                color: #999;
                margin-top: 10px;
                padding-top: 8px;
                border-top: 1px solid #eee;
            }

            .notification-icon {
                color: #ffc107;
                animation: shake 0.5s infinite;
            }

            @keyframes shake {
                0%, 100% { transform: rotate(0deg); }
                25% { transform: rotate(-5deg); }
                75% { transform: rotate(5deg); }
            }
        `;

        document.head.appendChild(style);
    }

    // localStorage에서 데이터 불러오기
    function loadData(key, defaultValue = {}) {
        try {
            const data = localStorage.getItem(key);
            return data ? JSON.parse(data) : defaultValue;
        } catch (e) {
            console.error(`데이터 로드 실패 (${key}):`, e);
            return defaultValue;
        }
    }

    // localStorage에 데이터 저장
    function saveData(key, data) {
        try {
            localStorage.setItem(key, JSON.stringify(data));
        } catch (e) {
            console.error(`데이터 저장 실패 (${key}):`, e);
        }
    }

    // 출석 데이터 및 설정 초기화
    let attendanceData = loadData(STORAGE_KEY);
    settings = { ...settings, ...loadData(SETTINGS_KEY) };

    // 시간 포맷팅 함수
    function formatTime(date) {
        return date.toLocaleTimeString('ko-KR', {
            hour: '2-digit',
            minute: '2-digit',
            second: '2-digit'
        });
    }

    // 학생별 수업 시간 가져오기
    function getStudentLessonDuration(studentName) {
        const studentData = attendanceData[studentName];
        return studentData?.lessonDuration || settings.defaultLessonDuration;
    }

    // 학생별 수업 시간 설정
    function setStudentLessonDuration(studentName, duration) {
        if (!attendanceData[studentName]) return;
        attendanceData[studentName].lessonDuration = duration;
        saveData(STORAGE_KEY, attendanceData);
    }

    // 남은 시간 계산
    function calculateRemainingTime(startTime, studentName) {
        const lessonDuration = getStudentLessonDuration(studentName);
        const elapsed = new Date().getTime() - startTime;
        const remaining = lessonDuration - elapsed;

        if (remaining <= 0) {
            return {
                hours: 0,
                minutes: 0,
                seconds: 0,
                total: 0,
                formatted: '완료',
                isCompleted: true,
                status: 'completed'
            };
        }

        const hours = Math.floor(remaining / (1000 * 60 * 60));
        const minutes = Math.floor((remaining % (1000 * 60 * 60)) / (1000 * 60));
        const seconds = Math.floor((remaining % (1000 * 60)) / 1000);

        // 경고 시간 체크
        let status = 'normal';
        const warningTime = settings.warningMinutes * 60 * 1000;
        if (remaining <= warningTime) {
            status = 'danger';
        } else if (remaining <= warningTime * 2) {
            status = 'warning';
        }

        return {
            hours,
            minutes,
            seconds,
            total: remaining,
            formatted: `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`,
            isCompleted: false,
            status: status
        };
    }

    // 알림 체크
    function checkNotifications(students) {
        students.forEach(student => {
            const remaining = student.remaining;
            const studentKey = student.name;

            // 하원 완료 알림
            if (remaining.isCompleted && !completedStudents.has(studentKey)) {
                completedStudents.add(studentKey);
                showNotification(
                    '하원 시간입니다!', 
                    `${student.name} 학생의 수업이 완료되었습니다.`
                );
                playTripleBeep();
                console.log(`🏠 ${student.name} - 하원 시간 알림 발송`);
            }

            // 10분 전 경고 알림
            else if (remaining.status === 'danger' && !alertedStudents.has(studentKey)) {
                alertedStudents.add(studentKey);
                showNotification(
                    '하원 10분 전!', 
                    `${student.name} 학생의 하원 시간이 ${Math.ceil(remaining.total / (1000 * 60))}분 남았습니다.`
                );
                playDoubleBeep();
                console.log(`⚠️ ${student.name} - 하원 10분 전 알림 발송`);
            }
        });
    }

    // 모니터링 창 생성
    function createMonitoringWindow() {
        if (monitoringWindow) return;

        addStyles();

        monitoringWindow = document.createElement('div');
        monitoringWindow.id = 'attendanceMonitor';

        monitoringWindow.innerHTML = `
            <div id="monitorHeader">
                <span>📚 출석 모니터링</span>
                <button id="monitorCloseBtn">&times;</button>
            </div>
            <div id="monitorContent">
                <div class="status-summary">
                    <div class="status-item">
                        <span class="status-number" id="totalStudents">0</span>
                        <span class="status-label">총 출석</span>
                    </div>
                    <div class="status-item">
                        <span class="status-number" id="inProgressStudents">0</span>
                        <span class="status-label">진행중</span>
                    </div>
                    <div class="status-item">
                        <span class="status-number" id="completedStudents">0</span>
                        <span class="status-label">완료</span>
                    </div>
                </div>

                <div class="monitor-section">
                    <h4>
                        출석 학생 목록
                        <span class="section-icon" id="notificationStatus">🔔</span>
                    </h4>
                    <div id="studentsList">
                        <div class="no-students">📝 출석한 학생이 없습니다</div>
                    </div>
                </div>

                <div class="monitor-section">
                    <h4>설정</h4>
                    <div class="settings-panel">
                        <div class="settings-row">
                            <span class="settings-label">기본 수업시간 (시간)</span>
                            <input type="number" class="settings-input" id="defaultDuration" value="2" min="0.5" max="8" step="0.5">
                        </div>
                        <div class="settings-row">
                            <span class="settings-label">경고 시간 (분)</span>
                            <input type="number" class="settings-input" id="warningMinutes" value="10" min="1" max="60">
                        </div>
                        <div class="settings-row">
                            <span class="settings-label">브라우저 알림</span>
                            <div class="settings-toggle" id="notificationToggle"></div>
                        </div>
                        <div class="settings-row">
                            <span class="settings-label">알림 소리</span>
                            <div class="settings-toggle" id="soundToggle"></div>
                        </div>
                    </div>
                </div>

                <div class="monitor-controls">
                    <button class="monitor-btn primary" id="refreshBtn">🔄 새로고침</button>
                    <button class="monitor-btn success" id="testSoundBtn">🔊 소리테스트</button>
                    <button class="monitor-btn secondary" id="exportBtn">📤 데이터내보내기</button>
                    <button class="monitor-btn danger" id="resetBtn">🗑️ 초기화</button>
                </div>

                <div class="last-update" id="lastUpdate">
                    마지막 업데이트: --:--:--
                </div>
            </div>
        `;

        document.body.appendChild(monitoringWindow);
        setupEventListeners();
        updateMonitoringWindow();
    }

    // 이벤트 리스너 설정
    function setupEventListeners() {
        const header = document.getElementById('monitorHeader');
        const closeBtn = document.getElementById('monitorCloseBtn');
        const refreshBtn = document.getElementById('refreshBtn');
        const resetBtn = document.getElementById('resetBtn');
        const testSoundBtn = document.getElementById('testSoundBtn');
        const exportBtn = document.getElementById('exportBtn');

        // 기본 버튼들
        closeBtn.addEventListener('click', closeMonitoringWindow);
        refreshBtn.addEventListener('click', () => {
            findAttendedStudentsWithTime();
            updateMonitoringWindow();
        });
        resetBtn.addEventListener('click', () => {
            if (confirm('모든 출석 데이터를 초기화하시겠습니까?')) {
                resetAttendanceData();
                updateMonitoringWindow();
            }
        });
        testSoundBtn.addEventListener('click', () => {
            playTripleBeep();
        });
        exportBtn.addEventListener('click', exportData);

        // 설정 입력들
        const defaultDuration = document.getElementById('defaultDuration');
        const warningMinutes = document.getElementById('warningMinutes');
        const notificationToggle = document.getElementById('notificationToggle');
        const soundToggle = document.getElementById('soundToggle');

        // 설정 초기화
        defaultDuration.value = settings.defaultLessonDuration / (1000 * 60 * 60);
        warningMinutes.value = settings.warningMinutes;
        notificationToggle.classList.toggle('active', settings.notificationsEnabled);
        soundToggle.classList.toggle('active', settings.soundEnabled);

        // 설정 이벤트
        defaultDuration.addEventListener('change', (e) => {
            settings.defaultLessonDuration = parseFloat(e.target.value) * 60 * 60 * 1000;
            saveData(SETTINGS_KEY, settings);
        });

        warningMinutes.addEventListener('change', (e) => {
            settings.warningMinutes = parseInt(e.target.value);
            saveData(SETTINGS_KEY, settings);
        });

        notificationToggle.addEventListener('click', async () => {
            if (!settings.notificationsEnabled) {
                const granted = await requestNotificationPermission();
                if (granted) {
                    settings.notificationsEnabled = true;
                    notificationToggle.classList.add('active');
                }
            } else {
                settings.notificationsEnabled = false;
                notificationToggle.classList.remove('active');
            }
            saveData(SETTINGS_KEY, settings);
            updateNotificationStatus();
        });

        soundToggle.addEventListener('click', () => {
            settings.soundEnabled = !settings.soundEnabled;
            soundToggle.classList.toggle('active', settings.soundEnabled);
            saveData(SETTINGS_KEY, settings);
            if (settings.soundEnabled) playBeep();
        });

        // 드래그 기능
        header.addEventListener('mousedown', startDrag);
        document.addEventListener('mousemove', drag);
        document.addEventListener('mouseup', stopDrag);
    }

    // 알림 상태 업데이트
    function updateNotificationStatus() {
        const statusIcon = document.getElementById('notificationStatus');
        if (settings.notificationsEnabled && notificationPermission) {
            statusIcon.textContent = '🔔';
            statusIcon.title = '알림 활성화됨';
        } else {
            statusIcon.textContent = '🔕';
            statusIcon.title = '알림 비활성화됨';
        }
    }

    // 데이터 내보내기
    function exportData() {
        const exportData = {
            attendanceData: attendanceData,
            settings: settings,
            exportDate: new Date().toISOString()
        };

        const blob = new Blob([JSON.stringify(exportData, null, 2)], { type: 'application/json' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `attendance_data_${new Date().toISOString().split('T')[0]}.json`;
        a.click();
        URL.revokeObjectURL(url);

        showNotification('데이터 내보내기', '출석 데이터가 파일로 저장되었습니다.');
    }

    // 드래그 관련 함수들
    function startDrag(e) {
        isDragging = true;
        const rect = monitoringWindow.getBoundingClientRect();
        dragOffset.x = e.clientX - rect.left;
        dragOffset.y = e.clientY - rect.top;
        monitoringWindow.style.cursor = 'grabbing';
    }

    function drag(e) {
        if (!isDragging) return;
        e.preventDefault();
        const x = e.clientX - dragOffset.x;
        const y = e.clientY - dragOffset.y;
        const maxX = window.innerWidth - monitoringWindow.offsetWidth;
        const maxY = window.innerHeight - monitoringWindow.offsetHeight;
        const boundedX = Math.max(0, Math.min(x, maxX));
        const boundedY = Math.max(0, Math.min(y, maxY));
        monitoringWindow.style.left = boundedX + 'px';
        monitoringWindow.style.top = boundedY + 'px';
        monitoringWindow.style.right = 'auto';
        monitoringWindow.style.bottom = 'auto';
    }

    function stopDrag() {
        isDragging = false;
        if (monitoringWindow) monitoringWindow.style.cursor = 'default';
    }

    // 현재 출석 학생 정보 가져오기
    function getCurrentAttendedStudents() {
        const allRows = document.querySelectorAll('tr, .student-row');
        let currentAttended = [];
        const now = new Date();

        allRows.forEach((row) => {
            const computedStyle = window.getComputedStyle(row);
            const bgColor = computedStyle.backgroundColor;

            const isSkyBlue = 
                bgColor.includes('135, 206, 235') ||
                bgColor.includes('skyblue') ||
                bgColor.toLowerCase().includes('skyblue') ||
                row.classList.contains('attended');

            if (isSkyBlue) {
                let studentName = '';

                if (row.dataset && row.dataset.name) {
                    studentName = row.dataset.name;
                } else if (row.querySelector('.student-name')) {
                    studentName = row.querySelector('.student-name').textContent.trim();
                } else if (row.querySelector('td:nth-child(2)')) {
                    studentName = row.querySelector('td:nth-child(2)').textContent.trim();
                } else if (row.textContent.trim()) {
                    const text = row.textContent.trim();
                    const nameMatch = text.match(/[가-힣]{2,4}/);
                    studentName = nameMatch ? nameMatch[0] : text.split(' ')[0];
                }

                if (studentName && studentName !== '학생명') {
                    if (!attendanceData[studentName]) {
                        attendanceData[studentName] = {
                            attendanceTime: now.getTime(),
                            attendanceTimeFormatted: formatTime(now),
                            lessonDuration: settings.defaultLessonDuration
                        };
                        saveData(STORAGE_KEY, attendanceData);
                    }

                    const studentData = attendanceData[studentName];
                    const remaining = calculateRemainingTime(studentData.attendanceTime, studentName);

                    currentAttended.push({
                        name: studentName,
                        attendanceTime: studentData.attendanceTimeFormatted,
                        remaining: remaining,
                        isCompleted: remaining.isCompleted,
                        lessonDuration: getStudentLessonDuration(studentName)
                    });
                }
            }
        });

        return currentAttended;
    }

    // 모니터링 창 업데이트
    function updateMonitoringWindow() {
        if (!monitoringWindow) return;

        const students = getCurrentAttendedStudents();

        // 알림 체크
        checkNotifications(students);

        // 요약 정보 업데이트
        const totalStudents = students.length;
        const completedStudents = students.filter(s => s.isCompleted).length;
        const inProgressStudents = totalStudents - completedStudents;

        document.getElementById('totalStudents').textContent = totalStudents;
        document.getElementById('inProgressStudents').textContent = inProgressStudents;
        document.getElementById('completedStudents').textContent = completedStudents;

        // 학생 목록 업데이트
        const studentsList = document.getElementById('studentsList');

        if (students.length === 0) {
            studentsList.innerHTML = '<div class="no-students">📝 출석한 학생이 없습니다</div>';
        } else {
            studentsList.innerHTML = students.map((student, index) => {
                const durationHours = (student.lessonDuration / (1000 * 60 * 60)).toFixed(1);
                return `
                    <div class="student-item ${student.remaining.status}" onclick="editStudentLessonTime('${student.name}')">
                        <div class="student-info">
                            <div class="student-name">${student.name}</div>
                            <div class="student-details">
                                출석: ${student.attendanceTime}
                                <div class="lesson-duration">${durationHours}시간 수업</div>
                            </div>
                        </div>
                        <div class="student-time">
                            <div class="remaining-time ${student.remaining.status}">
                                ${student.remaining.formatted}
                            </div>
                        </div>
                    </div>
                `;
            }).join('');
        }

        // 알림 상태 업데이트
        updateNotificationStatus();

        // 마지막 업데이트 시간
        document.getElementById('lastUpdate').textContent = 
            `마지막 업데이트: ${formatTime(new Date())}`;
    }

    // 학생별 수업 시간 편집
    function editStudentLessonTime(studentName) {
        const currentDuration = getStudentLessonDuration(studentName) / (1000 * 60 * 60);
        const newDuration = prompt(`${studentName}의 수업 시간을 입력하세요 (시간):`, currentDuration);

        if (newDuration !== null && !isNaN(newDuration) && newDuration > 0) {
            const durationMs = parseFloat(newDuration) * 60 * 60 * 1000;
            setStudentLessonDuration(studentName, durationMs);
            updateMonitoringWindow();
            showNotification('수업 시간 변경', `${studentName}의 수업 시간이 ${newDuration}시간으로 변경되었습니다.`);
        }
    }

    // 모니터링 창 닫기
    function closeMonitoringWindow() {
        if (monitoringWindow) {
            document.body.removeChild(monitoringWindow);
            monitoringWindow = null;
        }
        if (uiUpdateInterval) {
            clearInterval(uiUpdateInterval);
            uiUpdateInterval = null;
        }
    }

    // 기존 함수들 (콘솔용)
    function findAttendedStudentsWithTime() {
        const students = getCurrentAttendedStudents();

        if (students.length > 0) {
            console.log('\n=== 출석 현황 테이블 ===');
            console.log('┌─────────────────┬─────────────┬─────────────┬─────────────┐');
            console.log('│      학생명     │  출석시간   │  수업시간   │  남은시간   │');
            console.log('├─────────────────┼─────────────┼─────────────┼─────────────┤');

            students.forEach(student => {
                const nameCell = student.name.padEnd(15, ' ');
                const timeCell = student.attendanceTime.padEnd(11, ' ');
                const durationCell = `${(student.lessonDuration / (1000 * 60 * 60)).toFixed(1)}시간`.padEnd(11, ' ');
                const remainingCell = student.remaining.formatted.padEnd(11, ' ');

                console.log(`│ ${nameCell} │ ${timeCell} │ ${durationCell} │ ${remainingCell} │`);
            });

            console.log('└─────────────────┴─────────────┴─────────────┴─────────────┘');
        } else {
            console.log('현재 출석한 학생이 없습니다.');
        }

        return students;
    }

    // 자동 모니터링 시작
    function startMonitoring() {
        console.log('자동 출석 모니터링을 시작합니다...');

        // 알림 권한 요청
        requestNotificationPermission();

        // 모니터링 창 생성
        createMonitoringWindow();

        // 즉시 한 번 실행
        findAttendedStudentsWithTime();
        updateMonitoringWindow();

        // 5초마다 데이터 체크
        monitoringInterval = setInterval(() => {
            findAttendedStudentsWithTime();
        }, 5000);

        // 1초마다 UI 업데이트
        uiUpdateInterval = setInterval(() => {
            updateMonitoringWindow();
        }, 1000);

        console.log('자동 모니터링이 시작되었습니다.');
        console.log('- 데이터 체크: 5초마다');
        console.log('- UI 업데이트: 1초마다');
        console.log('- 알림 기능: 활성화');
        console.log('중지하려면 stopMonitoring()을 실행하세요.');
    }

    // 자동 모니터링 중지
    function stopMonitoring() {
        if (monitoringInterval) {
            clearInterval(monitoringInterval);
            monitoringInterval = null;
        }
        if (uiUpdateInterval) {
            clearInterval(uiUpdateInterval);
            uiUpdateInterval = null;
        }
        closeMonitoringWindow();
        console.log('자동 모니터링이 중지되었습니다.');
    }

    // 출석 데이터 초기화
    function resetAttendanceData() {
        attendanceData = {};
        alertedStudents.clear();
        completedStudents.clear();
        saveData(STORAGE_KEY, attendanceData);
        console.log('출석 데이터가 초기화되었습니다.');
        if (monitoringWindow) updateMonitoringWindow();
    }

    // 전역 함수 등록
    window.findAttendedStudentsWithTime = findAttendedStudentsWithTime;
    window.startMonitoring = startMonitoring;
    window.stopMonitoring = stopMonitoring;
    window.resetAttendanceData = resetAttendanceData;
    window.editStudentLessonTime = editStudentLessonTime;
    window.playBeep = playBeep;
    window.playDoubleBeep = playDoubleBeep;
    window.playTripleBeep = playTripleBeep;

    // 스크립트 로드 완료
    console.log('=== 출결 모니터링 Final Version 로드 완료! ===');
    console.log('새로운 기능:');
    console.log('🔔 하원 10분 전 브라우저 알림 + 비프음');
    console.log('🏠 하원 시간 알림 + 삼중 비프음');
    console.log('⚙️ 학생별 수업 시간 설정 (클릭하여 변경)');
    console.log('📊 개선된 UI 및 설정 패널');
    console.log('💾 데이터 내보내기 기능');
    console.log('');
    console.log('시작하려면 startMonitoring()을 실행하세요.');

    // 기존 데이터 알림
    if (Object.keys(attendanceData).length > 0) {
        console.log(`기존 출석 데이터 ${Object.keys(attendanceData).length}건이 로드되었습니다.`);
    }

})();

toolUseResult.content:
// attok.co.kr 출결 모니터링 스크립트 Final Version
// 브라우저 콘솔에 복사-붙여넣기하여 실행
// 최종 기능: 고정 UI창, 알림, 소리, 학생별 수업시간 설정

(function() {
    console.log('=== 출결 모니터링 스크립트 Final Version 시작 ===');

    let monitoringInterval;
    let uiUpdateInterval;
    const DEFAULT_LESSON_DURATION = 2 * 60 * 60 * 1000; // 기본 2시간 (밀리초)
    const STORAGE_KEY = 'attendanceData';
    const SETTINGS_KEY = 'attendanceSettings';
    let monitoringWindow = null;
    let isDragging = false;
    let dragOffset = { x: 0, y: 0 };
    let notificationPermission = false;
    let alertedStudents = new Set(); // 이미 알림을 받은 학생들
    let completedStudents = new Set(); // 수업 완료 알림을 받은 학생들

    // 설정 로드
    let settings = {
        defaultLessonDuration: DEFAULT_LESSON_DURATION,
        notificationsEnabled: true,
        soundEnabled: true,
        warningMinutes: 10
    };

    // 비프음 생성 함수
    function playBeep(frequency = 800, duration = 200, volume = 0.3) {
        if (!settings.soundEnabled) return;

        try {
            const audioContext = new (window.AudioContext || window.webkitAudioContext)();
            const oscillator = audioContext.createOscillator();
            const gainNode = audioContext.createGain();

            oscillator.connect(gainNode);
            gainNode.connect(audioContext.destination);

            oscillator.frequency.value = frequency;
            oscillator.type = 'square';

            gainNode.gain.setValueAtTime(0, audioContext.currentTime);
            gainNode.gain.linearRampToValueAtTime(volume, audioContext.currentTime + 0.01);
            gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + duration / 1000);

            oscillator.start(audioContext.currentTime);
            oscillator.stop(audioContext.currentTime + duration / 1000);
        } catch (e) {
            console.warn('오디오 재생 실패:', e);
        }
    }

    // 이중 비프음
    function playDoubleBeep() {
        playBeep(800, 200);
        setTimeout(() => playBeep(800, 200), 300);
    }

    // 삼중 비프음 (완료 시)
    function playTripleBeep() {
        playBeep(600, 150);
        setTimeout(() => playBeep(800, 150), 200);
        setTimeout(() => playBeep(1000, 150), 400);
    }

    // 브라우저 알림 권한 요청
    async function requestNotificationPermission() {
        if ('Notification' in window) {
            const permission = await Notification.requestPermission();
            notificationPermission = permission === 'granted';
            return notificationPermission;
        }
        return false;
    }

    // 브라우저 알림 표시
    function showNotification(title, body, icon = null) {
        if (!settings.notificationsEnabled || !notificationPermission) return;

        try {
            const notification = new Notification(title, {
                body: body,
                icon: icon || 'data:image/svg+xml;base64,',
                tag: 'attendance-alert',
                requireInteraction: true
            });

            // 3초 후 자동 닫기
            setTimeout(() => notification.close(), 3000);
        } catch (e) {
            console.warn('알림 표시 실패:', e);
        }
    }

