    // localStorage에서 데이터 불러오기
    function loadData(key, defaultValue = {}) {
        try {
            const data = localStorage.getItem(key);
            return data ? JSON.parse(data) : defaultValue;
        } catch (e) {
            console.error(`데이터 로드 실패 (${key}):`, e);
            return defaultValue;
        }
    }

    // localStorage에 데이터 저장
    function saveData(key, data) {
        try {
            localStorage.setItem(key, JSON.stringify(data));
        } catch (e) {
            console.error(`데이터 저장 실패 (${key}):`, e);
        }
    }

    // 출석 데이터 및 설정 초기화
    let attendanceData = loadData(STORAGE_KEY);
    settings = { ...settings, ...loadData(SETTINGS_KEY) };

    // 시간 포맷팅 함수
    function formatTime(date) {
        return date.toLocaleTimeString('ko-KR', {
            hour: '2-digit',
            minute: '2-digit',
            second: '2-digit'
        });
    }

    // 학생별 수업 시간 가져오기
    function getStudentLessonDuration(studentName) {
        const studentData = attendanceData[studentName];
        return studentData?.lessonDuration || settings.defaultLessonDuration;
    }

    // 학생별 수업 시간 설정
    function setStudentLessonDuration(studentName, duration) {
        if (!attendanceData[studentName]) return;
        attendanceData[studentName].lessonDuration = duration;
        saveData(STORAGE_KEY, attendanceData);
    }

    // 남은 시간 계산
    function calculateRemainingTime(startTime, studentName) {
        const lessonDuration = getStudentLessonDuration(studentName);
        const elapsed = new Date().getTime() - startTime;
        const remaining = lessonDuration - elapsed;

        if (remaining <= 0) {
            return {
                hours: 0,
                minutes: 0,
                seconds: 0,
                total: 0,
                formatted: '완료',
                isCompleted: true,
                status: 'completed'
            };
        }

        const hours = Math.floor(remaining / (1000 * 60 * 60));
        const minutes = Math.floor((remaining % (1000 * 60 * 60)) / (1000 * 60));
        const seconds = Math.floor((remaining % (1000 * 60)) / 1000);

        // 경고 시간 체크
        let status = 'normal';
        const warningTime = settings.warningMinutes * 60 * 1000;
        if (remaining <= warningTime) {
            status = 'danger';
        } else if (remaining <= warningTime * 2) {
            status = 'warning';
        }

        return {
            hours,
            minutes,
            seconds,
            total: remaining,
            formatted: `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`,
            isCompleted: false,
            status: status
        };
    }

    // 알림 체크
    function checkNotifications(students) {
        students.forEach(student => {
            const remaining = student.remaining;
            const studentKey = student.name;

            // 하원 완료 알림
            if (remaining.isCompleted && !completedStudents.has(studentKey)) {
                completedStudents.add(studentKey);
                showNotification(
                    '하원 시간입니다!', 
                    `${student.name} 학생의 수업이 완료되었습니다.`
                );
                playTripleBeep();
                console.log(`🏠 ${student.name} - 하원 시간 알림 발송`);
            }

            // 10분 전 경고 알림
            else if (remaining.status === 'danger' && !alertedStudents.has(studentKey)) {
                alertedStudents.add(studentKey);
                showNotification(
                    '하원 10분 전!', 
                    `${student.name} 학생의 하원 시간이 ${Math.ceil(remaining.total / (1000 * 60))}분 남았습니다.`
                );
                playDoubleBeep();
                console.log(`⚠️ ${student.name} - 하원 10분 전 알림 발송`);
            }
        });
    }

