     4→
     5→(function() {
     6→    console.log('=== attok.co.kr 출결 모니터링 스크립트 시작 ===');
     7→    
     8→    let monitoringInterval;
     9→    let uiUpdateInterval;
    10→    const DEFAULT_LESSON_DURATION = 2 * 60 * 60 * 1000; // 기본 2시간 (밀리초)
    11→    const STORAGE_KEY = 'attendanceData';
    12→    const SETTINGS_KEY = 'attendanceSettings';
