    19→    
    20→    // 설정 로드
    21→    let settings = {
    22→        defaultLessonDuration: DEFAULT_LESSON_DURATION,
    23→        notificationsEnabled: true,
    24→        soundEnabled: true,
    25→        warningMinutes: 10
    26→    };
    27→    
    28→    // attok.co.kr 특화 선택자 및 감지 함수들
    29→    const ATTOK_SELECTORS = {
    30→        // 일반적인 테이블 구조
    31→        studentRows: [
    32→            'table tr[bgcolor]',           // bgcolor 속성이 있는 tr
    33→            'table tr td[bgcolor]',        // bgcolor 속성이 있는 td의 부모 tr
    34→            'table tr',                    // 모든 테이블 행
    35→            '.student-row',                // 클래스명이 있는 경우
    36→            '[data-student]',              // data 속성이 있는 경우
    37→            'tr:has(td)',                  // td를 포함한 tr (최신 브라우저)
    38→        ],
    39→        
    40→        // 학생 이름을 찾을 수 있는 셀
    41→        nameSelectors: [
    42→            'td:nth-child(2)',             // 두 번째 열 (번호 다음)
    43→            'td:nth-child(3)',             // 세 번째 열
    44→            'td:first-child + td',         // 첫 번째 다음 열
    45→            'td[align="center"]',          // 중앙 정렬된 셀
    46→            'td:contains("학생")',         // "학생"이라는 텍스트 포함
    47→            'td',                          // 모든 td
    48→        ],
    49→        
    50→        // 출석 상태를 나타내는 배경색들
    51→        attendedColors: [
    52→            'rgb(135, 206, 235)',          // skyblue RGB
    53→            'skyblue',                     // skyblue 키워드
    54→            '#87ceeb',                     // skyblue HEX
    55→            'lightblue',                   // 연한 파란색
    56→            'rgb(173, 216, 230)',          // lightblue RGB
    57→            '#add8e6',                     // lightblue HEX
    58→            'rgb(176, 224, 230)',          // powderblue RGB
    59→            '#b0e0e6',                     // powderblue HEX
    60→        ]
    61→    };
    62→    
    63→    // 비프음 생성 함수
    64→    function playBeep(frequency = 800, duration = 200, volume = 0.3) {
    65→        if (!settings.soundEnabled) return;
    66→        
    67→        try {
    68→            const audioContext = new (window.AudioContext || window.webkitAudioContext)();
    69→            const oscillator = audioContext.createOscillator();
    70→            const gainNode = audioContext.createGain();
    71→            
    72→            oscillator.connect(gainNode);
    73→            gainNode.connect(audioContext.destination);
    74→            
    75→            oscillator.frequency.value = frequency;
    76→            oscillator.type = 'square';
    77→            
    78→            gainNode.gain.setValueAtTime(0, audioContext.currentTime);
    79→            gainNode.gain.linearRampToValueAtTime(volume, audioContext.currentTime + 0.01);
    80→            gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + duration / 1000);
    81→            
    82→            oscillator.start(audioContext.currentTime);
    83→            oscillator.stop(audioContext.currentTime + duration / 1000);
    84→        } catch (e) {
    85→            console.warn('오디오 재생 실패:', e);
    86→        }
    87→    }
    88→    
    89→    function playDoubleBeep() {
    90→        playBeep(800, 200);
    91→        setTimeout(() => playBeep(800, 200), 300);
    92→    }
    93→    
    94→    function playTripleBeep() {
    95→        playBeep(600, 150);
    96→        setTimeout(() => playBeep(800, 150), 200);
    97→        setTimeout(() => playBeep(1000, 150), 400);
    98→    }
    99→    
   100→    // 브라우저 알림 권한 요청
   101→    async function requestNotificationPermission() {
   102→        if ('Notification' in window) {
   103→            const permission = await Notification.requestPermission();
   104→            notificationPermission = permission === 'granted';
   105→            return notificationPermission;
   106→        }
   107→        return false;
   108→    }
   109→    
   110→    // 브라우저 알림 표시
   111→    function showNotification(title, body, icon = null) {
   112→        if (!settings.notificationsEnabled || !notificationPermission) return;
   113→        
   114→        try {
   115→            const notification = new Notification(title, {
   116→                body: body,
   117→                icon: icon || 'data:image/svg+xml;base64,',
   118→                tag: 'attendance-alert',
   119→                requireInteraction: true
   120→            });
   121→            
   122→            setTimeout(() => notification.close(), 3000);
   123→        } catch (e) {
   124→            console.warn('알림 표시 실패:', e);
   125→        }
   126→    }
   127→    
   128→    // attok.co.kr 특화 배경색 감지 함수
   129→    function isAttendedByBackground(element) {
   130→        const computedStyle = window.getComputedStyle(element);
   131→        const bgColor = computedStyle.backgroundColor;
   132→        
   133→        // 여러 방식으로 출석 배경색 감지
   134→        return ATTOK_SELECTORS.attendedColors.some(color => {
   135→            if (color.startsWith('rgb')) {
   136→                return bgColor === color;
   137→            } else if (color.startsWith('#')) {
   138→                // HEX를 RGB로 변환하여 비교
   139→                const rgb = hexToRgb(color);
   140→                return rgb && bgColor === `rgb(${rgb.r}, ${rgb.g}, ${rgb.b})`;
   141→            } else {
   142→                return bgColor.toLowerCase().includes(color.toLowerCase());
   143→            }
   144→        });
   145→    }
   146→    
   147→    // HEX를 RGB로 변환
   148→    function hexToRgb(hex) {
   149→        const result = /^#?([a-f\\d]{2})([a-f\\d]{2})([a-f\\d]{2})$/i.exec(hex);
   150→        return result ? {
   151→            r: parseInt(result[1], 16),
   152→            g: parseInt(result[2], 16),
   153→            b: parseInt(result[3], 16)
   154→        } : null;
   155→    }
   156→    
   157→    // attok.co.kr 특화 학생 이름 추출 함수
   158→    function extractStudentName(element) {
   159→        // 1. data 속성에서 먼저 확인
   160→        if (element.dataset) {
   161→            if (element.dataset.name) return element.dataset.name;
   162→            if (element.dataset.student) return element.dataset.student;
   163→            if (element.dataset.studentName) return element.dataset.studentName;
   164→        }
   165→        
   166→        // 2. 클래스나 특정 선택자로 이름 찾기
   167→        for (const selector of ATTOK_SELECTORS.nameSelectors) {
   168→            try {
   169→                const nameElement = element.querySelector(selector);
   170→                if (nameElement) {
   171→                    const text = nameElement.textContent.trim();
   172→                    if (text && text !== '학생명' && text !== '이름' && text !== '성명') {
   173→                        // 한글 이름 패턴 매칭 (2-4글자)
   174→                        const koreanNameMatch = text.match(/[가-힣]{2,4}/);
   175→                        if (koreanNameMatch) {
   176→                            return koreanNameMatch[0];
   177→                        }
   178→                        
   179→                        // 숫자가 아닌 첫 번째 단어
   180→                        const words = text.split(/\\s+/);
   181→                        for (const word of words) {
   182→                            if (!/^\\d+$/.test(word) && word.length >= 2) {
   183→                                return word;
   184→                            }
   185→                        }
   186→                    }
   187→                }
   188→            } catch (e) {
   189→                // 선택자 오류 무시하고 계속
   190→                continue;
   191→            }
   192→        }
   193→        
   194→        // 3. 전체 텍스트에서 한글 이름 패턴 찾기
   195→        const fullText = element.textContent.trim();
   196→        const koreanNameMatch = fullText.match(/[가-힣]{2,4}/);
   197→        if (koreanNameMatch) {
   198→            return koreanNameMatch[0];
   199→        }
   200→        
   201→        // 4. 마지막 수단: 첫 번째 의미있는 단어
   202→        const words = fullText.split(/\\s+/);
   203→        for (const word of words) {
   204→            if (word && !/^\\d+$/.test(word) && word.length >= 2) {
   205→                return word;
   206→            }
   207→        }
   208→        
   209→        return null;
   210→    }
   211→    
   212→    // attok.co.kr 특화 학생 행 찾기 함수
   213→    function findStudentRows() {
   214→        const studentRows = [];
   215→        
   216→        // 각 선택자를 시도해서 학생 행 찾기
   217→        for (const selector of ATTOK_SELECTORS.studentRows) {
   218→            try {
   219→                const elements = document.querySelectorAll(selector);
   220→                
   221→                for (const element of elements) {
   222→                    // 이미 추가된 요소는 건너뛰기
   223→                    if (studentRows.includes(element)) continue;
   224→                    
   225→                    // 학생 이름이 있는지 확인
   226→                    const studentName = extractStudentName(element);
   227→                    if (studentName) {
   228→                        studentRows.push(element);
   229→                    }
   230→                }
   231→                
   232→                // 충분한 행을 찾으면 중단
   233→                if (studentRows.length >= 3) break;
   234→                
   235→            } catch (e) {
   236→                console.warn(`선택자 오류 (${selector}):`, e);
   237→                continue;
   238→            }
   239→        }
   240→        
   241→        console.log(`학생 행 ${studentRows.length}개 발견:`, 
   242→                   studentRows.map(row => extractStudentName(row)).filter(Boolean));
   243→        
   244→        return studentRows;
   245→    }
