   618→    // localStorage 관련 함수들 (기존과 동일)
   619→    function loadData(key, defaultValue = {}) {
   620→        try {
   621→            const data = localStorage.getItem(key);
   622→            return data ? JSON.parse(data) : defaultValue;
   623→        } catch (e) {
   624→            console.error(`데이터 로드 실패 (${key}):`, e);
   625→            return defaultValue;
   626→        }
   627→    }
   628→    
   629→    function saveData(key, data) {
   630→        try {
   631→            localStorage.setItem(key, JSON.stringify(data));
   632→        } catch (e) {
   633→            console.error(`데이터 저장 실패 (${key}):`, e);
   634→        }
   635→    }
