   641→    function formatTime(date) {
   642→        return date.toLocaleTimeString('ko-KR', {
   643→            hour: '2-digit',
   644→            minute: '2-digit',
   645→            second: '2-digit'
   646→        });
   647→    }
   648→    
   649→    function getStudentLessonDuration(studentName) {
   650→        const studentData = attendanceData[studentName];
   651→        return studentData?.lessonDuration || settings.defaultLessonDuration;
   652→    }
   653→    
   654→    function setStudentLessonDuration(studentName, duration) {
   655→        if (!attendanceData[studentName]) return;
   656→        attendanceData[studentName].lessonDuration = duration;
   657→        saveData(STORAGE_KEY, attendanceData);
   658→    }
   659→    
   660→    function calculateRemainingTime(startTime, studentName) {
   661→        const lessonDuration = getStudentLessonDuration(studentName);
   662→        const elapsed = new Date().getTime() - startTime;
   663→        const remaining = lessonDuration - elapsed;
   664→        
   665→        if (remaining <= 0) {
   666→            return {
   667→                hours: 0,
   668→                minutes: 0,
   669→                seconds: 0,
   670→                total: 0,
   671→                formatted: '완료',
   672→                isCompleted: true,
   673→                status: 'completed'
   674→            };
   675→        }
   676→        
   677→        const hours = Math.floor(remaining / (1000 * 60 * 60));
   678→        const minutes = Math.floor((remaining % (1000 * 60 * 60)) / (1000 * 60));
   679→        const seconds = Math.floor((remaining % (1000 * 60)) / 1000);
   680→        
   681→        let status = 'normal';
   682→        const warningTime = settings.warningMinutes * 60 * 1000;
   683→        if (remaining <= warningTime) {
   684→            status = 'danger';
   685→        } else if (remaining <= warningTime * 2) {
   686→            status = 'warning';
   687→        }
   688→        
   689→        return {
   690→            hours,
   691→            minutes,
   692→            seconds,
   693→            total: remaining,
   694→            formatted: `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`,
   695→            isCompleted: false,
   696→            status: status
   697→        };
   698→    }
   699→    
   700→    function checkNotifications(students) {
   701→        students.forEach(student => {
   702→            const remaining = student.remaining;
   703→            const studentKey = student.name;
   704→            
   705→            if (remaining.isCompleted && !completedStudents.has(studentKey)) {
   706→                completedStudents.add(studentKey);
   707→                showNotification(
   708→                    '하원 시간입니다!', 
   709→                    `${student.name} 학생의 수업이 완료되었습니다.`
   710→                );
   711→                playTripleBeep();
   712→                console.log(`🏠 ${student.name} - 하원 시간 알림 발송`);
   713→            }
   714→            else if (remaining.status === 'danger' && !alertedStudents.has(studentKey)) {
   715→                alertedStudents.add(studentKey);
   716→                showNotification(
   717→                    '하원 10분 전!', 
   718→                    `${student.name} 학생의 하원 시간이 ${Math.ceil(remaining.total / (1000 * 60))}분 남았습니다.`
   719→                );
   720→                playDoubleBeep();
   721→                console.log(`⚠️ ${student.name} - 하원 10분 전 알림 발송`);
   722→            }
   723→        });
   724→    }
