   726→    // attok.co.kr 특화 현재 출석 학생 정보 가져오기 함수
   727→    function getCurrentAttendedStudents() {
   728→        const studentRows = findStudentRows();
   729→        let currentAttended = [];
   730→        const now = new Date();
   731→        
   732→        console.log(`총 ${studentRows.length}개의 학생 행을 분석합니다...`);
   733→        
   734→        studentRows.forEach((row, index) => {
   735→            const studentName = extractStudentName(row);
   736→            if (!studentName) {
   737→                console.log(`행 ${index + 1}: 학생 이름을 찾을 수 없음`);
   738→                return;
   739→            }
   740→            
   741→            // 출석 상태 확인 (여러 방법 시도)
   742→            let isAttended = false;
   743→            
   744→            // 1. 행 전체 배경색 확인
   745→            isAttended = isAttendedByBackground(row);
   746→            
   747→            // 2. 각 셀의 배경색 확인
   748→            if (!isAttended) {
   749→                const cells = row.querySelectorAll('td');
   750→                for (const cell of cells) {
   751→                    if (isAttendedByBackground(cell)) {
   752→                        isAttended = true;
   753→                        break;
   754→                    }
   755→                }
   756→            }
   757→            
   758→            // 3. bgcolor 속성 확인
   759→            if (!isAttended) {
   760→                const bgColorAttr = row.getAttribute('bgcolor') || '';
   761→                if (bgColorAttr) {
   762→                    isAttended = ATTOK_SELECTORS.attendedColors.some(color => 
   763→                        bgColorAttr.toLowerCase().includes(color.toLowerCase()) ||
   764→                        bgColorAttr === color
   765→                    );
   766→                }
   767→                
   768→                // 각 셀의 bgcolor 속성도 확인
   769→                if (!isAttended) {
   770→                    const cells = row.querySelectorAll('td[bgcolor]');
   771→                    for (const cell of cells) {
   772→                        const cellBgColor = cell.getAttribute('bgcolor') || '';
   773→                        if (ATTOK_SELECTORS.attendedColors.some(color => 
   774→                            cellBgColor.toLowerCase().includes(color.toLowerCase()) ||
   775→                            cellBgColor === color
   776→                        )) {
   777→                            isAttended = true;
   778→                            break;
   779→                        }
   780→                    }
   781→                }
   782→            }
   783→            
   784→            console.log(`행 ${index + 1}: ${studentName} - ${isAttended ? '출석' : '미출석'}`);
   785→            
   786→            if (isAttended) {
   787→                if (!attendanceData[studentName]) {
   788→                    attendanceData[studentName] = {
   789→                        attendanceTime: now.getTime(),
   790→                        attendanceTimeFormatted: formatTime(now),
   791→                        lessonDuration: settings.defaultLessonDuration
   792→                    };
   793→                    saveData(STORAGE_KEY, attendanceData);
   794→                    console.log(`새로운 출석 등록: ${studentName}`);
   795→                }
   796→                
   797→                const studentData = attendanceData[studentName];
   798→                const remaining = calculateRemainingTime(studentData.attendanceTime, studentName);
   799→                
   800→                currentAttended.push({
   801→                    name: studentName,
   802→                    attendanceTime: studentData.attendanceTimeFormatted,
   803→                    remaining: remaining,
   804→                    isCompleted: remaining.isCompleted,
   805→                    lessonDuration: getStudentLessonDuration(studentName)
   806→                });
   807→            }
   808→        });
   809→        
   810→        return currentAttended;
   811→    }
