  1003→    // 드래그 관련 함수들 (기존과 동일)
  1004→    function startDrag(e) {
  1005→        isDragging = true;
  1006→        const rect = monitoringWindow.getBoundingClientRect();
  1007→        dragOffset.x = e.clientX - rect.left;
  1008→        dragOffset.y = e.clientY - rect.top;
  1009→        monitoringWindow.style.cursor = 'grabbing';
  1010→    }
  1011→    
  1012→    function drag(e) {
  1013→        if (!isDragging) return;
  1014→        e.preventDefault();
  1015→        const x = e.clientX - dragOffset.x;
  1016→        const y = e.clientY - dragOffset.y;
  1017→        const maxX = window.innerWidth - monitoringWindow.offsetWidth;
  1018→        const maxY = window.innerHeight - monitoringWindow.offsetHeight;
  1019→        const boundedX = Math.max(0, Math.min(x, maxX));
  1020→        const boundedY = Math.max(0, Math.min(y, maxY));
  1021→        monitoringWindow.style.left = boundedX + 'px';
  1022→        monitoringWindow.style.top = boundedY + 'px';
  1023→        monitoringWindow.style.right = 'auto';
  1024→        monitoringWindow.style.bottom = 'auto';
  1025→    }
  1026→    
  1027→    function stopDrag() {
  1028→        isDragging = false;
  1029→        if (monitoringWindow) monitoringWindow.style.cursor = 'default';
  1030→    }
