  1090→    function editStudentLessonTime(studentName) {
  1091→        const currentDuration = getStudentLessonDuration(studentName) / (1000 * 60 * 60);
  1092→        const newDuration = prompt(`${studentName}의 수업 시간을 입력하세요 (시간):`, currentDuration);
