    // localStorage 관련 함수들 (기존과 동일)
    function loadData(key, defaultValue = {}) {
        try {
            const data = localStorage.getItem(key);
            return data ? JSON.parse(data) : defaultValue;
        } catch (e) {
            console.error(`데이터 로드 실패 (${key}):`, e);
            return defaultValue;
        }
    }

    function saveData(key, data) {
        try {
            localStorage.setItem(key, JSON.stringify(data));
        } catch (e) {
            console.error(`데이터 저장 실패 (${key}):`, e);
        }
    }

    // 출석 데이터 및 설정 초기화
    let attendanceData = loadData(STORAGE_KEY);
    settings = { ...settings, ...loadData(SETTINGS_KEY) };

    function formatTime(date) {
        return date.toLocaleTimeString('ko-KR', {
            hour: '2-digit',
            minute: '2-digit',
            second: '2-digit'
        });
    }

    function getStudentLessonDuration(studentName) {
        const studentData = attendanceData[studentName];
        return studentData?.lessonDuration || settings.defaultLessonDuration;
    }

    function setStudentLessonDuration(studentName, duration) {
        if (!attendanceData[studentName]) return;
        attendanceData[studentName].lessonDuration = duration;
        saveData(STORAGE_KEY, attendanceData);
    }

    function calculateRemainingTime(startTime, studentName) {
        const lessonDuration = getStudentLessonDuration(studentName);
        const elapsed = new Date().getTime() - startTime;
        const remaining = lessonDuration - elapsed;

        if (remaining <= 0) {
            return {
                hours: 0,
                minutes: 0,
                seconds: 0,
                total: 0,
                formatted: '완료',
                isCompleted: true,
                status: 'completed'
            };
        }

        const hours = Math.floor(remaining / (1000 * 60 * 60));
        const minutes = Math.floor((remaining % (1000 * 60 * 60)) / (1000 * 60));
        const seconds = Math.floor((remaining % (1000 * 60)) / 1000);

        let status = 'normal';
        const warningTime = settings.warningMinutes * 60 * 1000;
        if (remaining <= warningTime) {
            status = 'danger';
        } else if (remaining <= warningTime * 2) {
            status = 'warning';
        }

        return {
            hours,
            minutes,
            seconds,
            total: remaining,
            formatted: `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`,
            isCompleted: false,
            status: status
        };
    }

    function checkNotifications(students) {
        students.forEach(student => {
            const remaining = student.remaining;
            const studentKey = student.name;

            if (remaining.isCompleted && !completedStudents.has(studentKey)) {
                completedStudents.add(studentKey);
                showNotification(
                    '하원 시간입니다!', 
                    `${student.name} 학생의 수업이 완료되었습니다.`
                );
                playTripleBeep();
                console.log(`🏠 ${student.name} - 하원 시간 알림 발송`);
            }
            else if (remaining.status === 'danger' && !alertedStudents.has(studentKey)) {
                alertedStudents.add(studentKey);
                showNotification(
                    '하원 10분 전!', 
                    `${student.name} 학생의 하원 시간이 ${Math.ceil(remaining.total / (1000 * 60))}분 남았습니다.`
                );
                playDoubleBeep();
                console.log(`⚠️ ${student.name} - 하원 10분 전 알림 발송`);
            }
        });
    }

    // attok.co.kr 특화 현재 출석 학생 정보 가져오기 함수
    function getCurrentAttendedStudents() {
        const studentRows = findStudentRows();
        let currentAttended = [];
        const now = new Date();

        console.log(`총 ${studentRows.length}개의 학생 행을 분석합니다...`);

        studentRows.forEach((row, index) => {
            const studentName = extractStudentName(row);
            if (!studentName) {
                console.log(`행 ${index + 1}: 학생 이름을 찾을 수 없음`);
                return;
            }

            // 출석 상태 확인 (여러 방법 시도)
            let isAttended = false;

            // 1. 행 전체 배경색 확인
            isAttended = isAttendedByBackground(row);

            // 2. 각 셀의 배경색 확인
            if (!isAttended) {
                const cells = row.querySelectorAll('td');
                for (const cell of cells) {
                    if (isAttendedByBackground(cell)) {
                        isAttended = true;
                        break;
                    }
                }
            }

            // 3. bgcolor 속성 확인
            if (!isAttended) {
                const bgColorAttr = row.getAttribute('bgcolor') || '';
                if (bgColorAttr) {
                    isAttended = ATTOK_SELECTORS.attendedColors.some(color => 
                        bgColorAttr.toLowerCase().includes(color.toLowerCase()) ||
                        bgColorAttr === color
                    );
                }

                // 각 셀의 bgcolor 속성도 확인
                if (!isAttended) {
                    const cells = row.querySelectorAll('td[bgcolor]');
                    for (const cell of cells) {
                        const cellBgColor = cell.getAttribute('bgcolor') || '';
                        if (ATTOK_SELECTORS.attendedColors.some(color => 
                            cellBgColor.toLowerCase().includes(color.toLowerCase()) ||
                            cellBgColor === color
                        )) {
                            isAttended = true;
                            break;
                        }
                    }
                }
            }

            console.log(`행 ${index + 1}: ${studentName} - ${isAttended ? '출석' : '미출석'}`);

            if (isAttended) {
                if (!attendanceData[studentName]) {
                    attendanceData[studentName] = {
                        attendanceTime: now.getTime(),
                        attendanceTimeFormatted: formatTime(now),
                        lessonDuration: settings.defaultLessonDuration
                    };
                    saveData(STORAGE_KEY, attendanceData);
                    console.log(`새로운 출석 등록: ${studentName}`);
                }

                const studentData = attendanceData[studentName];
                const remaining = calculateRemainingTime(studentData.attendanceTime, studentName);

                currentAttended.push({
                    name: studentName,
                    attendanceTime: studentData.attendanceTimeFormatted,
                    remaining: remaining,
                    isCompleted: remaining.isCompleted,
                    lessonDuration: getStudentLessonDuration(studentName)
                });
            }
        });

        return currentAttended;
    }

