    function startMonitoring() {
        console.log('=== attok.co.kr 자동 출석 모니터링 시작 ===');

        requestNotificationPermission();
        createMonitoringWindow();

        // 즉시 한 번 실행
        const initialStudents = findAttendedStudentsWithTime();
        updateMonitoringWindow();

        if (initialStudents.length === 0) {
            console.warn('⚠️ 출석한 학생이 발견되지 않았습니다.');
            console.log('페이지 HTML 구조를 확인하거나 새로고침을 시도해보세요.');
        }

        monitoringInterval = setInterval(() => {
            findAttendedStudentsWithTime();
        }, 5000);

        uiUpdateInterval = setInterval(() => {
            updateMonitoringWindow();
        }, 1000);

        console.log('✅ 자동 모니터링이 시작되었습니다.');
        console.log('- 데이터 체크: 5초마다');
        console.log('- UI 업데이트: 1초마다');
        console.log('- 알림 기능: 활성화');
        console.log('중지하려면 stopMonitoring()을 실행하세요.');
    }

    function stopMonitoring() {
        if (monitoringInterval) {
            clearInterval(monitoringInterval);
            monitoringInterval = null;
        }
        if (uiUpdateInterval) {
            clearInterval(uiUpdateInterval);
            uiUpdateInterval = null;
        }
        closeMonitoringWindow();
        console.log('❌ 자동 모니터링이 중지되었습니다.');
    }

    function resetAttendanceData() {
        attendanceData = {};
        alertedStudents.clear();
        completedStudents.clear();
        saveData(STORAGE_KEY, attendanceData);
        console.log('🗑️ 출석 데이터가 초기화되었습니다.');
        if (monitoringWindow) updateMonitoringWindow();
    }

    // 디버그 함수들
    function debugPageStructure() {
        console.log('\\n=== attok.co.kr 페이지 구조 분석 ===');

        // 모든 테이블 찾기
        const tables = document.querySelectorAll('table');
        console.log(`발견된 테이블: ${tables.length}개`);

        tables.forEach((table, index) => {
            const rows = table.querySelectorAll('tr');
            console.log(`테이블 ${index + 1}: ${rows.length}개 행`);

            rows.forEach((row, rowIndex) => {
                const cells = row.querySelectorAll('td');
                if (cells.length > 0) {
                    const firstCellText = cells[0]?.textContent?.trim() || '';
                    const secondCellText = cells[1]?.textContent?.trim() || '';
                    const bgColor = window.getComputedStyle(row).backgroundColor;
                    const bgColorAttr = row.getAttribute('bgcolor') || '';

                    console.log(`  행 ${rowIndex + 1}: ${cells.length}개 셀, "${firstCellText}" | "${secondCellText}", 배경색: ${bgColor}, bgcolor속성: ${bgColorAttr}`);
                }
            });
        });

        // 학생 행 찾기 시도
        const studentRows = findStudentRows();
        console.log(`\\n학생 행으로 인식된 요소: ${studentRows.length}개`);
        studentRows.forEach((row, index) => {
            const name = extractStudentName(row);
            const isAttended = isAttendedByBackground(row);
            console.log(`  ${index + 1}. ${name} - ${isAttended ? '출석' : '미출석'}`);
        });
    }

    // 전역 함수 등록
    window.findAttendedStudentsWithTime = findAttendedStudentsWithTime;
    window.startMonitoring = startMonitoring;
    window.stopMonitoring = stopMonitoring;
    window.resetAttendanceData = resetAttendanceData;
    window.editStudentLessonTime = editStudentLessonTime;
    window.debugPageStructure = debugPageStructure;
    window.playBeep = playBeep;
    window.playDoubleBeep = playDoubleBeep;
    window.playTripleBeep = playTripleBeep;

    // 스크립트 로드 완료
    console.log('=== attok.co.kr 출결 모니터링 스크립트 로드 완료! ===');
    console.log('🎯 실제 attok.co.kr 사이트에 최적화됨');
    console.log('');
    console.log('📋 사용 가능한 함수:');
    console.log('  • startMonitoring() - 모니터링 시작');
    console.log('  • stopMonitoring() - 모니터링 중지');
    console.log('  • findAttendedStudentsWithTime() - 수동 체크');
    console.log('  • debugPageStructure() - 페이지 구조 분석');
    console.log('  • resetAttendanceData() - 데이터 초기화');
    console.log('');
    console.log('🔧 문제 해결:');
    console.log('  1. 학생이 감지되지 않으면: debugPageStructure() 실행');
    console.log('  2. HTML 구조 확인 후 선택자 수정 요청');
    console.log('  3. 실제 HTML 코드를 제공하면 정확한 수정 가능');
    console.log('');
    console.log('▶️ 시작하려면: startMonitoring()');

    // 기존 데이터 알림
    if (Object.keys(attendanceData).length > 0) {
        console.log(`📁 기존 출석 데이터 ${Object.keys(attendanceData).length}건이 로드되었습니다.`);
    }

    // 자동으로 페이지 구조 분석 (디버깅용)
    setTimeout(() => {
        debugPageStructure();
    }, 1000);

})();

