/* AI 학습 플랫폼 메인 스타일 */

/* 기본 설정 */
:root {
    /* 컬러 팔레트 */
    --primary-color: #4f46e5;
    --primary-dark: #3730a3;
    --secondary-color: #06b6d4;
    --success-color: #10b981;
    --warning-color: #f59e0b;
    --danger-color: #ef4444;

    /* 배경색 */
    --bg-primary: #ffffff;
    --bg-secondary: #f8fafc;
    --bg-tertiary: #f1f5f9;
    --bg-dark: #1e293b;

    /* 텍스트 색상 */
    --text-primary: #1e293b;
    --text-secondary: #64748b;
    --text-muted: #94a3b8;
    --text-white: #ffffff;

    /* 보더 */
    --border-color: #e2e8f0;
    --border-radius: 8px;
    --border-radius-lg: 12px;

    /* 그림자 */
    --shadow-sm: 0 1px 2px 0 rgb(0 0 0 / 0.05);
    --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.1);
    --shadow-lg: 0 10px 15px -3px rgb(0 0 0 / 0.1);

    /* 폰트 */
    --font-family: 'Noto Sans KR', -apple-system, BlinkMacSystemFont, sans-serif;
    --font-size-xs: 0.75rem;
    --font-size-sm: 0.875rem;
    --font-size-base: 1rem;
    --font-size-lg: 1.125rem;
    --font-size-xl: 1.25rem;
    --font-size-2xl: 1.5rem;
    --font-size-3xl: 1.875rem;

    /* 트랜지션 */
    --transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

/* 다크 모드 */
[data-theme="dark"] {
    --bg-primary: #0f172a;
    --bg-secondary: #1e293b;
    --bg-tertiary: #334155;
    --text-primary: #f1f5f9;
    --text-secondary: #cbd5e1;
    --text-muted: #94a3b8;
    --border-color: #334155;
}

/* 기본 스타일 리셋 */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: var(--font-family);
    font-size: var(--font-size-base);
    line-height: 1.5;
    color: var(--text-primary);
    background-color: var(--bg-secondary);
    transition: var(--transition);
}

/* 스크롤바 스타일 */
::-webkit-scrollbar {
    width: 8px;
    height: 8px;
}

::-webkit-scrollbar-track {
    background: var(--bg-tertiary);
}

::-webkit-scrollbar-thumb {
    background: var(--border-color);
    border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
    background: var(--text-muted);
}

/* 로딩 화면 */
.loading-screen {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: var(--bg-primary);
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    z-index: 9999;
    transition: opacity 0.5s ease;
}

.loading-screen.hidden {
    opacity: 0;
    pointer-events: none;
}

.spinner {
    width: 48px;
    height: 48px;
    border: 4px solid var(--border-color);
    border-top: 4px solid var(--primary-color);
    border-radius: 50%;
    animation: spin 1s linear infinite;
    margin-bottom: 16px;
}

@keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

/* 메인 네비게이션 */
.main-nav {
    background: var(--bg-primary);
    border-bottom: 1px solid var(--border-color);
    padding: 0 24px;
    height: 64px;
    display: flex;
    align-items: center;
    justify-content: space-between;
    position: sticky;
    top: 0;
    z-index: 100;
    box-shadow: var(--shadow-sm);
}

.nav-brand {
    display: flex;
    align-items: center;
    gap: 12px;
    font-size: var(--font-size-xl);
    font-weight: 700;
    color: var(--primary-color);
}

.nav-brand i {
    font-size: 28px;
}

.nav-controls {
    display: flex;
    align-items: center;
    gap: 24px;
}

/* 사용자 선택기 */
.user-selector {
    display: flex;
    align-items: center;
    gap: 8px;
}

.user-selector select {
    padding: 8px 12px;
    border: 1px solid var(--border-color);
    border-radius: var(--border-radius);
    background: var(--bg-primary);
    color: var(--text-primary);
    min-width: 150px;
    font-size: var(--font-size-sm);
}

/* 과목 탭 */
.subject-tabs {
    display: flex;
    gap: 4px;
}

.subject-tab {
    padding: 8px 16px;
    border: 1px solid var(--border-color);
    border-radius: var(--border-radius);
    background: var(--bg-secondary);
    color: var(--text-secondary);
    text-decoration: none;
    font-size: var(--font-size-sm);
    transition: var(--transition);
    cursor: pointer;
}

.subject-tab:hover {
    background: var(--bg-tertiary);
}

.subject-tab.active {
    background: var(--primary-color);
    color: var(--text-white);
    border-color: var(--primary-color);
}

/* 버튼 스타일 */
.btn-icon {
    width: 40px;
    height: 40px;
    border: none;
    border-radius: var(--border-radius);
    background: var(--bg-secondary);
    color: var(--text-secondary);
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    transition: var(--transition);
}

.btn-icon:hover {
    background: var(--bg-tertiary);
    color: var(--text-primary);
}

.btn-primary {
    background: var(--primary-color);
    color: var(--text-white);
    border: none;
    padding: 12px 24px;
    border-radius: var(--border-radius);
    font-size: var(--font-size-sm);
    font-weight: 500;
    cursor: pointer;
    transition: var(--transition);
    display: inline-flex;
    align-items: center;
    gap: 8px;
}

.btn-primary:hover {
    background: var(--primary-dark);
    box-shadow: var(--shadow-md);
}

.btn-secondary {
    background: var(--bg-secondary);
    color: var(--text-primary);
    border: 1px solid var(--border-color);
    padding: 12px 24px;
    border-radius: var(--border-radius);
    font-size: var(--font-size-sm);
    font-weight: 500;
    cursor: pointer;
    transition: var(--transition);
    display: inline-flex;
    align-items: center;
    gap: 8px;
}

.btn-secondary:hover {
    background: var(--bg-tertiary);
    box-shadow: var(--shadow-sm);
}

.btn-danger {
    background: var(--danger-color);
    color: var(--text-white);
    border: none;
    padding: 12px 24px;
    border-radius: var(--border-radius);
    font-size: var(--font-size-sm);
    font-weight: 500;
    cursor: pointer;
    transition: var(--transition);
    display: inline-flex;
    align-items: center;
    gap: 8px;
}

.btn-danger:hover {
    background: #dc2626;
    box-shadow: var(--shadow-md);
}

/* 메인 컨텐츠 */
.main-content {
    padding: 24px;
    max-width: 1400px;
    margin: 0 auto;
}

/* 화면 전환 */
.screen {
    display: none;
    animation: fadeIn 0.3s ease;
}

.screen.active {
    display: block;
}

@keyframes fadeIn {
    from { opacity: 0; transform: translateY(10px); }
    to { opacity: 1; transform: translateY(0); }
}

/* 대시보드 */
.dashboard-grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 24px;
    margin-bottom: 24px;
}

.card {
    background: var(--bg-primary);
    border-radius: var(--border-radius-lg);
    padding: 24px;
    box-shadow: var(--shadow-sm);
    border: 1px solid var(--border-color);
    transition: var(--transition);
}

.card:hover {
    box-shadow: var(--shadow-md);
}

.card h2 {
    font-size: var(--font-size-xl);
    font-weight: 600;
    margin-bottom: 20px;
    display: flex;
    align-items: center;
    gap: 12px;
    color: var(--text-primary);
}

.card h2 i {
    color: var(--primary-color);
}

/* 파일 업로드 영역 */
.upload-area {
    grid-row: span 2;
}

.drop-zone {
    border: 2px dashed var(--border-color);
    border-radius: var(--border-radius-lg);
    padding: 48px 24px;
    text-align: center;
    cursor: pointer;
    transition: var(--transition);
    position: relative;
}

.drop-zone:hover {
    border-color: var(--primary-color);
    background: var(--bg-secondary);
}

.drop-zone.dragover {
    border-color: var(--primary-color);
    background: rgba(79, 70, 229, 0.05);
}

.drop-zone i {
    font-size: 48px;
    color: var(--text-muted);
    margin-bottom: 16px;
}

.drop-zone p {
    color: var(--text-secondary);
    margin-bottom: 24px;
}

.drop-zone input[type="file"] {
    position: absolute;
    opacity: 0;
    pointer-events: none;
}

.file-list {
    margin-top: 24px;
}

.file-item {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 12px;
    background: var(--bg-secondary);
    border-radius: var(--border-radius);
    margin-bottom: 8px;
    border: 1px solid var(--border-color);
}

.file-info {
    display: flex;
    align-items: center;
    gap: 12px;
}

.file-icon {
    width: 32px;
    height: 32px;
    background: var(--primary-color);
    border-radius: var(--border-radius);
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--text-white);
}

.file-details h4 {
    font-size: var(--font-size-sm);
    font-weight: 500;
    margin-bottom: 2px;
}

.file-details p {
    font-size: var(--font-size-xs);
    color: var(--text-muted);
}

.file-actions {
    display: flex;
    gap: 8px;
}

.file-actions button {
    width: 32px;
    height: 32px;
    border: none;
    border-radius: var(--border-radius);
    background: transparent;
    color: var(--text-muted);
    cursor: pointer;
    transition: var(--transition);
}

.file-actions button:hover {
    background: var(--bg-tertiary);
    color: var(--text-primary);
}

/* 통계 개요 */
.stats-grid {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 16px;
    margin-bottom: 24px;
}

.stat-item {
    text-align: center;
    padding: 16px;
    background: var(--bg-secondary);
    border-radius: var(--border-radius);
    border: 1px solid var(--border-color);
}

.stat-value {
    font-size: var(--font-size-2xl);
    font-weight: 700;
    color: var(--primary-color);
    margin-bottom: 4px;
}

.stat-label {
    font-size: var(--font-size-sm);
    color: var(--text-secondary);
}

/* 빠른 시작 */
.quick-actions {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 12px;
}

.action-btn {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 8px;
    padding: 20px;
    background: var(--bg-secondary);
    border: 1px solid var(--border-color);
    border-radius: var(--border-radius-lg);
    cursor: pointer;
    transition: var(--transition);
    text-decoration: none;
    color: var(--text-primary);
}

.action-btn:hover {
    background: var(--bg-tertiary);
    box-shadow: var(--shadow-sm);
    transform: translateY(-2px);
}

.action-btn i {
    font-size: 32px;
    color: var(--primary-color);
}

.action-btn span {
    font-size: var(--font-size-sm);
    font-weight: 500;
}

/* 퀴즈 화면 */
.quiz-container {
    max-width: 800px;
    margin: 0 auto;
}

.quiz-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 32px;
    padding: 20px;
    background: var(--bg-primary);
    border-radius: var(--border-radius-lg);
    box-shadow: var(--shadow-sm);
}

.quiz-progress {
    display: flex;
    align-items: center;
    gap: 16px;
    flex: 1;
}

.quiz-progress span {
    font-size: var(--font-size-lg);
    font-weight: 600;
}

.progress-bar {
    flex: 1;
    height: 8px;
    background: var(--bg-tertiary);
    border-radius: 4px;
    overflow: hidden;
}

.progress-fill {
    height: 100%;
    background: var(--primary-color);
    transition: width 0.3s ease;
}

.quiz-controls {
    display: flex;
    gap: 12px;
}

.question-area {
    background: var(--bg-primary);
    border-radius: var(--border-radius-lg);
    padding: 32px;
    margin-bottom: 24px;
    box-shadow: var(--shadow-sm);
}

.question-text {
    font-size: var(--font-size-lg);
    line-height: 1.6;
    margin-bottom: 32px;
    color: var(--text-primary);
}

.options-container {
    display: flex;
    flex-direction: column;
    gap: 12px;
}

.option {
    padding: 16px 20px;
    background: var(--bg-secondary);
    border: 2px solid var(--border-color);
    border-radius: var(--border-radius-lg);
    cursor: pointer;
    transition: var(--transition);
    display: flex;
    align-items: center;
    gap: 12px;
}

.option:hover {
    background: var(--bg-tertiary);
    border-color: var(--primary-color);
}

.option.selected {
    background: rgba(79, 70, 229, 0.1);
    border-color: var(--primary-color);
}

.option.correct {
    background: rgba(16, 185, 129, 0.1);
    border-color: var(--success-color);
}

.option.incorrect {
    background: rgba(239, 68, 68, 0.1);
    border-color: var(--danger-color);
}

.option-number {
    width: 24px;
    height: 24px;
    background: var(--text-muted);
    color: var(--text-white);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: var(--font-size-sm);
    font-weight: 600;
}

.option.selected .option-number {
    background: var(--primary-color);
}

.option-text {
    flex: 1;
    font-size: var(--font-size-base);
}

.quiz-footer {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 20px;
    background: var(--bg-primary);
    border-radius: var(--border-radius-lg);
    box-shadow: var(--shadow-sm);
}

.quiz-info {
    display: flex;
    align-items: center;
    gap: 24px;
}

.timer {
    font-size: var(--font-size-lg);
    font-weight: 600;
    color: var(--primary-color);
}

.difficulty {
    padding: 4px 12px;
    background: var(--bg-tertiary);
    border-radius: var(--border-radius);
    font-size: var(--font-size-sm);
    font-weight: 500;
}

.quiz-navigation {
    display: flex;
    gap: 12px;
}

/* 모달 */
.modal {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.5);
    display: none;
    justify-content: center;
    align-items: center;
    z-index: 1000;
}

.modal.active {
    display: flex;
}

.modal-content {
    background: var(--bg-primary);
    border-radius: var(--border-radius-lg);
    box-shadow: var(--shadow-lg);
    max-width: 500px;
    width: 90%;
    max-height: 90vh;
    overflow-y: auto;
}

.modal-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 24px 24px 0;
    margin-bottom: 24px;
}

.modal-header h2 {
    font-size: var(--font-size-xl);
    font-weight: 600;
}

.modal-close {
    background: none;
    border: none;
    font-size: 24px;
    cursor: pointer;
    color: var(--text-muted);
    padding: 0;
    width: 32px;
    height: 32px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.modal-body {
    padding: 0 24px;
}

.modal-footer {
    padding: 24px;
    display: flex;
    justify-content: flex-end;
    gap: 12px;
    border-top: 1px solid var(--border-color);
    margin-top: 24px;
}

/* 폼 스타일 */
.form-group {
    margin-bottom: 20px;
}

.form-group label {
    display: block;
    margin-bottom: 8px;
    font-weight: 500;
    color: var(--text-primary);
}

.form-group input,
.form-group select,
.form-group textarea {
    width: 100%;
    padding: 12px;
    border: 1px solid var(--border-color);
    border-radius: var(--border-radius);
    background: var(--bg-primary);
    color: var(--text-primary);
    font-size: var(--font-size-base);
    transition: var(--transition);
}

.form-group input:focus,
.form-group select:focus,
.form-group textarea:focus {
    outline: none;
    border-color: var(--primary-color);
    box-shadow: 0 0 0 3px rgba(79, 70, 229, 0.1);
}

.checkbox-group {
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.checkbox-group label {
    display: flex;
    align-items: center;
    gap: 8px;
    font-weight: normal;
    margin-bottom: 0;
}

.checkbox-group input[type="checkbox"] {
    width: auto;
    margin: 0;
}

/* 반응형 디자인 */
@media (max-width: 768px) {
    .main-nav {
        padding: 0 16px;
        flex-wrap: wrap;
        height: auto;
        min-height: 64px;
    }

    .nav-controls {
        flex-wrap: wrap;
        gap: 12px;
    }

    .main-content {
        padding: 16px;
    }

    .dashboard-grid {
        grid-template-columns: 1fr;
        gap: 16px;
    }

    .stats-grid {
        grid-template-columns: repeat(2, 1fr);
        gap: 12px;
    }

    .quick-actions {
        grid-template-columns: 1fr;
    }

    .quiz-header {
        flex-direction: column;
        gap: 16px;
        align-items: stretch;
    }

    .quiz-footer {
        flex-direction: column;
        gap: 16px;
        align-items: stretch;
    }

    .modal-content {
        width: 95%;
        margin: 16px;
    }
}

@media (max-width: 480px) {
    .card {
        padding: 16px;
    }

    .question-area {
        padding: 20px;
    }

    .option {
        padding: 12px 16px;
    }

    .stats-grid {
        grid-template-columns: 1fr;
    }
}

/* 접근성 */
@media (prefers-reduced-motion: reduce) {
    * {
        animation-duration: 0.01ms !important;
        animation-iteration-count: 1 !important;
        transition-duration: 0.01ms !important;
    }
}

/* 포커스 스타일 */
button:focus,
input:focus,
select:focus,
textarea:focus,
.option:focus {
    outline: 2px solid var(--primary-color);
    outline-offset: 2px;
}

/* 선택 불가 텍스트 */
.no-select {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}

/* 유틸리티 클래스 */
.hidden {
    display: none !important;
}

.sr-only {
    position: absolute;
    width: 1px;
    height: 1px;
    padding: 0;
    margin: -1px;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border: 0;
}

input.content:
/**
 * 파일 파싱 모듈
 * PDF, TXT 파일을 읽어 텍스트를 추출하고 구조화
 */

class FileParser {
    constructor() {
        this.supportedTypes = ['.pdf', '.txt'];
        this.pdfjsLib = window['pdfjs-dist/build/pdf'];
        if (this.pdfjsLib) {
            this.pdfjsLib.GlobalWorkerOptions.workerSrc = 'https://cdnjs.cloudflare.com/ajax/libs/pdf.js/3.8.162/pdf.worker.min.js';
        }
    }

    /**
     * 파일 유형 검증
     */
    validateFile(file) {
        const fileExtension = '.' + file.name.split('.').pop().toLowerCase();
        return this.supportedTypes.includes(fileExtension);
    }

    /**
     * 파일 크기 검증 (최대 10MB)
     */
    validateFileSize(file) {
        const maxSize = 10 * 1024 * 1024; // 10MB
        return file.size <= maxSize;
    }

    /**
     * 메인 파싱 함수
     */
    async parseFile(file) {
        try {
            if (!this.validateFile(file)) {
                throw new Error('지원하지 않는 파일 형식입니다.');
            }

            if (!this.validateFileSize(file)) {
                throw new Error('파일 크기가 너무 큽니다. (최대 10MB)');
            }

            const fileExtension = '.' + file.name.split('.').pop().toLowerCase();

            let content = '';
            switch (fileExtension) {
                case '.pdf':
                    content = await this.parsePDF(file);
                    break;
                case '.txt':
                    content = await this.parseTXT(file);
                    break;
                default:
                    throw new Error('지원하지 않는 파일 형식입니다.');
            }

            // 기본 메타데이터 생성
            const metadata = {
                fileName: file.name,
                fileSize: file.size,
                fileType: fileExtension,
                parsedAt: new Date().toISOString(),
                wordCount: this.countWords(content),
                characterCount: content.length
            };

            return {
                content: content.trim(),
                metadata,
                success: true
            };

        } catch (error) {
            console.error('파일 파싱 오류:', error);
            return {
                content: '',
                metadata: null,
                success: false,
                error: error.message
            };
        }
    }

    /**
     * PDF 파일 파싱
     */
    async parsePDF(file) {
        return new Promise((resolve, reject) => {
            const fileReader = new FileReader();

            fileReader.onload = async function() {
                try {
                    const typedArray = new Uint8Array(this.result);
                    const pdf = await window['pdfjs-dist/build/pdf'].getDocument(typedArray).promise;

                    let fullText = '';

                    // 모든 페이지 순회
                    for (let pageNum = 1; pageNum <= pdf.numPages; pageNum++) {
                        const page = await pdf.getPage(pageNum);
                        const textContent = await page.getTextContent();

                        let pageText = '';
                        textContent.items.forEach(item => {
                            pageText += item.str + ' ';
                        });

                        fullText += pageText + '\\n\\n';
                    }

                    resolve(fullText);
                } catch (error) {
                    reject(new Error('PDF 파싱 중 오류가 발생했습니다: ' + error.message));
                }
            };

            fileReader.onerror = () => {
                reject(new Error('파일 읽기 오류가 발생했습니다.'));
            };

            fileReader.readAsArrayBuffer(file);
        });
    }

    /**
     * TXT 파일 파싱
     */
    async parseTXT(file) {
        return new Promise((resolve, reject) => {
            const fileReader = new FileReader();

            fileReader.onload = function() {
                try {
                    // UTF-8 인코딩으로 읽기 시도
                    let content = this.result;

                    // 인코딩 감지 및 변환
                    if (content.includes('�')) {
                        // EUC-KR로 재시도
                        const fileReaderEUCKR = new FileReader();
                        fileReaderEUCKR.onload = function() {
                            resolve(this.result);
                        };
                        fileReaderEUCKR.onerror = () => {
                            reject(new Error('텍스트 파일 인코딩 오류'));
                        };
                        fileReaderEUCKR.readAsText(file, 'euc-kr');
                    } else {
                        resolve(content);
                    }
                } catch (error) {
                    reject(new Error('텍스트 파일 파싱 오류: ' + error.message));
                }
            };

            fileReader.onerror = () => {
                reject(new Error('파일 읽기 오류가 발생했습니다.'));
            };

            fileReader.readAsText(file, 'utf-8');
        });
    }

    /**
     * 단어 수 계산
     */
    countWords(text) {
        return text.split(/\\s+/).filter(word => word.length > 0).length;
    }

    /**
     * 텍스트 전처리
     */
    preprocessText(text) {
        return text
            // 불필요한 공백 제거
            .replace(/\\s+/g, ' ')
            // 연속된 줄바꿈 정리
            .replace(/\\n{3,}/g, '\\n\\n')
            // 특수문자 정리
            .replace(/[\\u200B-\\u200D\\uFEFF]/g, '')
            // 앞뒤 공백 제거
            .trim();
    }

    /**
     * 문장 단위로 분리
     */
    splitIntoSentences(text) {
        // 한국어 문장 구분자
        const sentenceEnders = /[.!?\\n]\\s*/g;
        const sentences = text.split(sentenceEnders)
            .map(sentence => sentence.trim())
            .filter(sentence => sentence.length > 10); // 너무 짧은 문장 제외

        return sentences;
    }

    /**
     * 단락 단위로 분리
     */
    splitIntoParagraphs(text) {
        const paragraphs = text.split(/\\n\\s*\\n/)
            .map(paragraph => paragraph.trim())
            .filter(paragraph => paragraph.length > 20); // 너무 짧은 단락 제외

        return paragraphs;
    }

    /**
     * 키워드 추출 (간단한 빈도 기반)
     */
    extractKeywords(text, minLength = 2, maxKeywords = 50) {
        // 한글, 영문, 숫자만 추출
        const words = text.match(/[가-힣a-zA-Z0-9]+/g) || [];

        // 단어 빈도 계산
        const wordFreq = {};
        words.forEach(word => {
            if (word.length >= minLength) {
                const normalizedWord = word.toLowerCase();
                wordFreq[normalizedWord] = (wordFreq[normalizedWord] || 0) + 1;
            }
        });

        // 빈도순 정렬
        const sortedKeywords = Object.entries(wordFreq)
            .sort(([,a], [,b]) => b - a)
            .slice(0, maxKeywords)
            .map(([word, freq]) => ({ word, frequency: freq }));

        return sortedKeywords;
    }

    /**
     * 텍스트 난이도 추정 (간단한 지표 기반)
     */
    estimateTextDifficulty(text) {
        const sentences = this.splitIntoSentences(text);
        const words = text.match(/[가-힣a-zA-Z0-9]+/g) || [];

        // 평균 문장 길이
        const avgSentenceLength = words.length / sentences.length;

        // 복잡한 단어 비율 (4글자 이상)
        const complexWords = words.filter(word => word.length >= 4).length;
        const complexWordRatio = complexWords / words.length;

        // 난이도 점수 계산 (1-10)
        let difficulty = 1;

        if (avgSentenceLength > 20) difficulty += 2;
        if (avgSentenceLength > 30) difficulty += 2;
        if (complexWordRatio > 0.3) difficulty += 2;
        if (complexWordRatio > 0.5) difficulty += 2;

        // 전문 용어 패턴 감지
        const technicalPatterns = [
            /[가-힣]+기사/g,
            /[가-힣]+사/g,
            /제\\d+조/g,
            /[가-힣]+법/g,
            /규정|조항|항목/g
        ];

        technicalPatterns.forEach(pattern => {
            if (pattern.test(text)) {
                difficulty += 1;
            }
        });

        return Math.min(10, Math.max(1, Math.round(difficulty)));
    }

    /**
     * 주제 분류 (기본적인 키워드 기반)
     */
    classifySubject(text, filename = '') {
        const subjects = {
            '전기기능사': ['전기', '회로', '저항', '전압', '전류', '변압기', '모터', '배선', '접지'],
            '소방관리사': ['소방', '화재', '스프링클러', '소화기', '방재', '안전', '대피', '경보'],
            '정보처리기사': ['프로그래밍', '데이터베이스', '네트워크', '소프트웨어', '알고리즘', 'DB', 'SQL'],
            '건축기사': ['건축', '구조', '시공', '설계', '콘크리트', '철근', '건물', '도면'],
            '기계기사': ['기계', '엔진', '펌프', '터빈', '기어', '베어링', '가공', '용접'],
            '일반': ['일반', '기본', '상식', '교양']
        };

        const textLower = text.toLowerCase();
        const filenameLower = filename.toLowerCase();
        const combinedText = textLower + ' ' + filenameLower;

        let maxScore = 0;
        let predictedSubject = '일반';

        Object.entries(subjects).forEach(([subject, keywords]) => {
            let score = 0;
            keywords.forEach(keyword => {
                const regex = new RegExp(keyword, 'gi');
                const matches = combinedText.match(regex);
                if (matches) {
                    score += matches.length;
                }
            });

            if (score > maxScore) {
                maxScore = score;
                predictedSubject = subject;
            }
        });

        return {
            subject: predictedSubject,
            confidence: Math.min(1, maxScore / 10), // 0-1 사이의 신뢰도
            scores: Object.keys(subjects).reduce((acc, subject) => {
                acc[subject] = 0;
                subjects[subject].forEach(keyword => {
                    const regex = new RegExp(keyword, 'gi');
                    const matches = combinedText.match(regex);
                    if (matches) {
                        acc[subject] += matches.length;
                    }
                });
                return acc;
            }, {})
        };
    }
}

