import { useState, useEffect, useRef } from 'react'

interface IdeaSketchProps {
  projectId: string
  stepId: number
  onSave: (data: any) => void
}

interface SketchData {
  ideas: { id: string; title: string; description: string; score: number }[]
  sketches: { id: string; ideaId: string; dataUrl: string; notes: string }[]
  selectedIdea: string | null
}

export function StepIdeaSketch({ projectId, stepId, onSave }: IdeaSketchProps) {
  const [sketchData, setSketchData] = useState<SketchData>({
    ideas: [],
    sketches: [],
    selectedIdea: null
  })

  const [newIdea, setNewIdea] = useState({ title: '', description: '' })
  const [isDrawing, setIsDrawing] = useState(false)
  const [activeIdeaId, setActiveIdeaId] = useState<string | null>(null)
  const [sketchNotes, setSketchNotes] = useState('')

  const canvasRef = useRef<HTMLCanvasElement>(null)
  const [isCanvasActive, setIsCanvasActive] = useState(false)
  const [brushSize, setBrushSize] = useState(3)
  const [brushColor, setBrushColor] = useState('#ec4899')

  useEffect(() => {
    const saved = localStorage.getItem(`sketch-${projectId}-${stepId}`)
    if (saved) {
      setSketchData(JSON.parse(saved))
    }
  }, [projectId, stepId])

  const saveData = (data: SketchData) => {
    setSketchData(data)
    localStorage.setItem(`sketch-${projectId}-${stepId}`, JSON.stringify(data))
    onSave(data)
  }

  const addIdea = () => {
    if (!newIdea.title.trim()) return
    const idea = {
      id: Date.now().toString(),
      title: newIdea.title,
      description: newIdea.description,
      score: 0
    }
    const updated = { ...sketchData, ideas: [...sketchData.ideas, idea] }
    saveData(updated)
    setNewIdea({ title: '', description: '' })
  }

  const updateIdeaScore = (ideaId: string, score: number) => {
    const updated = {
      ...sketchData,
      ideas: sketchData.ideas.map(idea =>
        idea.id === ideaId ? { ...idea, score } : idea
      )
    }
    saveData(updated)
  }

  const selectBestIdea = (ideaId: string) => {
    const updated = { ...sketchData, selectedIdea: ideaId }
    saveData(updated)
  }

  const deleteIdea = (ideaId: string) => {
    const updated = {
      ...sketchData,
      ideas: sketchData.ideas.filter(i => i.id !== ideaId),
      sketches: sketchData.sketches.filter(s => s.ideaId !== ideaId),
      selectedIdea: sketchData.selectedIdea === ideaId ? null : sketchData.selectedIdea
    }
    saveData(updated)
  }

  // Canvas ê·¸ë¦¬ê¸° í•¨ìˆ˜ë“¤
  const startDrawing = (e: React.MouseEvent<HTMLCanvasElement>) => {
    if (!canvasRef.current) return
    setIsDrawing(true)
    const canvas = canvasRef.current
    const ctx = canvas.getContext('2d')
    if (!ctx) return

    const rect = canvas.getBoundingClientRect()
    const x = e.clientX - rect.left
    const y = e.clientY - rect.top

    ctx.beginPath()
    ctx.moveTo(x, y)
    ctx.strokeStyle = brushColor
    ctx.lineWidth = brushSize
    ctx.lineCap = 'round'
  }

  const draw = (e: React.MouseEvent<HTMLCanvasElement>) => {
    if (!isDrawing || !canvasRef.current) return
    const canvas = canvasRef.current
    const ctx = canvas.getContext('2d')
    if (!ctx) return

    const rect = canvas.getBoundingClientRect()
    const x = e.clientX - rect.left
    const y = e.clientY - rect.top

    ctx.lineTo(x, y)
    ctx.stroke()
  }

  const stopDrawing = () => {
    setIsDrawing(false)
  }

  const clearCanvas = () => {
    if (!canvasRef.current) return
    const canvas = canvasRef.current
    const ctx = canvas.getContext('2d')
    if (!ctx) return
    ctx.clearRect(0, 0, canvas.width, canvas.height)
  }

  const saveSketch = () => {
    if (!canvasRef.current || !activeIdeaId) return
    const dataUrl = canvasRef.current.toDataURL()

    const sketch = {
      id: Date.now().toString(),
      ideaId: activeIdeaId,
      dataUrl,
      notes: sketchNotes
    }

    const updated = { ...sketchData, sketches: [...sketchData.sketches, sketch] }
    saveData(updated)

    clearCanvas()
    setSketchNotes('')
    setIsCanvasActive(false)
    setActiveIdeaId(null)
  }

  const deleteSketch = (sketchId: string) => {
    const updated = {
      ...sketchData,
      sketches: sketchData.sketches.filter(s => s.id !== sketchId)
    }
    saveData(updated)
  }

  return (
