  const [newIdea, setNewIdea] = useState({ title: '', description: '' })
  const [isDrawing, setIsDrawing] = useState(false)
  const [activeIdeaId, setActiveIdeaId] = useState<string | null>(null)
  const [sketchNotes, setSketchNotes] = useState('')

  const canvasRef = useRef<HTMLCanvasElement>(null)
  const [isCanvasActive, setIsCanvasActive] = useState(false)
  const [brushSize, setBrushSize] = useState(3)
  const [brushColor, setBrushColor] = useState('#ec4899')

  useEffect(() => {
    const saved = localStorage.getItem(`sketch-${projectId}-${stepId}`)
    if (saved) {
      setSketchData(JSON.parse(saved))
    }
  }, [projectId, stepId])

  const saveData = (data: SketchData) => {
    setSketchData(data)
    localStorage.setItem(`sketch-${projectId}-${stepId}`, JSON.stringify(data))
    onSave(data)
  }

  const addIdea = () => {
    if (!newIdea.title.trim()) return
    const idea = {
      id: Date.now().toString(),
      title: newIdea.title,
      description: newIdea.description,
      score: 0
    }
    const updated = { ...sketchData, ideas: [...sketchData.ideas, idea] }
    saveData(updated)
    setNewIdea({ title: '', description: '' })
  }

  const updateIdeaScore = (ideaId: string, score: number) => {
    const updated = {
